css:
@-moz-document domain(ponyvillefm.com) {

html, body {
    background: #20232d;
}

.navbar-inverse.translucent {
	background: none !important;
}
.navbar-inverse {
    background-color: #20232d !important;
}
.breadcrumb {
    background-color: #20232d;
}
.body, .content {
    background-color: #2d3242;
    color: lightgrey;
}
.content-block .media {
 box-shadow: none;
}
.content-block .media#pvfm1 {
	background: linear-gradient(to bottom, #2d3242 15%,#54187B 100%);
}
.content-block .media#pvfm2 {
	background: linear-gradient(to bottom,#182F7B 15%, #2d3242 100%);
}
.content-block .media#pvfm3 {
	background: linear-gradient(to bottom, #2d3242 15%,#6A1286 100%);
}

/* FM3 BG #6A1286 */

footer {
    background-color: #20232d;
    color: lightgrey;
    border-top: none;
}

/* team page */

.dj-card-head {
	background: linear-gradient(to right, #2d3242 15%,#300A5D 100%);
}

}
