css:
@-moz-document domain("steemit.com") {
body {
    background: #dedede !important;
}
/*disable translate to allow fixed pos */

.PostsList__post_container {
    -webkit-transform: none !important;
    transform: none !important;
}
/* apply overflow to fix 100% view height */

html {
    overflow: visible !important;
}
/* content div */
.PostsList__post_container, .PostsList__post_top_bar {
width: 54.78% !important;
}
.Post {
margin-left:50px;
}
/* comment nesting */
.Comment__replies {
margin-left: 2% !important;
}

/* menu back button */
.back-button-menu li {
text-align:right !important;
position:fixed;
top:10px;
left:10px;   
}
/* close button */
.PostsList__post_top_bar .close-button {
visibility:visible !important;
position:fixed;
top:20px;
right:23% !important;  
}

/* pad header */

.Header__top,
ul.HorizontalMenu {
    padding-left: 50px !important;
}
/* Navigation */

.Icon.dropdown-arrow polygon {
    fill: #fff !important;
}
.UserProfile__top-nav .menu>li>a:hover {
    background-color: #1A5099 !important;
}
/* Pace One - Page Loading Indicator (Google Chrome Addon) */

.pace-active .pace-activity {
    display: block;
    position: fixed;
    z-index: 2000;
    top: 0px;
    left: 0px !important;
    width: 300px;
    height: 300px;
    transform: translateX(-50%) translateY(-50%) rotate(315deg) !important;
    pointer-events: none;
    background: rgb(102, 221, 22);
    transition: transform 0.3s;
}
/* Comment Box Trigger */

.Post .PostFull__reply a {
    display: block;
    background-color: #4BA2F2;
    color: #4BA2F2;
    position: absolute;
    top: 0px !important;
    bottom: 0px;
    left: 0px !important;
    height: inherit;
    z-index: 1998;
}
/* comment box */

.Post .ReplyEditor {
    position: fixed !important;
    top: 0px !important;
    left: 2% !important;
    width: 20%;
    height: 400px;
    background-color: rgba(255, 255, 255, 1.0);
    border-radius: 7px;
    border: solid 1px #cacaca;
    padding: 0px 10px;
    z-index: 1998;
}
/* comment box form */

.Post .ReplyEditor textarea {
    position: relative !important;
    background-color: rgba(255, 255, 255, 0.0);
    min-height: 150px;
}
/* comment box button */

.button {
    opacity: 1.0 !important;
    background-color: #4BA2F2 !important;
}
.button.hollow.secondary {
    color: #fff !important;
    border: 1px solid #fff !important;
}
.Promote__button {
    border: none !important;
    color: #fff !important;
}
/* post link text deco */

.PostFull__body a {
    text-decoration: underline !important;
}
.PostFull__body a:hover {
    color: #4078c0 !important;
    text-decoration: none !important;
}
/* resteems */

#postslist .callout {
    background: repeating-linear-gradient( 45deg, #1a5099, #1a5099 10px, #4BA2F2 10px, #4BA2F2 20px) !important;
    color: #fff !important;
    font-size: 10px !important;
}
.PostSummary__reblogged_by {
    background: repeating-linear-gradient( 45deg, #1a5099, #1a5099 10px, #4BA2F2 10px, #4BA2F2 20px) !important;
    color: rgba(255, 255, 255, 0.0) !important;
    font-size: 10px !important;
    position: relative;
    top: 3px;
    margin-bottom: 5px;
}
/* resteem icon */

.PostSummary__reblogged_by .Icon path {
    fill: rgba(75, 162, 242, 1.0) !important;
}
/* Flag Icon */

.flag1 path {
    fill: #4BA2F2 !important;
}
span.Voting__button-down a {
    display: block;
    color: #4BA2F2;
    z-index: 2000;
}
/* border posts in list view */

#posts_list .PostSummary {
    border: solid 1px #cecece;
    border-radius: 7px;
    padding: 5px 5px;
}
/* post view background overlay */

.PostsList__post_overlay {
    background-color: rgba(0, 0, 0, 0.3) !important;
}
/* Profile header background */

.UserProfile__banner>div.column,
.UserProfile__banner>div.columns {
    background: linear-gradient(45deg, #4BA2F2 45px, transparent 45px)64px 64px, linear-gradient(45deg, #4BA2F2 45px, transparent 45px, transparent 91px, #1A5099 91px, #1A5099 135px, transparent 135px), linear-gradient(-45deg, #4BA2F2 23px, transparent 23px, transparent 68px, #4BA2F2 68px, #4BA2F2 113px, transparent 113px, transparent 158px, #4BA2F2 158px);
    background-color: #1A5099;
    background-size: 128px 128px;
    min-height: 155px;
}
/* Header Font */

span.UserProfile__stats {
    color: #fff !important;
    font-family: 'verdana', sans-serif;
    font-weight: 700 !important;
}
/* Body Font */

body {
    color: #000 !important;
    font-family: 'verdana', sans-serif;
    font-weight: 300 !important;
}
.Markdown {
    font-family: 'verdana', sans-serif;
}
/* post submit preview */
.ReplyEditor .Preview .Markdown {
background-color:#fff !important;
}
/* H Tags */

h3.entry-title,
h3.PostSummary__header {
    color: #000 !important;
    font-family: 'Verdana', sans-serif !important;
    font-weight: 200 !important;
    font: 200 100% Verdana, sans-serif;
}
.UserProfile__banner .UserProfile__stats>span {
    border-left: 0px solid #000 !important;
    margin-left: 20px;
    padding: 0px 0px !important;
}
.UserProfile__banner .UserProfile__stats>span::before {
    position: relative;
    left: -10px;
    content: "?";
}
.UserProfile__top-nav {
    background: rgba(75, 161, 242, 1);
    background: -moz-linear-gradient(top, rgba(75, 161, 242, 1) 0%, rgba(26, 81, 153, 1) 100%);
    background: -webkit-gradient(left top, left bottom, color-stop(0%, rgba(75, 161, 242, 1)), color-stop(100%, rgba(26, 81, 153, 1)));
    background: -webkit-linear-gradient(top, rgba(75, 161, 242, 1) 0%, rgba(26, 81, 153, 1) 100%);
    background: -o-linear-gradient(top, rgba(75, 161, 242, 1) 0%, rgba(26, 81, 153, 1) 100%);
    background: -ms-linear-gradient(top, rgba(75, 161, 242, 1) 0%, rgba(26, 81, 153, 1) 100%);
    background: linear-gradient(to bottom, rgba(75, 161, 242, 1) 0%, rgba(26, 81, 153, 1) 100%);
}
/* wallet */
.UserWallet__claimbox {
    border: 1px solid #bad132;
    background: #bad132;
}
.button.hollow, .button.hollow:focus, .button.hollow:hover {
    border-color: #4BA2F2 !important;
    color: #fff !important;
}
input.buy-color, input.buy-color:focus , input.buy-color:hover, input.sell-color, input.sell-color:focus , input.sell-color:hover {
    background-color: #4BA2F2 !important;
    border: solid 1px #4BA2F2 !important;
}
.UserWallet .Icon.dropdown-arrow polygon {
fill:#4BA2F2 !important;
}
}
