css:
@-moz-document domain("transport-games.ru") {
#ipsLayout_header .ipsm-nav.is-stuck,
.ipsm-nav {
    background-color: rgba(26, 32, 39, 0.9);
    box-shadow: 0 0 5px rgba(0, 0, 0, 0.8);
}

.ipsNavBar_primary > ul > li > a,
#ipsLayout_header .ipsm-nav.is-stuck #elUserNav > li > a,
.ipsType_richText,
.ipsNavBar_primary > ul > li.ipsNavBar_active > a,
#elUserNav > li > a,
.ipsQuote_citation,
body, a, .ipsType_pageTitle, .ipsType_sectionHead,
.ipsWidget.ipsWidget_vertical .ipsWidget_title,
.ipsBreadcrumb > ul > li,
.ipsBreadcrumb [data-action="defaultStream"], .ipsBreadcrumb [data-action="markSiteRead"],
.ipsNavBar_secondary > li > a,
.ipsTabs_activeItem,
.ipsPager_type,
.ipsPager .ipsPager_next a:after, .ipsPager .ipsPager_prev a:after,
.ipsApp .ipsButton_light,
.ipsComposeArea_dummy
{
    color: #ddd;	/* Серый */
}

.ipsAreaBackground_light, .ipsQuote_citation, .inline100:last-child
{
    border-radius: 0px 0px 5px 5px;
}

.ipsApp .ipsButton_light,
.ipsComposeArea_dummy,
.cke_top,
.ipsComposeArea_editor,
.ipsNavBar_primary > ul > li #ipsmakeHomeIcon,
.ipsApp .ipsField_autocomplete, .ipsApp textarea, .ipsApp input[type="text"], .ipsApp input[type="password"], .ipsApp input[type="datetime"], .ipsApp input[type="datetime-local"], .ipsApp input[type="date"], .ipsApp input[type="month"], .ipsApp input[type="time"], .ipsApp input[type="week"], .ipsApp input[type="number"], .ipsApp input[type="email"], .ipsApp input[type="url"], .ipsApp input[type="search"], .ipsApp input[type="tel"],
.ipsQuote_citation, .inline100:last-child
{
    background-color: #55626d;	/* Светлый */
}

.ipsNavBar_primary > ul > li > .ipsNavBar_secondary,
.ipsTabs_panel,
.ipsTabs_activeItem,
.ipsHovercard,
.ipsMenu
{
    background-color: #4e5f74 !important;	/* Средний */
}


.ipsBox:not( .ipsBox_transparent ):not( .ipsModerated ),
.cAuthorPane,
#ipsLayout_contentWrapper,
.ipsQuote,
.ipsDataList.ipsDataList_zebra .ipsDataItem:not( .ipsDataItem_selected ):not( .ipsModerated ):not( .ipsDataItem_new ):not( .ipsDataItem_success ):not( .ipsDataItem_warning ):not( .ipsDataItem_error ):not( .ipsDataItem_info ):not( .ipsDataItem_status ):nth-child(even),
.ipsAreaBackground_reset,
.ipsLikeRep,
.ipsReputation_count,
.ipsReputation_count.ipsType_positive,
.material_card,
.ipsWidget.ipsWidget_horizontal .ipsWidget_title,
.ipsTabs,
.ipsComment:not( .ipsModerated ) .ipsComment_header,
.ipsAreaBackground,
.cke_wysiwyg_frame, .cke_wysiwyg_div,
.ipsDataList_readStatus .ipsDataItem:not( .ipsDataItem_unread ):not( .ipsDataItem_selected ):not( .ipsModerated )
{
    background-color: #363e45;		/* Темный */
}

.ipsPageHeader,
.ipsAreaBackground_light
{
    background-color: #1c222a !important;		/* Очень темный */
}



.ipsHovercard,
.ipsApp .ipsField_autocomplete, .ipsApp textarea, .ipsApp input[type="text"], .ipsApp input[type="password"], .ipsApp input[type="datetime"], .ipsApp input[type="datetime-local"], .ipsApp input[type="date"], .ipsApp input[type="month"], .ipsApp input[type="time"], .ipsApp input[type="week"], .ipsApp input[type="number"], .ipsApp input[type="email"], .ipsApp input[type="url"], .ipsApp input[type="search"], .ipsApp input[type="tel"]
{
	border-color: #363e45 !important;		/* Темный */
}



.ipsMenu_headerBar,
.ipsMenu
{
    border-color: #55626d !important;	/* Светлый */
}

.ipsMenu {
    border-radius: 5px;
}


.ipsMenu_footerBar
{
    border-top-color: #55626d !important;	/* Светлый */
}

body
{
    background: none !important;
    background-color: #363e45 !important;
}

.ipsMenu_headerBar, .ipsMenu_footerBar
{
    background: none !important;
    background-color: #4e5f74 !important;	/* Средний */
}

.ipsMenu.ipsMenu_bottomLeft:after,
.ipsMenu.ipsMenu_bottomCenter:after,
.ipsMenu.ipsMenu_bottomRight:after,
.ipsMenu.ipsMenu_bottomLeft:before,
.ipsMenu.ipsMenu_bottomCenter:before,
.ipsMenu.ipsMenu_bottomRight:before
{
    border-color: transparent transparent #4e5f74 transparent;
}

.ipsDataItem,
.ipsDataList.ipsDataList_zebra > .ipsDataItem
{
    border-color: #4e5f74;
    border-top: 0px solid;
    border-bottom: 1px solid #4e5f74;
}

.ipsNavBar_secondary > li > a:hover {
    background-color: #31506b !important;
    color: #ddd
}

a:hover
{
    color: #7f9dc1;
}

.ipsPageHeader,
.ipsWidget.ipsWidget_horizontal .ipsWidget_title{
    background-color: #4e5f74;
    border: 1px solid #363e45;
    border-radius: 5px;
}

.cCmsRecord_row.ipsDataItem.ipsDataItem_responsivePhoto
{
    border: 1px solid #4e5f74;
    border-radius: 5px;
}

.ipsmakeBox,
.ipsWidget.ipsWidget_vertical .ipsWidget_title
{
    background-color: #4e5f74;
    border: 1px solid #4e5f74;
}

.ipsWidget.ipsWidget_vertical .ipsWidget_inner,
.ipsPad
{
    border-radius: 0px 0px 5px 5px;
}


.ipsWidget.ipsWidget_vertical .ipsWidget_title,
.ipsComment_header
{
    border-radius: 5px 5px 0px 0px;
}

.ipsImage_thumbnailed,
.ipsThumb
{
    border: 1px solid #417ba3;
}

.ipsComment, .ipsComment.ipsBox, .cAuthorPane
{
    border-radius: 5px;
}

.ipsAreaBackground
{
    border-radius: 5px 0px 0px 5px;
}

.ipsQuote,
.ipsWidget.ipsWidget_vertical .ipsWidget_inner
{
    border-color: #55626d;
}

.inline100 h4.ipsDataItem_title.ipsContained_container a,
a.ipsTruncate.ipsTruncate_line
{
    font-weight: bold;
}


.ipsReputation_count.ipsType_positive {
    color:#779a58;
}

.ipsPagination.ipsPagination_mini a {
    background-color: rgba(255,255,255,0.1);
    text-shadow: none;
    color: #fff;
}

.ipsTabs_panels.ipsTabs_contained,
.ipsComment
{
    border: none;
}

.ipsNavBar_primary > ul > li.ipsNavBar_active > a
{
    background: rgb(78, 95, 116,0.4);
}


.ipsDataItem:hover,
.cForumList ol.ipsDataList li.ipsDataItem:hover,
.ipsDataList.ipsDataList_zebra > .ipsDataItem:hover
{
    background-color: #31506b !important;
    transition: all 0.3s ease-out;
}

.ipsWidget.ipsWidget_vertical .ipsWidget_title:after,
.cPost .ipsComment_content::before,
html[dir="ltr"] .cPost .ipsComment_content::before,
html[dir="ltr"] .ipsComposeArea_withPhoto .ipsComposeArea_editor:before
{
    display: none;
}

.ipsReputation_count.ipsType_negative
{
    background: #a72f35;
}
}
