css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("videos.assemblee-nationale.fr")  {

@media (min-width: 978px){
    .fullscreen .tab{
      width: 20% !important;
      min-width: 260px !important;
    }

    .fullscreen .video{
      width: 80% !important;
      height: 100% !important;
      top: auto !important;
    }
  
  }
  
  @media (max-width: 978px){
    .fullscreen .video{
      top: 0 !important;
      margin-top: -220px !important;
      height: 100% !important;
    }
    
    .fullscreen .slider {
      top: 520px !important;
    }
    
    .fullscreen .tab{
      top: 570px !important;
    }
  }
}
