css:
@-moz-document url-prefix("http://docs.sencha.com"), url-prefix("https://docs.sencha.com") {
.has-guides .toolkit-badge-ct,
.has-guides .guide-body-wrap,
.has-guides #product-tree-ct,
.has-guides #product-version-tree-ct,
.has-guides #search-results-ct > div,
.has-guides .active-tab,
.has-guides #tree-sub-header .active-tab,
.has-guides .selected-tree-node,
.has-guides #member-filter-field,
.has-guides .da-inline-fiddle-nav-active,
.has-guides #class-body-wrap {
    background-color: #000 !important;
}

.has-guides .toolbarButton,
.has-guides .private-class-toggle-ct,
.has-guides .active-tab,
.has-guides #tree-sub-header,
.has-guides.tree-hidden #class-tree-ct,
.has-guides.tree-hidden #tree-header,
.has-guides #memberTypeMenu,
.has-guides #tree {
    background-color: #181818;
}

.has-guides .guide-search-item {
    background-color: #222;
}

.has-guides #footer,
.has-guides .sub-tab:hover,
.has-guides .tree-node:hover,
.has-guides #toolbar {
    background-color: #333 !important;
}

.has-guides #product-tree-ct .selected-product,
.has-guides .search-results-nav,
.has-guides .search-results-header,
.has-guides #tree-header,
.has-guides #member-display,
.has-guides .da-inline-fiddle-nav,
.has-guides #member-toolbars {
    background-color: #444;
}

.has-guides .da-inline-fiddle-nav-code,
.has-guides .da-inline-fiddle-nav-fiddle {
    background-color: #282828;
    border: none;
}

.has-guides .readonly,
.has-guides .bindable,
.has-guides .private {
    color: #000;
}

.has-guides .tree-node-indexed .tree-node-icon,
.has-guides .toc a,
.has-guides .guide-body-wrap,
.has-guides #product-version-tree-ct,
.has-guides #product-tree-ct .product-name-item,
.has-guides #search-results-ct .search-source,
.has-guides #search-results-ct .search-match,
.has-guides .search-results-header,
.has-guides #tree-header,
.has-guides #member-display,
.has-guides h3.detail-header,
.has-guides code,
.has-guides #member-toolbars {
    color: #bbb !important;
}

.has-guides pre.language-javascript,
.has-guides code {
    text-shadow: none !important;
}

.has-guides #toolbar {
    padding-top: 4px;
}

.has-guides #member-display {
    margin-top: 22px;
}

.has-guides #search-results-ct .search-item.classic,
.has-guides #search-results-ct .search-item.modern {
    background-image: none;
    position: relative;
}

.has-guides #search-results-ct .search-item.classic:after,
.has-guides #search-results-ct .search-item.modern:after {
    content: "C";
    position: absolute;
    right: 4px;
    top: 2px;
    font-size: 80%;
    font-weight: 700;
}

.has-guides #search-results-ct .search-item.modern:after {
    content: "M";
}

.has-guides .isGetter:before,
.has-guides .isSetter:before,
.has-guides #member-toolbars,
.has-guides #history-nav {
    background-color: #444;
}

.has-guides .isGetter:before,
.has-guides .isSetter:before {
    border-width: 1px;
}

.has-guides #product-version-tree-ct,
.has-guides #product-tree-ct {
    border: 1px solid #666;
}

.has-guides #member-toolbars,
.has-guides #history-nav {
    border-bottom: 1px solid #666;
}

.has-guides #footer {
    border-top: 1px solid #666;
}

.has-guides .toc h2,
.has-guides .active-tab,
.has-guides .since-dec,
.has-guides #history-nav .history-title {
    color: #ddd !important;
}

.has-guides .guide-search-item,
.has-guides #tree-sub-header .sub-tab,
.has-guides #history-nav a {
    color: #2ae;
}

.has-guides #member-toolbars {
    padding: 12px 0;
}

.has-guides .classmembers:after,
.has-guides .collapse-toggle,
.has-guides .type-sub-category-title {
    background-color: #333 !important;
    color: #ccc !important;
}

.has-guides .collapse-toggle:hover {
    background-color: #666 !important;
}

.has-guides .collapse-toggle {
    border-right: 1px solid #666;
}

.has-guides .member a.member-name,
.has-guides .member span.member-name,
.has-guides #member-class-name h1 {
    color: #29f !important;
}

.has-guides #member-toolbars h1 {
    margin: 0;
}

.has-guides .since-dec {
    background-color: #557;
}

.has-guides .defaults-to-dec {
    color: #eb9;
}

.has-guides .toolbarButton i,
.has-guides #history-config,
.has-guides #toggleAll,
.has-guides .collapse-tool:before,
.has-guides .expand-tool:before,
.has-guides.tree-hidden #tree-header i,
.has-guides #toggleExamples {
    /*color: #55f;*/
    color: #fff;
}

.has-guides .nav-btn-count {
    background-color: #44f;
}

.has-guides .classMeta,
.has-guides .tree-node,
.has-guides .tree-node a,
.has-guides p code,
.has-guides #member-class-name .alias,
.has-guides .private-class-toggle-ct label,
.has-guides .fiddle-code-beautify,
.has-guides #class-body-wrap {
    color: #bbb;
}

.has-guides pre.language-javascript,
.has-guides p code {
    font-family: Consolas, Monospace;
}

.has-guides .guide-body-wrap h1,
.has-guides .toolbarButton,
.has-guides #member-filter-field,
.has-guides .selected-tree-node,
.has-guides #class-body-wrap h2 {
    color: #ddd;
}

.has-guides .selected-tree-node {
    position: relative;
}

.has-guides .selected-tree-node:before {
    position: absolute;
    font-family: FontAwesome;
    content: "\f060"; /* fa-arrow-left */
    top: 0;
    right: 0;
    bottom: 0;
}

.has-guides .toc,
.has-guides p code,
.has-guides .classMeta,
.has-guides .accessor-method,
.has-guides pre.language-javascript,
.has-guides #class-body-wrap code {
    background-color: #181818 !important;
}

.has-guides .toc,
.has-guides p code,
.has-guides pre.language-javascript,
.has-guides #member-filter-field,
.has-guides .classMeta,
.has-guides #class-body-wrap pre {
    border: 1px solid #555;
}

.is-guide #toggleExamples,
.has-guides .toggle-all-tree-nodes-btn {
    background-color: orange;
    color: #fff;
}

.is-guide #toggleExamples {
    top: 12px;
}

}
