css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rustbyexample.com") {
  
  /** Left-side Contents **/
  
  .book .book-summary {
    background: #111111;
  }
  
  .book .book-summary ul.summary li a {
    color: #838383;
  }
  
  .book .book-summary ul.summary li.done > a {
    color: #5c5c5c;
  }
  
  /** Right-side Page **/
  
  .book .book-body {
    background: #111111;
  }
  
  .book .book-body .page-wrapper .page-inner section {
    background: #111111;
  }
  
  .book .book-body .page-wrapper .page-inner section.normal {
    color: #aaaaaa;
  }
  
  /** Code Snippet boxes **/
  
  #active-code, .ace-tomorrow {
    background-color: #111111;
  }
  
  .book .book-body .page-wrapper .page-inner section.normal code {
    background-color: #111111;
  }
  
  .book .book-body .page-wrapper .page-inner section.normal pre {
    background-color: #111111;
  }
  
  .ace-tomorrow .ace_marker-layer .ace_active-line {
    background-color: #333333;
  }
  
  .ace-tomorrow .ace_marker-layer .ace_selection {
    background-color: #333333;
  }
  
  /*
  
  /* dynamically generated output */
  #result {
    color: #aaaaaa;
    background-color: #0f0f0f !important;
  }
  
  /** Search field on top-left corner **/
  
  .book .book-summary .book-search input, 
  .book .book-summary .book-search input:focus, 
  .book .book-summary .book-search input:hover {
    color: #aaaaaa;
  }
  
  /** Share button on top-right corner **/
  
  .dropdown-menu {
    background: #3f3f3f;
  }
  
  .dropdown-menu .buttons .button:hover {
    color: #cccccc;
  }
  
  .dropdown-menu .dropdown-caret .caret-inner {
    border-bottom-color: #3f3f3f;
  }

}
