css:
/* i really want this to be global */
@namespace url(http://www.w3.org/1999/xhtml);
.navbar-default {
    background-color: #0092CA;
    border-color: #0092CA;
}
.navbar-default .navbar-nav>li>a, .navbar-default .navbar-text {
    color: #fff;
}
.navbar-default:hover .navbar-nav>li>a:hover, .navbar-default:hover .navbar-text:hover {
    color: #fff;
}
.navbar-default .navbar-brand {
    color: #fff;
}
.navbar-default:hover .navbar-brand:hover {
    color: #fff;
}
.box.box-primary {
    border-top-color: #e8e8e8;
}
.profile-usermenu ul li.active a {
    color: #777;
    background-color: #f5f8fa;
    border-left: 2px solid #e8e8e8;
    margin-left: -2px;
}
.profile-usermenu ul li.active a:hover {
    border-left: 2px solid #0092ca;
    transition: all 0.24s ease-in-out;
}
.btn-primary {
    color: #fff;
    background-color: #0092ca;
    border-color: #0092ca;
}
.box.box-success {
    border-top-color: #0092ca;
}
.cover-container {
    height: 200px;
}
.img-avatar {
    display: block;
    border-radius: 5px;
    border: 0;
    margin: auto;
    margin-top: 10px;
    width: 120px;
    height: 120px;
    position: relative;
    box-shadow: 0px 0px 10px 2px rgba(0,0,0,0.22);
}
.nav-tabs-custom > .nav-tabs > li.active {
    border-top-color: #0092ca;
    border-radius: 3px;
}
