css:
@-moz-document domain("cnbeta.com") {
div[class^='ad-widget-imageplus']{
	display:none;
}

div.main-wrap div.w1200 div[id^='_']{
	display:none;
}

div[id^='BAIDU_SSP']{
	display:none;
}

div[id^='BAIDU_DUP']{
	display:none;
}

ins.adsbygoogle{
	display:none;
   opacity:0;
}

div.cnbeta-home-side div.cbv,div.cbv{
	display:none;
}

/*样式调整*/
html{
	background:#fff;
}

/*header*/
header.pageHeader{
	margin: 20px 0px;
   border-width: 0px;
}

nav.global-navi .nav-item a , .topSearchBox{
	border:none;
}

/*Slider*/
.topContentInner{
	background:#2B2D32;
}

/*body*/
.main-wrap{
	background:#F4F4F5;
}

div.tks,div#cbhahaha,div.cnbeta-article-latest,a.page-last,a.page-next{
	display:none!important;
}

div.article-global{
	display:none;
}

/*footer*/
.pageFooter{
	background:#414347;
}

/*Sidebar*/
.cnbeta-side-blue-title , .cnbeta-side-yellow-title{
	background:#2B2D32;
   color:#fff;
}

.hot-comments-plus{
	background-color:#414347;
    color:#ECEDED;
}

.cnbeta-hot-comments-wrapper li.reply .msg-box .inner{
   border-color:#414347;
}

/*Bottom Bar*/
div[style^='display:block !important;position:fixed;bottom:0;']{
	display:none;
   opacity:0;
   width:0px;
height:0px;
overflow:hidden;
}

/*Hot.cnbeta.com*/
.cnbeta-hot-welcome .cate-help{
	display:none;
}

div.w1200 .tac{
	display:none;
}
}
