@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bugzilla.mozilla.org") {
body, td, th, input, select, option, optgroup, .text_input {
    font-family: "Open Sans", "Helvetica Neue", Arial, Helvetica, sans-serif;
    line-height: 1.5;
  }
  
  body {
    background: white;
    padding: 0;
  }
  
  .wrapper {
    width: auto;
    margin: 0;
  }
  
  #titles {
    width: auto;
  }
  
  #header {
    background: white;
    border: 0;
    box-shadow: none;
    margin: 0;
  }
  
  #bugzilla-body {
    margin: 0 !important;
    width: auto !important;
  }
  
  #footer {
    margin: 0;
  }
  
  #bugzilla-body,
  #footer,
  .module .fields-lhs,
  .module .fields-rhs {
    min-width: 0 !important;
  }
  
  button.minor,
  button.minor:hover,
  button.minor:active,
  button.major,
  button.major:hover,
  button.major:active {
    font-weight: normal;
    color: black;
    text-shadow: none;
    box-shadow: none;
    border-radius: 0;
    margin: 0;
  }
  
  #mode-container {
    margin: 0;
    padding: 0;
  }
  
  .module {
    border-radius: 0;
    margin: 0;
    box-shadow: none;
    font-size: 100%;
  }
  
  .module-content {
    padding: 0;
    box-shadow: none;
  }
  
  .module-header {
    box-shadow: none;
    background: transparent;
    border: 0;
  }
  
  .module-header:hover {
    outline: none;
  }
  
  #attachments .attach-patch .attach-desc-td {
    background: none;
  }
  
  .change-set {
    box-shadow: none;
    border: 0;
    margin: 0;
  }
  
  .change-head {
    background: transparent;
  }
  
  .comment-text {
    border: 0;
    background: transparent;
    margin: 0;
  }
  
  .activity {
    background: transparent;
    border: 0;
    padding: 0;
  }
  
  #mode-container .button-row {
    border: 0;
    margin: 0;
  }
  
  .context-menu-list {
    border: 0;
    border-radius: 4px;
    overflow: hidden;
    box-shadow: 0 0 0 0.5px hsla(0,0%,0%,0.08), 0 4px 8px hsla(0,0%,0%,0.3);
    font-size: 12px;
  }
  
  .context-menu-item {
    padding: 2px 8px;
    line-height: 20px;
  }
  
  .context-menu-item.hover {
    color: white;
  }
  
  html {
    height: 100%;
  }
  
  body {
    min-height: 100%;
    display: flex;
    flex-flow: column nowrap;
  }
  
  #header {
    background: #c13832;
    color: white;
    border-top: 2px solid white;
  }
  
  #header a,
  #header a:visited,
  #header .links {
    color: white;
  }
  
  #header .links > .dropdown > ul,
  #header .links > .dropdown > ul > li > a,
  #header .links > .dropdown > ul > li > a:visited {
    color: black;
  }
  
  #header .links > .dropdown > ul {
    padding: 0;
  }
  #header .links > .dropdown > ul > li > a {
    margin: 0;
    display: block;
    padding: 1px 10px;
  }
  
  #header .links {
    padding: 0;
    width: auto;
  }
  
  #header .wrapper {
    width: auto;
    margin: 0;
  }
  
  #titles {
    border-radius: 0;
    padding: 5px 0 0;
    width: 100%;
    max-width: 1024px;
    margin: 0 auto;
  }
  #header #badge {
    background: #0095dd !important;
    color: #fff !important;
    margin-inline-start: -176px !important;
    display: inline-block;
    height: 2em;
    width: 2em;
    border-radius: 100%;
    padding: 0;
    text-align: center;
    line-height: 2em;
  }

  #create-account-left {
    border-right: none;
  }

  
  #header {
    background: #C13832 !important;
    box-shadow: none !important;
    border-bottom: none !important;

  }
  #mini_login_container_top,
  #header a,
  #header .form.quicksearch_form {
    color: #fff !important;
  }
  #header .separator,
  #bugzilla_version {
    color: #fff !important;
    opacity: 0.5 !important;
  }
  #header input {
    border: none !important;
  }
  #title a {
    font-weight: normal;
  }
  #title a::before {
    content: "";
    width: 32px;
    height: 32px;
    vertical-align: middle;
    background-color: white;
    border-radius: 100%;
    background-image: url(data:image/svg+xml;base64,);
    background-size: 30px;
    background-repeat: no-repeat;
    background-position: center;
    margin-right: 13px;
    display: inline-block;
  }
  #header .dropdown {
    background: none;
    border: none;
  }
  #header .dropdown .anchor {
    color: #fff;
    background-image: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" width="8" height="4"><polygon points="0,0 4,4 8,0" fill="white"/></svg>') !important;
  }
  #header .dropdown ul {
    border-radius: 0.25em;
  }
  #header .dropdown ul a {
    color: #000 !important;
  }
  #header .quicksearch_form {
    float: right !important;
    font-size: 0;
    margin-top: -2px;
    margin-bottom: 5px;
  }
  #header .quicksearch_form input,
  #quicksearch_main {
    background-image: url(http://firefoxux.github.io/firefox-svg-icons/icons/navigation/magnifying-glass.svg);
    background-size: 18px;
    background-color: #fff;
  }
  #header .quicksearch_form input {
    height: 25px;
  }
  #header .quicksearch_form + li {
    border-left: 1px solid rgba(255,255,255,0.5);
    margin-left: 5px;
    padding-left: 5px;
  }
  #quicksearch_top {
    font-size: 13px !important;
  }
  #header .quicksearch_form a {
    display: inline-block;
    width: 20px;
    height: 20px;
    vertical-align: middle;
    margin-top: 4px;
    font-size: 0;
    background: url(https://firefoxux.github.io/firefox-svg-icons/icons/alert/help.svg);
    filter: invert(1);
    margin-left: 10px;
    background-size: cover;
  }
  #find_top {
    display: none !important;
  }
  #moz_tab {
    width: 150px;
  }
  #moz_tab > a {
    position: absolute;
    top: 2px;
    display: block;
    width: 150px;
    height: 36px;
    text-indent: 120%;
    white-space: nowrap;
    overflow: hidden;
    background-image: url(data:image/svg+xml,%3Csvg%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%20viewBox%3D%220%200%20300%2072%22%20width%3D%22150%22%20height%3D%2236%22%3E%3Cpath%20fill%3D%22%23fff%22%20d%3D%22M0%200q21%200%2030%2035q10%2036%2050%2036h135q40%200%2050-36q9-35%2030-35zm78.07%2020.6c.44.7.64%201.32.9%202.6%201.74-1.7%203.9-2.6%206.24-2.6%202.2%200%203.9.7%205.3%202.1.4.36.7.82%201%201.26%202.4-2.4%204.5-3.35%207.3-3.35%202%200%203.9.6%205.08%201.6%201.45%201.3%201.9%202.8%201.9%206.3v18.2h-6.5V29.8c0-3.06-.36-3.66-2.1-3.66-1.26%200-3.02.88-4.46%202.18v18.4h-6.4V30c0-3.18-.47-3.93-2.33-3.93-1.24%200-2.95.64-4.4%201.95V46.7H73V28.8c0-3.7-.27-5.3-.97-6.56l6.07-1.65m39.5%207.5c-.48%201.3-.7%203.1-.7%205.7%200%202.9.3%205.1.83%206.4.6%201.4%202.14%202.1%203.4%202.1%202.94%200%204.18-2.6%204.18-8.7%200-3.46-.4-5.7-1.3-6.87-.6-.84-1.7-1.35-2.9-1.35-1.6%200-2.9%201-3.4%202.6zm12.04-3.7c2.05%202.4%202.97%205.2%202.97%209.3%200%204.4-1%207.4-3.2%209.9-1.9%202.2-4.5%203.5-8.4%203.5-6.94%200-11.5-5.2-11.5-13.26%200-8.06%204.6-13.4%2011.5-13.4%203.68%200%206.5%201.23%208.7%203.84m24.5-3.34v4.6L142.85%2042h11.76l-1.6%204.7h-18.9v-4.16l12-16.77h-11v-4.7h19.02m10.56-.6V46.7H158V21.5zm.8-6.47c0%202.3-1.9%204.15-4.2%204.15-2.3%200-4.1-1.84-4.1-4.15%200-2.3%201.9-4.15%204.2-4.15%202.2%200%204%201.85%204%204.15m11.42%202.3v20c0%204.4.05%205%20.45%205.7.23.46.8.7%201.34.7.22%200%20.36%200%20.72-.1l1.13%204c-1.13.46-2.53.7-4%20.7-2.84%200-5.15-1.34-5.94-3.48-.5-1.3-.6-2.1-.6-5.77V18.9c0-3.35-.1-5.4-.33-7.7l6.86-1.56c.2%201.4.3%203.06.3%206.67m13.9%200v20c0%204.4.05%205%20.46%205.7.23.5.8.7%201.33.7.27%200%20.4%200%20.77-.1l1.17%204c-1.17.5-2.57.7-4%20.7-2.88%200-5.2-1.3-6-3.4-.5-1.3-.6-2.1-.6-5.73V18.9c0-3.35-.1-5.4-.35-7.7l6.83-1.56c.25%201.4.34%203.06.34%206.67M210%2035.5c-4.62%200-6.25.84-6.25%203.9%200%202%201.27%203.36%202.97%203.36%201.25%200%202.5-.65%203.5-1.75l.1-5.5zM201.38%2022c2.5-1.04%204.64-1.5%207-1.5%204.32%200%207.26%201.6%208.27%204.47.34%201.05.5%201.85.46%204.6l-.1%208.6v.46c0%202.76.5%203.8%202.4%205.26l-3.6%204.1c-1.5-.7-2.9-1.8-3.6-3.1-.5.5-1%201-1.5%201.3-1.24.9-3.06%201.4-5.16%201.4-5.7%200-8.8-2.9-8.8-8%200-6%204.16-8.8%2012.3-8.8h1.5v-1c0-2.86-.55-3.8-3-3.8-2.14%200-4.66%201.03-7.4%202.9l-2.87-4.8c1.38-.8%202.38-1.3%204.2-2.1%22%2F%3E%3C%2Fsvg%3E);
    background-repeat: no-repeat;
    z-index: 2;
  }
  
  #moz_tab > a > img {
    visibility: hidden;
  }
  
  #titles #title {
    font-weight: normal;
    font-size: 100%;
    width: 179px;
  }
  
  #header .wrapper > .links {
    padding: 10px 0;
    max-width: 1024px;
    margin: 0 auto;
  }
  
  #floating-message-text,
  body .ui-tooltip {
    box-shadow: 0 0 0 0.5px hsla(0,0%,0%,0.15),
                0 2px 4px hsla(0,0%,0%,0.3);
    background: hsl(0,0%,95%);
    border: none;
  }
  
  body[class*="bz_group_"] {
    padding-left: 0;
    border: 0;
  }
  
  body[class*="core-security"],
  body.bz_group_infrasec {
    background: #ffe0b0;
  }
  
  #bugzilla-body {
    flex: 1;
    padding: 10px 0;
    align-self: center;
    width: 100% !important; /* otherwise align-self:center makes us shrink to fit */
    max-width: 1024px !important;
  }
  
  .module {
    background: transparent;
    box-shadow: none;
    padding-top: 5px;
    padding-bottom: 5px;
  }

  .module ~ .module {
    background: #f6f4ec;
    box-shadow: -50vw 0 0 #f6f4ec,
                50vw 0 0 #f6f4ec;
  }
  
  #field-status_summary {
    border: 0;
    padding-bottom: 5px;
  }
  
  .module-header,
  .module-content {
    max-width: 1024px;
    margin: 0 auto;
  }
  
  /* .module-header takes up the full width, and it's what has the JS onclick handler,
   * but I don't want the full width to be clickable. Hack around that using pointer-events:none. */
  .module-header {
    pointer-events: none;
  }
  
  .module-header > .module-latch {
    pointer-events: auto;
    border-radius: 3px;
  }
  
  .module-header > .module-latch:hover {
    background: hsla(0,0%,0%,0.05);
  }
  
  .module-header > .module-latch:active:hover {
    background: hsla(0,0%,0%,0.1);
  }
  
  .module-content {
    background: transparent;
    display: block;
  }
  .module-content::after {
    content: '';
    display: block;
    clear: both;
    height: 1px;
  }
  
  #module-attachments .module-content {
    flex-flow: column nowrap;
  }
  
  .module-content > .fields-lhs {
    float: left;
    width: 45%;
  }
  .module-content > .fields-rhs {
    float: right;
    width: 45%;
  }
  .module-content > .field {
    display: block;
    clear: both;
  }
  
  table.mozreview-requests {
    width: 100%;
  }
  
  tbody.mozreview-request > tr > td {
    vertical-align: top;
  }
  
  tbody.mozreview-request > tr > td:last-child {
    white-space: nowrap;
  }
  
  #user-story {
    padding: 10px;
    background: white;
  }
  #user-story-actions {
    margin-top: 5px;
    margin-right: 5px;
  }
  
  #attachments {
    background: white;
    margin: 5px 0;
  }
  
  #attachments td {
    border: 0;
  }
  
  #attachments .attach-patch {
    background: #D6F9FF !important;
    background: #ffc !important;
  }
  
  button.minor,
  button.major {
    border-radius: 4px !important;
    transition: none;
  }
  
  #top-actions,
  #comment-actions {
    background: #565a5c;
    box-shadow: 50vw 0 #565a5c, -50vw 0 #565a5c;
    color: #bbb;
    margin: 0;
    height: 30px;
    padding: 10px;
    color: white;
  }
  #top-actions + #comment-actions {
    margin-top: -50px;
    background: transparent;
  }
  
  #top-actions button,
  #comment-actions button {
    color: white;
  }
  
  #top-actions .save-btn {
    margin-left: 4px;
  }
  
  button.minor,
  button.minor:hover,
  button.minor:active {
    background: hsla(0,0%,0%,0.1);
  }
  
  button.minor:hover {
    box-shadow: inset 0 0 0 2px hsla(0,0%,0%,0.05);
    background: hsla(0,0%,0%,0.05);
  }
  
  button.minor:hover:active {
    background: hsla(0,0%,0%,0.15);
  }
  
  .field-button {
    margin-left: 4px !important;
  }
  
  button.major,
  button.major:hover,
  button.major:active,
  #attachments-add-btn {
    color: white;
    background: #4caf50;
    box-shadow: inset 0 0 0 2px #4aa84a;
    transition: background-color .1s ease-in-out;
  }
  button.major,
  button.major:hover,
  button.major:active {
    padding: 4px 8px;
    margin: 0 4px;
  }
  
  button.major:hover,
  #attachments-add-btn:hover {
    background: #66bb6a;
  }
  
  .change-set {
    border: 1px solid #CCC;
    border-radius: 5px;
    margin: 10px;
    max-width: 1024px;
    background: white;
    overflow: hidden;
  }
  
  .change-set:target {
    outline: none;
    box-shadow: 0 0 0 2px #0095dd;
  }
  
  .change-set .comment,
  .change-set .change {
    padding-bottom: 0;
  }
  
  .change-author,
  .comment-actions {
    padding: 0 !important;
  }
  
  .change-gravatar {
    padding: 0 !important;
  }
  
  .change-head td {
    font-size: 13px;
    line-height: 1.3;
  }
  
  .change-head {
    padding: 5px;
    box-shadow: 0 1px #CCC;
  }
  
  .change-author .vcard {
    font-weight: normal;
  }
  
  .comment-tags {
    padding-top: 5px !important;
    padding-left: 0px !important;
  }
  
  .comment-tag {
    background: #EEE;
    padding: 0 4px;
    font-size: 90%;
    line-height: 1;
  }
  
  .comment-text {
    padding: 10px;
    overflow-y: hidden;
  }
  
  .comment-text span.quote,
  .comment-text span.quote_wrapped {
    background: transparent !important;
    color: hsla(0,0%,0%,0.5) !important;
    display: block !important;
    position: relative;
    white-space: pre;
    overflow-x: auto;
    margin-bottom: -1em;
  }
  .comment-text span.quote::before,
  .comment-text span.quote_wrapped::before {
    content: '';
    display: block;
    position: absolute;
    left: 0;
    top: 5px;
    bottom: 5px;
    width: 2px;
    border: solid white;
    border-width: 0 2px 0 9px;
    /* just wide enough to cover the '>' character in 13px Menlo on macOS Firefox. */
    background: #DDD;
  }
  
  .activity {
    line-height: 1.3;
    font-size: 12px;
    padding: 8px;
    background: hsla(0,0%,0%,0.05);
  }
  
  #add-comment,
  #new-comment-actions,
  #bottom-actions {
    margin: 0;
  }
  
  #add-comment {
    padding: 10px 10px 50px;
    box-shadow: -50vw 0 #f6f4ec, 50vw 0 #f6f4ec;
    background: #f6f4ec;
  }
  
  body.bug_modal > #bugzilla-body {
    padding-bottom: 0; /* so that there's no space below #add-comment */
  }

  #new-comment-actions,
  #bottom-actions {
    height: 20px;
    padding: 0 10px 20px 10px;
  }
  
  #add-comment + #new-comment-actions,
  #new-comment-actions + #bottom-actions {
    background: none;
    margin-top: -40px;
  }
  
  #comment-tabs,
  #comment_tabs {
    display: flex;
    flex-flow: row nowrap;
    padding: 0 !important;
  }
  #comment-tabs > li,
  .comment_tab {
    border: 0;
    padding: 2px 1em !important;
    text-transform: uppercase;
    cursor: pointer;
    background: transparent;
  }
  #comment-tabs > li.current,
  .comment_tab.active_comment_tab {
    border-bottom: 2px solid #0095dd;
    color: #0095dd;
    background: none;
    font-weight: bold;
  }
  #comment-tabs > li:not(.current):hover,
  .comment_tab:not(.active_comment_tab):hover {
    border-bottom: 2px solid #999;
  }
  
  #comment,
  #comment-preview{
    background: white;
    border: 1px solid #DDD;
  }
  
  #viewFrame {
    background: white;
  }
  
  
  #attachment_table tr:not(#a0):not(.bz_attach_footer) {
    background-image: linear-gradient(var(--attachment-bg), var(--attachment-bg)) !important;
    background-repeat: no-repeat;
    background-size: 40px;
    --attachment-bg: #555;
  }
  #attachment_table tr:not(#a0):not(.bz_attach_footer) td {
      padding-left: 45px;
    position: relative;
  }
  #attachment_table tr:not(#a0),
  .bz_attach_footer {
    background-color: #fff !important;
    --attachment-image: url(https://firefoxux.github.io/firefox-svg-icons/icons/content/save.svg);
  }
  #attachment_table tr:not(#a0):not(.bz_attach_footer) td a::before {
    content: "";
    display: inline-block;
    position: absolute;
    top: 0;
    left: 0;
    width: 40px;
    height: 100%;
    background-size: 16px;
    background-position: center;
    background-repeat: no-repeat;
    background-image: var(--attachment-image);
    filter:invert(1);
    opacity: 0.9;
  }
  [class^="bz_contenttype_image"] {
    --attachment-bg: #57bd35 !important;
    --attachment-image: url(https://storage.googleapis.com/material-icons/external-assets/v4/icons/svg/ic_landscape_black_24px.svg) !important;
  }
  [class^="bz_contenttype_video"] {
    --attachment-bg: #ea385e !important;
    --attachment-image: url(https://firefoxux.github.io/firefox-svg-icons/icons/navigation/camera.svg) !important;
  }
  #attachment_table .bz_patch {
    background-color: rgba(2, 83, 139, 1) !important;
    --attachment-bg: #02538b !important;
    --attachment-image: url(https://firefoxux.github.io/firefox-svg-icons/icons/developer/diff.svg) !important;
  }
  
  
  /* Home */
  #welcome {
    margin-top: 30px;
  }

  #welcome + .intro .bz_common_actions,
  .bz_common_actions {
    background-color: #0996f8 !important;
    background-image: none !important;
    color: white !important;
    display: block !important;
    position: relative;
    margin: 5px;
    border-radius: 2px;
    height: 145px;
  }
  #quicksearchForm {
    padding-top: 4vh !important;
  }
  #welcome {
    margin-bottom: 3vh !important;
  }
  #welcome + .intro .bz_common_actions:hover,
  .bz_common_actions:hover,
  #welcome + .intro .bz_common_actions:active,
  .bz_common_actions:active,
  #welcome + .intro .bz_common_actions:focus,
  .bz_common_actions:focus {
    box-shadow: inset 0 0 0 5px hsla(0,0%,0%,0.2);
  }
  .bz_common_actions span {
    top: auto !important;
    bottom: 10px !important;
    position: absolute !important;
    left: 0 !important;
    right: 0 !important;
    text-align: center;
    font-weight: normal;
  }
  .bz_common_actions::before {
    content: "";
    position: absolute;
    background-size: cover;
    filter: invert(1);
    opacity: 0.85;
    top: 50%;
    left: 50%;
    width: 48px;
    height: 48px;
    margin: -30px 0 0 -24px;
  }
