@-moz-document url-prefix("https://ox.hosteurope.de/appsuite/") {
/* liste */

/* E-Mail Absendername */
.from .person {
	color   : transparent;
	display : none;
	width   : 0px;
}
.from:after {
	content      : 'Absender Name';
	display      : inline-block;
	white-space  : nowrap;
}
.unread .from:after {
	font-weight : bold;
}
/* Datum */
time.date:before {
	content      : '00:00';
	display      : inline-block;
	margin-right : 5px;
	white-space  : nowrap;
}
time.date {
	height   : 15px;
	overflow : hidden;
	width    : 33px;
}
.unread time.date:before {
	font-weight : bold;
}
.unread time.date {
	width : 34px;
}
/* Größe aka groesse aka size aka email size */
.size:before {
	content      : '0 KB';
	display      : inline-block;
	margin-right : 5px;
	white-space  : nowrap;
}
.size {
	height   : 15px;
	overflow : hidden;
	width    : 29px;
}
.unread .size:before {
	font-weight : bold;
}
.unread .size {
	width : 30px;
}
/* betreffzeile */
.subject .drag-title {
	display : none;
	color   : transparent;
	width   : 0px;
}
.subject .flags:after {
	content      : 'E-Mail Betreffzeile';
	display      : inline-block;
	white-space  : nowrap;
}
.unread .subject .flags:after {
	font-weight : bold;
}

}

@-moz-document url-prefix("https://ox.hosteurope.de/appsuite/") {
/* in email */

/* betreffzeile in email */
.mail-detail-pane .thread-view-header .subject {
	display : none;
}
.mail-detail-pane .thread-view-header:after {
	content     : 'Email Betreffzeile';
	display     : inline-block;
	font-size   : 20px;
	font-weight : 300;
	padding     : 12px 16px 4px 16px;
	white-space : nowrap;
	word-break  : break-word;
}

/* absender name in email */
.detail-view-row .from .person-link {
	display : none;
}
.detail-view-row .from:after {
	content     : 'Absender Name';
	display     : inline-block;
	white-space : nowrap;
}
/* an e-mail adresse in email */
.detail-view-row .recipients .person-to {
	display : none;
}
.detail-view-row .recipients .io-ox-label:after {
	content     : 'Empfänger E-Mail Adresse';
	display     : inline-block;
	white-space : nowrap;
}
}

@-moz-document url-prefix("https://ox.hosteurope.de/appsuite/") {
article.mail-detail section.body {
	display : none !important;
}
article.mail-detail {
	background-color : #FFF;
}
article.mail-detail:after {
	background-color : rgba(255,255,255,1);
	background-image : repeating-linear-gradient(45deg, transparent, transparent 20px, rgba(0,0,0,0.5) 20px, rgba(0,0,0,0.5) 40px);
	content          : 'E-Mail Inhalt';
	/* background-color : #FFF; */
	display          : block;
	height           : 1000px;
	font-weight      : bold;
	padding          : 12px;
}
}

