css:
@-moz-document domain("ghacks.net") {
 
  #sidebar
{
display: none !important;
}

body #content #main {
    width: 100% !important;
}

html body {
    background-color: #222;
    padding: 20px 50px;
}
body #content #main {
    width: 100% !important;
}
.entry,
.entry p,
#commentform input.txt,
#commentform textarea,
#comments h3,
.page-title,
.post .title,
.page .title a:link,
.page .title a:visited,
#respond h3 {
    color: #fff !important;
}

.page .title,
.post .title a:link,
.post .title a:visited
{
    color: #0ff !important;
}

#commentform textarea,
#commentform input.txt {
    background: #555 !important;
}
select,
.searchform,
#searchform {
    filter: invert(80%) !important;
}
#comments {
    border-color: #ddd;
}
.entry table {
    background: inherit !important;
    ;
}
body,
p {
    color: #eee !important;
    ;
}

#wrapper {
    padding: 10px 50px;
    background: #222 !important;
}

#logo {
    margin-bottom: 10px;
}

#logo img {
    filter: invert(87%);
}

#main-sidebar-container #sidebar,
#footer-widgets,
#footer {
    display: none;
}
#comments .comment .comment-container {
    padding: 0 20px;
}
#main #comments .comment-head {
    padding-top: 10px;
}
body #wrapper #content .comment .reply {
    padding: 5px 0;
}
body #wrapper #content .comment .reply{
    color: #eee !important;
    background: none;
    text-shadow: none;
    box-shadow: none;
    padding: 0;
}
body #wrapper #content .reply {
    color: #eee;
}
#main #comments .comment {
    border-top: 0;
    border-left: 1px #999 solid;
    margin: 0;
    background: #222;
}
#main #comments .comment.depth-1 {
    border: 1px #555 solid;
    margin-bottom: 10px !important;
}
#main .post-meta,
#main header {
    text-align: center
}
#main .post-meta .post-comments {
    float: none
}

#cookie-notice
{
display: none !important;
}
  
}
