.headerNormal-1cioxU,
.body-3rkFrF,
.note .note-3Zfyi2,   
.footer-2J5zqP,
.quick-message,
.container-RYiLUQ,
.friends-header,
.friends-table,
.guilds-wrapper,
.guild-header header,
.channels-wrap,
.private-channels .search-bar,
.private-channels,
.guild-channels,
.account,
.friend-table-add-header,
.chat,
.content,
.layers,
.title-wrap:not(.search-bar),
.messages-wrapper,
.messages .divider span,
.messages .divider:before,
.content,
.message-group-blocked,
.is-local-bot-message,
.channel-members-loading,
.channel-members-loading .heading,
.channel-members-loading .member,
.container-iksrDt,.channelTextArea-1HTP3C, .channelTextArea-os01xC,.titleWrapper-3Vi_wz, .theme-dark,.title-qAcLxz,
.theme-dark .chat form,   
.sidebar-region,.content-region,
.headerBar-cxbhPD,  
.topSectionNormal-2LlRG1,
.theme-dark .body-3_tdh6, 
.topSectionStreaming-3rIBIm,
.topSectionPlaying-3jAH9b,
.headerStreaming-3F-wjT,
.headerPlaying-2eYqm9,
