}  
    
.userPopout-4pfA0d,
  .body-3rkFrF,
  .footer-2J5zqP,
  .note,
  .quick-message{color: /*[[bgC]]*/}    
.userPopout-4pfA0d { border: 1px solid /*[[bgC]]*/}   
.note input,.note textarea{
background: none !important; 
color: inherit !important;
width: 100%;
font-size: 14px;
line-height: 20px;
padding: 8px 10px;
height: 80px !important;
border-radius: 5px;
border: 1px solid rgba(255,255,255,0.2);
}
   
/* Guilds */
.guilds-wrapper .scroller-wrap{
width: 81px;
}
   
.guilds-wrapper .guilds .guild,.guilds-wrapper .guilds .guild .guild-inner a, .guilds-wrapper .guilds .friends-icon{
width: 40px;
height: 40px;
border-radius: 50%;
}   
   
.guilds-wrapper,.channels-wrap,.chat,.theme-dark .channel-members,.channel-members,.account .btn{
background: black !important;
}

.channel-textarea-inner{
border:1px solid #eee !important;
}

/* Account Settings */
.input-2YozMi:hover{
border-color:  /*[[bgC]]*/;
}   

/* Profile */
.root-2sNHUF{
    border: 1px solid  /*[[bgC]]*/;
}
.scroller-fzNley.listScroller-1M3aYe{
    max-height: 180px;
}

/* Scrollbar */

.scrollbar, .scrollbar::after,.scrollbar-2so3Ly,.scroller-wrap .scrollbar,.scroller-fzNley{
background: black !important;
}
   
.scroller-fzNley{
overflow-y: auto;
}   

.theme-dark .friends-table .scroller-wrap.polyfill .scrollbar .track, .theme-dark .messages-wrapper .scroller-wrap.polyfill .scrollbar .track{
background: black;
}

.scrollbar .thumb,.scrollbar-2so3Ly .thumb-3VsfAI{
background:  /*[[bgC]]*/ !important;
}

.scrollbar .thumb::after{
background:  /*[[bgC]]*/ !important;
}

.pad-3D5tOh{
background: black !important;
}   
   
.scroller-wrap.fade .scrollbar .thumb,.scrollbar-2so3Ly{
opacity: 1 !important;
}
    
.scroller-wrap.fade .scrollbar .thumb,.scrollbar-2so3Ly .thumb-3VsfAI, .scrollerFade-28dRsO .scrollbar-2so3Ly .track-2Kex6E,.scrollbar .thumb {
-moz-transition: none;
-webkit-transition: none;
-o-transition: color 0 ease-in;
transition: none;
opacity: 1 !important;
width: 4px;   
}

  
}
