css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bulbagarden.net") {

/* Ad & cruft removal */
#bottomframe {display:none!important;}
#extadcontainer {display:none!important;}
#rightcolbox {display:none!important;}
#inhouseadcontainer {display:none!important;}
#headeradinhouse {display:none!important;}
#amazon-related {display:none!important;}
.ad-placement, .ad-tag, #headerad {display:none!important;}
#bottomframelower {display:none!important;}
#upper-article {display:none!important;}
div.ub-emb-container {display:none!important;}
#leaderboard_atf {display:none;}
div#mw-content-text~div.visualClear+div {display:none;}
div#cdm-zone-end {display:none;}
div.printfooter~div[style="min-height:315px;"] {display:none;}
style + div[style="min-height:315px;"] {display:none;}
div#AdRow {display:none;}
div#med_rect_atf2 {display:none;}
.adrow-bottom {display:none;}
div[id*="adthrive"] {display:none!important;}
  /* Fix the content area */
#column-content, #content, #outercontentbox, #bodyContent {min-height:0px!important;}
#column-content {float:right!important;width:85%!important;overflow:visible;}
#content {max-width:100%!important;width:100%!important;float:left;border-top-right-radius:0px!important;border-bottom-right-radius:0px!important;border-right-width:0px!important;padding-right:30px!important;box-sizing:border-box!important;margin-left:0px;margin-right:0px;margin-top:15px;}
#contentbox {border-right-width:0px!important;}
html body {margin-top:0px!important;}
  /* Fix the menu */
#column-one {width:13%!important;box-sizing:border-box!important;display:block!important;visibility:visible!important;position:static!important;float:left!important;margin-left:16px!important;background-color:transparent;margin-top:39px;padding-top:0px!important;}
#column-one ::before {display:none!important;}
#column-one div.portlet {margin-bottom:5px;border-style:solid;border-width:1px;border-color:#008800;background-color:#d8ffd8;border-radius:7px;width:100%!important;}
#column-one > div#p-personal {display:none!important;}
#column-one div.portlet div {width:100%;margin:0px;padding:0px;border-width:0px;}
#column-one div.portlet ul {background-color:white;border-bottom-left-radius:7px;border-bottom-right-radius:7px;box-sizing:border-box!important;width:100%!important;padding:0px;margin:0px;list-style:none!important;}
#column-one div li {height:14pt;width:100%!important;padding-left:10px;}
#column-one div li:hover {background-color:#eeffee;}
#column-one div li:first-child {padding-top:5px;}
#column-one div li:last-child {padding-bottom:5px;}
#column-one div li:last-child:hover {border-bottom-left-radius:7px;border-bottom-right-radius:7px;}
#column-one div li a {font-size:9pt;vertical-align:middle;}
#column-one div.portlet h3 {color:#006600;font-size:11pt;margin-left:7px;font-variant:small-caps;}
div#p-logo {position:static;background-color:rgba(0,0,0,0)!important;border-width:0px!important;}
div#p-logo a.mw-wiki-logo {margin-right:auto;margin-left:auto;}

}
