css:
@-moz-document domain("semiengineering.com") {
.col-sm-4 {
    /* remove sponsored column in articles */
    display: none;
    width: 0%;
}
.col-md-6 {
    /* resize article column */
    width: 80%;
}
.col-md-3 {
    /* resize technical papers column */
    width: 20%;
}
#stock-news {
    display: none;
}
body {
    color: #bbb;
}
}

@-moz-document domain("semiengineering.com") {
body {
    /* set background color */
    background: #333;
}
/* header part */
div.header_wrapper {
    /* bar where semiE logo resides */
    height: 105px;
}
div.header {
    border-bottom: 0px !important;
    height: 1em;
}
.logo img {
    /* change semiE logo to fit inside header wrapper */
    position: absolute;
    top: 0px;
    height: 0;
    width: 0;
    padding: 40px 360px;
    background-image: url(https://semiengineering.com/wp-content/themes/se_current/images/se_logo_blk.gif);
    background-repeat: no-repeat;
}
div.nav-menu_wrapper {
    /* where the different categories are - top of page */
    background: #1b1b1b;
    border-bottom: 3px solid #1b1b1b;
}
.nav-menu_second-level li a:hover,
.nav-menu_new-menu ul a:hover {
    color: #ccc;
    background: #272727
}
div.single_top_page_main_cat_button a {
    /* the category button in each article top */
    background: #215a92;
    color: #333 !important;
    box-shadow: inset 0px 0px 4px 0px #051521;
}
.mlb {
    border-left: 0px !important;
}
/* recommended papers sidebar */
div.post_cnt,
div.post_snippet,
div.tag_list a,
div.post_author_bio_single {
    color: #bbb;
}
.snippet_header,
.snippet_header_l {
    color: #000;
}
div.post_snippet h3 {
    color: #215a92;
}
#technicals ul.display-posts-listing a.title {
    color: #215a92 !important;
}
table.gsc-search-box td.gsc-input {
    background: #1d1d1d;
}
/* front page top stories */
div.post_snippet_l h3 {
    color: #215a92 !important;
}
div.post_snippet_l {
    color: #bbb;
}
h3.special_reports {
    color: #bbb;
    background-color: #272727;
}
/* special reports headlines */
div.special_reports_slides_wrapper {
    background: #1d1d1d;
    border: 2px solid #000;
}
div.special_reports_slides_post_title a {
    color: #215a92;
}
div.special_reports_slides_exceprt {
    color: #ccc;
}
.special_reports_wrapper > div:nth-child(2) > div:nth-child(1),
.special_reports_wrapper > div:nth-child(2) > div:nth-child(2),
.special_reports_wrapper > div:nth-child(2) > div:nth-child(3) {
    width: 33.33% !important/* Resize special reports columns */
}
/* bottom next/last paper */
div.pna_wrapper,
div.pna_1,
div.pna_r {
    background: #1f1f1f;
}
div.pna_l,
div.pna_r {
    border: 1px solid #1f1f1f;
    background: #272727;
}
.pna_wrapper > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) {
    width: 50%;
    /* Width of article selector back to 50% to have them side-by-side*/
    background: #242424;
}
.pna_wrapper > div:nth-child(1) > div:nth-child(1) > div:nth-child(2) {
    width: 50%;
    background: #1a1a1a;
}
/* knowledge center banner */
div.fp_kc_banner {
    background: #292929;
    padding: 4% 2%;
}
/* Leave a reply section */
.loop_post_wrapper {
    background: #2b2b2b;
    margin-bottom: 30px !important;
}
#reply-title {
    color: #ccc;
}
/* footer */
.footer_logo {
    background: #1d1d1d;
}
div.footer_bk {
    background: #1b1b1b;
}
div.col-md-3:nth-child(1),
.footer_c > div:nth-child(1) > div:nth-child(3) {
    width: 25%
}
div.row:nth-child(2) > div:nth-child(1),
div.row:nth-child(2) > div:nth-child(2),
.footer_c > div:nth-child(1) > div:nth-child(2) {
    width: 50%
}
}

@-moz-document url-prefix("https://semiengineering.com/category") {
.col-md-6 {
    color: #bbbbae;
}
div.sec_header {
    background: #243d56;
    border: 2px solid #9c9c9c;
}
div.sec_header h1,
div.sec_header h1 em {
    color: #9c9c9c;
    border: 2px solid #9c9c9c;
    background-color: #1e2e3e;
}
div.post_snippet_xl h3 {
    color: #215a92;
}
.col-lg-3 + .col-lg-3 {
    display: none;
    /* remove sponsor bar that pops up in category page - selector might cause issues*/
}
}

@-moz-document url-prefix("https://semiengineering.com/kc"), url-prefix("https://semiengineering.com/startup-corner/"), url-prefix("https://semiengineering.com/semiconductor-research/") {
div.sec_header {
    background: #243d56;
    border: 2px solid #9c9c9c;
}
div.sec_header h1,
div.sec_header h1 em {
    color: #9c9c9c;
    border: 2px solid #9c9c9c;
    background-color: #1e2e3e;
}
ul.lmenu li a:hover,
ul.lmenu_drk li a:hover {
    background: #215a92;
}
ul.lmenu li a,
ul.lmenu_drk li a {
    color: #bbb;
}
li.sidebar_widget {
    color: #bbb;
}
body {
    color: #bbb;
}
}

@-moz-document url-prefix("https://semiengineering.com/knowledge_centers/"), url-prefix("https://semiengineering.com/knowledge-center/") {
/* This section is for the new knowledge center page(s) */
/* Colors */
div.sec_header {
    background: #1d1d1d;
}
.kc-inside .vg-treemenu .treeview span:hover {
    color: #ccc !important;
}
/* Resize columns */
div.col-sm-8.col-md-6.col-lg-6.nopadding {
    width: 60%
}
div.hidden-xs.hidden-sm.col-xs-0.col-sm-0.col-md-3.col-lg-3,
div.col-sm-4.col-md-3.col-lg-3 {
    width: 20% !important;
    display: block !important;
}
ul.sidebar_widget {
    display: none;
}
.col-xs-2 {
    height: 0px;
}
.post_title_wrapper {
    margin-bottom: 0px;
    padding: 0 0 0px;
}
}

@-moz-document url-prefix("https://semiengineering.com/special-reports/") {
/* Special reports page */
.page-template-report ul.display-posts-listing li a {
    color: #215a92;
}
li.sidebar_widget {
    color: #bbb;
}
}

@-moz-document url-prefix("https://semiengineering.com/jobs/") {
/* Jobs page */
div.hidden-xs.hidden-sm.col-xs-0.col-sm-0.col-md-3.col-lg-3 {
    width: 0% !important;
    display: none !important;
}
div.col-xs-9 {
    width: 100%
}
ul.job_listings li.job_listing a:hover {
    background-color: #1b1b1b;
}
ul.job_listings li.job_listing a:focus {
    background-color: #1d1d1d;
}
div.position > h3:nth-child(1) {
    color: #ccc;
}
}

