css:
@-moz-document domain("workflowy.com") {
/*=============================================
        [WorkFlowy Theme] - Noty
=============================================*/
/*

  Noty
	 Write workflowy like a text editor.

  Enjoy in this style.
  
  by wineroses. (http://d.hatena.ne.jp/wineroses/)
  
*/

body{background:white;}
.page{font-family:Hiragino Sans, Meiryo, sans-serif; color:black; background:white;line-height:1.6em !important;border-radius:5px;}
.project.selected>.name .content{font-size:large;font-weight:bold;line-height:1.6em !important;margin-bottom:-16px;}
.project.selected>.children>div>.name .content{color:darkred;font-weight:bold;width:100%;margin-top:10px;line-height:1.6em !important;}
.selected>.children>.project>.children .project>.name>.content{font-family:YuMincho, Hiragino Mincho ProN, Meiryo, serif;line-height:1.6em !important;}
.notes>.content{color:black !important;line-height:1.6em !important;}
.contentTag{color:silver;}
.contentLink{color:darkred !important;}
.contentBold{border-bottom:2px pink solid;}
.contentItalic{background:yellow;}
.contentUnderline{border-bottom:2px aqua solid;text-decoration:none;}
.done>.name>.content{color:silver;}
.contentTag[title*="@l1"]{color:red;background:red;}
.contentTag[title*="@l2"]{color:orange;background:orange;}
.contentTag[title*="@l3"]{color:green;background:green;}
.contentTag[title*="@l4"]{color:blue;background:blue;}

#documentView{background:none;}
#header{opacity:0;}
#header:hover{opacity:1;}
.parent>.name>.parentArrow{display:none !important;}
.parent>.name{font-size:small;margin:3px;}
.project.selected>.children>.project{margin-right:-24px;}
.children{position:relative;margin:0px;border:0px;padding:0px;}
.selected .project>.name,.selected .project>.notes,.selected>.children>.project .project{padding-left:0;margin-left:0}
.bullet,.project.task>.name>.bullet,.project.open>.name>.bullet,.noHalo .bullet{background:none !important;border:none}
.project.highlighted::before,.project.moving::before,.project.tapped::before,.draggableNameClone::before {background:none !important;}
#moveDropLine{background:darkred;}
.content:focus{background:#f5f5f5;}

#controlsLeft{left: -3.2em;}
#controls #expandButton{left: -2em;}
  .bullet{left: -1em !important;}
  
}
