css:
@-moz-document domain("tradingview.com") {
/* MAIN SECTIONS */

body,
.chart-page {
	background: #2f2f40;
}
[class*="drawingToolbar"],
.widgetbar-pages,
.widgetbar-widget,
.widgetbar-tabs,
#footer-chart-panel,
#bottom-area .bottom-widgetbar-content,
#bottom-area .tv-screener-toolbar {
	background: none;
}
[class*="scrollTop"],
[class*="scrollBot"] {
	background: #606375;
}
.common-tooltip-wrapper.vertical {
	margin: 0 13px;
	border: none;
}
.common-tooltip-wrapper:before,
.common-tooltip-wrapper:after {
	display: none;
}
.common-tooltip-wrapper .common-tooltip-body {
	background: #1e1e2a;
	border-color: #606375;
	border-radius: 5px;
	color: #606375;
}
[class*="menuWrap"] [class*="menu-"],
.charts-popup-list {
	background: #292938;
	box-shadow: 0px 6px 5px 1px rgba(0, 0, 0, 0.57);
	border-radius: 5px;
	border: none;
}
.charts-popup-list {
	border: 1px solid rgba(30, 30, 42, 1);
}
[class*="menuWrap"] [class*="menu-"] [class*="menuBox"] {
	border-radius: 5px;
	box-shadow: inset 0px 0px 0px 1px rgba(30, 30, 42, 1);
}
[class*="menuWrap"] [class*="menu-"] [class*="item"],
[class*="menuWrap"] [class*="menu-"] [class*="item"] [class*="toolbox"],
[class*="menuWrap"] [class*="menu-"] [class*="item"] [class*="hint"] {
	background: none;
	color: #606375;
}
[class*="menuWrap"] [class*="menu-"] [class*="item"] svg path,
[class*="menuWrap"] [class*="menu-"] [class*="item"] svg circle,
[class*="menuWrap"] [class*="menu-"] [class*="item"] svg rect {
	fill: #606375;
}
[class*="menuWrap"] [class*="menu-"] [class*="item"]:hover {
	background: none;
	color: #d5d6e1;
}
[class*="menuWrap"] [class*="menu-"] [class*="item"]:hover svg path,
[class*="menuWrap"] [class*="menu-"] [class*="item"]:hover svg circle,
[class*="menuWrap"] [class*="menu-"] [class*="item"]:hover svg rect,
[class*="menuWrap"] [class*="menu-"] [class*="item"]:active svg path,
[class*="menuWrap"] [class*="menu-"] [class*="item"]:active svg circle,
[class*="menuWrap"] [class*="menu-"] [class*="item"]:active svg rect {
	fill: #d5d6e1;
}
[class*="menuWrap"] [class*="menu-"] [class*="item"]:active {
	background: none;
}
[class*="menuWrap"] [class*="menu-"] [class*="item"][class*="isActive"] {
	color: #54c7b0;
}
[class*="menuWrap"] [class*="menu-"] [class*="item"][class*="isActive"] svg path,
[class*="menuWrap"] [class*="menu-"] [class*="item"][class*="isActive"] svg rect,
[class*="menuWrap"] [class*="menu-"] [class*="item"][class*="isActive"] svg circle {
	fill: #54c7b0;
}
[class*="menuWrap"] [class*="menu-"] [class*="separator"] {
	background: #1e1e2a;
}
.charts-popup-list .separator {
	border-color: #1e1e2a;
}
.charts-popup-list.intervals-list .separator:first-of-type {
	display: none;
}
.charts-popup-list .item {
	background: none!important;
	color: #606375!important;
	padding: 10px 15px!important;
}
.charts-popup-list .item[class*="active"],
.charts-popup-list .item[class*="selected"],
.charts-popup-list .item[class*="active"] *:not(.icon),
.charts-popup-list .item[class*="selected"] *:not(.icon) {
	color: #54c7b0!important;
}
.charts-popup-list .item[class*="active"] svg path,
.charts-popup-list .item[class*="selected"] svg path,
.charts-popup-list .item[class*="active"] svg circle,
.charts-popup-list .item[class*="selected"] svg circle,
.charts-popup-list .item[class*="active"] svg rect,
.charts-popup-list .item[class*="selected"] svg rect {
	fill: #54c7b0!important;
}
.charts-popup-list .item[class*="active"]:hover svg path,
.charts-popup-list .item[class*="selected"]:hover svg path,
.charts-popup-list .item[class*="active"]:hover svg circle,
.charts-popup-list .item[class*="selected"]:hover svg circle,
.charts-popup-list .item[class*="active"]:hover svg rect,
.charts-popup-list .item[class*="selected"]:hover svg rect {
	fill: #d5d6e1!important;
}
.charts-popup-list .item *:not(.icon) {
	color: #606375!important;
	background: none!important;
}
.charts-popup-list .item .icon {
	filter: invert();
}
.charts-popup-list .item:hover,
.charts-popup-list .item:hover * {
	background: none!important;
	color: #d5d6e1!important;
}
.charts-popup-list .item .to-quick,
.charts-popup-list .item .delete {
	margin-top: 3px;
}
.charts-popup-list .item svg path {
	fill: #606375;
}
.charts-popup-list .item:hover svg path {
	fill: #d5d6e1;
}
.charts-popup-list .tv-text-input.inset {
	background: #1e1e2a;
	border: 1px solid transparent;
	color: #606375!important;
}
.charts-popup-list .tv-text-input.inset:focus {
	border-color: transparent;
	color: #d5d6e1!important;
}
.charts-popup-list .tv-ticker,
._tv-dialog .tv-ticker {
	filter: invert();
	cursor: pointer;
	opacity: .5;
}
.charts-popup-list .tv-ticker div:hover,
._tv-dialog .tv-ticker div:hover {
	filter: invert();
}
.charts-popup-list ._tv-button.add {
	background: none;
	border: 1px solid transparent;
	color: #606375!important;
}
.charts-popup-list ._tv-button.mins,
.charts-popup-list ._tv-button.mins:active,
.charts-popup-list ._tv-button.add:hover,
.charts-popup-list ._tv-button.add:active {
	background: #1e1e2a;
	border: 1px solid transparent;
	cursor: pointer;
	color: #606375!important;
}
.charts-popup-list ._tv-button.mins:hover,
.charts-popup-list ._tv-button.mins:active,
.charts-popup-list ._tv-button.add:hover,
.charts-popup-list ._tv-button.add:active {
	color: #d5d6e1!important;
}
.charts-popup-list .charts-popup-group .item {
	padding-left: 0px!important;
	padding-right: 0px!important;
	border-color: #1e1e2a;
}
.charts-popup-list .charts-popup-group .item:hover {
	background: #1e1e2a!important;
}
.tv-control-select__dropdown,
.tv-dropdown__body {
	background: #292938;
	border: 1px solid #1e1e2a;
	box-shadow: 0px 6px 5px 1px rgba(0, 0, 0, 0.57);
	border-radius: 5px;
}
.tv-control-select__dropdown input,
.tv-dropdown__body input {
	background: transparent;
	border: 0;
	height: auto;
	padding: 0 20px;
	color: #54c7b0;
	font-weight: bold;
}
input::-webkit-input-placeholder {
  color: #606375!important;
}
input::-moz-placeholder {
  color: #606375!important;
}
input:-moz-placeholder {
  color: #606375!important;
}
.tv-screener-fields-popup__search-wrap {
	border-color: #1e1e2a;
	height: 25px;
}
.tv-screener-fields-popup__item,
.tv-control-radio__label,
.tv-screener-popup__item {
	color: #606375;
}
.tv-screener-popup__item--active {
	background: none;
	color: #54c7b0;
}
.tv-control-checkbox__input:checked+.tv-control-checkbox__box, .tv-control-radio__input:checked+.tv-control-radio__box, .tv-device-menu-checkbox__input:checked+.tv-device-menu-checkbox__box
{
	background-color: #54c7b0!important;
}
.tv-control-checkbox__input:checked+.tv-control-checkbox__box:after, .tv-control-radio__input:checked+.tv-control-radio__box:after, .tv-device-menu-checkbox__input:checked+.tv-device-menu-checkbox__box:after {
	border-color: #54c7b0!important;
}
.tv-control-checkbox__box:before, .tv-control-radio__box:before, .tv-device-menu-checkbox__box:before {
	background-color: transparent!important;
}
.tv-control-checkbox__box:after, .tv-control-radio__box:after, .tv-device-menu-checkbox__box:after {
	border-color: #606375!important;
}
.tv-control-select__option-wrap:hover .tv-control-checkbox__box:before, 
.tv-control-select__option-wrap:hover .tv-control-radio__box:before, 
.tv-control-select__option-wrap:hover .tv-device-menu-checkbox__box:before,
.tv-dropdown__item:hover .tv-control-checkbox__box:before, 
.tv-dropdown__item:hover .tv-control-radio__box:before, 
.tv-dropdown__item:hover .tv-device-menu-checkbox__box:before {
	background-color: #d5d6e1!important;
}
.tv-control-select__option-wrap:hover .tv-control-checkbox__box:after, 
.tv-control-select__option-wrap:hover .tv-control-radio__box:after, 
.tv-control-select__option-wrap:hover .tv-device-menu-checkbox__box:after,
.tv-dropdown__item:hover .tv-control-checkbox__box:after, 
.tv-dropdown__item:hover .tv-control-radio__box:after, 
.tv-dropdown__item:hover .tv-device-menu-checkbox__box:after {
	border-color: #d5d6e1!important;
}
.feature-no-touch .tv-control-checkbox:hover .tv-control-checkbox__input:checked+.tv-control-checkbox__box, .feature-no-touch .tv-control-radio:hover .tv-control-radio__input:checked+.tv-control-radio__box, .feature-no-touch .tv-device-menu-checkbox:hover .tv-device-menu-checkbox__input:checked+.tv-device-menu-checkbox__box, 

.tv-control-select__option-wrap:hover .tv-control-checkbox .tv-control-checkbox__input:checked+.tv-control-checkbox__box, 
.tv-control-select__option-wrap:hover .tv-control-radio .tv-control-radio__input:checked+.tv-control-radio__box,
.tv-control-select__option-wrap:hover .tv-device-menu-checkbox .tv-device-menu-checkbox__input:checked+.tv-device-menu-checkbox__box,
.tv-control-select__option-wrap:hover .tv-control-checkbox .tv-control-checkbox__input:checked+.tv-control-checkbox__box,

.tv-dropdown__item:hover .tv-control-checkbox .tv-control-checkbox__input:checked+.tv-control-checkbox__box, 
.tv-dropdown__item:hover .tv-control-radio .tv-control-radio__input:checked+.tv-control-radio__box,
.tv-dropdown__item:hover .tv-device-menu-checkbox .tv-device-menu-checkbox__input:checked+.tv-device-menu-checkbox__box,
.tv-dropdown__item:hover .tv-control-checkbox .tv-control-checkbox__input:checked+.tv-control-checkbox__box {
	background-color: #d5d6e1!important;
}
.tv-control-checkbox:hover .tv-control-checkbox__input:checked+.tv-control-checkbox__box:after, 
.tv-control-radio:hover .tv-control-radio__input:checked+.tv-control-radio__box:after,
.tv-device-menu-checkbox:hover .tv-device-menu-checkbox__input:checked+.tv-device-menu-checkbox__box:after,
.tv-control-checkbox:hover .tv-control-checkbox__input:checked+.tv-control-checkbox__box:after,

.tv-control-select__option-wrap:hover .tv-control-checkbox .tv-control-checkbox__input:checked+.tv-control-checkbox__box:after, 
.tv-control-select__option-wrap:hover .tv-control-radio .tv-control-radio__input:checked+.tv-control-radio__box:after,
.tv-control-select__option-wrap:hover .tv-device-menu-checkbox .tv-device-menu-checkbox__input:checked+.tv-device-menu-checkbox__box:after,
.tv-control-select__option-wrap:hover .tv-control-checkbox .tv-control-checkbox__input:checked+.tv-control-checkbox__box:after,

.tv-dropdown__item:hover .tv-control-checkbox .tv-control-checkbox__input:checked+.tv-control-checkbox__box:after, 
.tv-dropdown__item:hover .tv-control-radio .tv-control-radio__input:checked+.tv-control-radio__box:after,
.tv-dropdown__item:hover .tv-device-menu-checkbox .tv-device-menu-checkbox__input:checked+.tv-device-menu-checkbox__box:after,
.tv-dropdown__item:hover .tv-control-checkbox .tv-control-checkbox__input:checked+.tv-control-checkbox__box:after {
	border-color: #d5d6e1!important;
}
.tv-control-select__option-wrap:hover, 
.tv-dropdown__item:hover {
	background: none!important;
	color: #d5d6e1!important;
}
.tv-screener-popup__item--presets, 
.tv-screener-popup__item--save {
	border-color: #1e1e2a;
}
.tv-screener-popup__item svg path {
	fill: #606375;
	stroke: #606375;
}
.tv-screener-popup__item--active svg path {
	fill: #54c7b0;
	stroke: #54c7b0;
}
.tv-screener-popup__item [class*="tv-screener-popup__item"]:hover svg path {
	fill: #d5d6e1;
	stroke: #d5d6e1;
}
.tv-screener-popup__item--save svg path {
	fill: none;
}
.tv-screener-popup__item--save:hover svg path {
	stroke: #d5d6e1;
}
.tv-screener-popup__item:hover .tv-control-radio__label {
	color: #d5d6e1;
}
.context-menu-wrapper,
._tv-dialog,
div.symbol-edit-popup,
.tv-dialog,
.ui-datepicker,
.clockpicker-popover,
.help-menu {
	background: #292938!important;
	border: 1px solid #1e1e2a!important;
	border-radius: 5px!important;
	box-shadow: 0px 6px 5px 1px rgba(0, 0, 0, 0.57)!important;
	color: #606375;
}
._tv-dialog,
.ui-datepicker {
	overflow: hidden;
}
div.symbol-edit-popup .filter,
._tv-dialog .filter {
	background: #1e1e2a;
	border: 0;
}
div.symbol-edit-popup .type-filter,
._tv-dialog .type-filter {
	color: #606375!important;
}
div.symbol-edit-popup .type-filter:hover,
._tv-dialog .type-filter:hover {
	color: #d5d6e1!important;
}
div.symbol-edit-popup .type-filter.active,
._tv-dialog .type-filter.active {
	color: #54c7b0!important;
}
div.symbol-edit-popup .type-filter.active:after,
._tv-dialog .type-filter.active:after {
	background: #54c7b0!important;
}
div.symbol-edit-popup .table-wrapper,
._tv-dialog .table-wrapper {
	border: none;
}
div.symbol-edit-popup tr.symbol-edit-popup,
._tv-dialog tr.symbol-edit-popup {
	border-top: 1px solid #1e1e2a;
}
div.symbol-edit-popup tr.symbol-edit-popup:hover,
div.symbol-edit-popup tr.symbol-edit-popup-active,
._tv-dialog tr.symbol-edit-popup:hover,
._tv-dialog tr.symbol-edit-popup-active{
	background: #2f2f40!important;
	cursor: pointer;
}
div.symbol-edit-popup tr.symbol-edit-popup td.symbol-edit-popup-td,
._tv-dialog tr.symbol-edit-popup td.symbol-edit-popup-td {
	border: 0!important;
	background: none!important;
	color: #606375;
}
div.symbol-edit-popup tr.symbol-edit-popup:hover td.symbol-edit-popup-td,
div.symbol-edit-popup tr.symbol-edit-popup-active td.symbol-edit-popup-td,
._tv-dialog tr.symbol-edit-popup:hover td.symbol-edit-popup-td,
._tv-dialog tr.symbol-edit-popup-active td.symbol-edit-popup-td{
	color: #d5d6e1;
}
div.symbol-edit-popup tr.symbol-edit-popup td.symbol-edit-popup-td em,
._tv-dialog tr.symbol-edit-popup td.symbol-edit-popup-td em {
	color: #54c7b0;
}
.tv-symbol-select .sbSelector {
	color: #606375!important;
}
._tv-dialog .symbol-block .spread-actions {
	background: #1e1e2a;
	border: none;
	border-bottom: 1px solid #1e1e2a;
	padding-left: 5px;
}
._tv-dialog .symbol-block .spread-actions button {
	border: none;
}
._tv-dialog .symbol-block .spread-actions button svg path {
	fill: #606375;
}
._tv-dialog .symbol-block .spread-actions button:hover svg path {
	fill: #d5d6e1;
}
._tv-dialog .results,
._tv-dialog ._tv-dialog-title,
div.symbol-edit-popup .results,
.tv-alert-dialog__title-wrap,
.tv-dialog__title {
	color: #606375;
}
._tv-dialog input:not(.colorpicker-widget) {
	background: #1e1e2a!important;
}
._tv-dialog.change-interval-dialog ._tv-dialog-content input,
._tv-dialog.change-interval-dialog .interval-caption {
	border-radius: 5px;
	border-color: #54c7b0!important;
	color: #54c7b0!important;
}
._tv-dialog.change-interval-dialog ._tv-dialog-content input.error,
._tv-dialog.change-interval-dialog .interval-caption.error {
	border-color: #d5464a!important;
	color: #d5464a!important;
}
.tv-dialog__section {
	border-color: #1e1e2a;
}
.tv-dialog__section p {
	color: #606375;
}
.tv-dialog__section p a {
	color: #54c7b0!important;
}
.tv-dialog__section p a:hover {
	color: white!important;
}
.tv-dialog__close svg path,
.date-container svg path,
.time-container svg path {
	fill: #606375;
}
.tv-dialog__close:hover svg,
.date-container:hover svg path,
.time-container:hover svg path {
	fill: #d5d6e1;
}
.tv-control-fieldset__label,
.tv-rename-chart-dialog__label {
	color: #606375;
}
.tv-control-checkbox__label {
	color: #d5d6e1;
	cursor: pointer;
}
.tv-dialog a {
	color: #54c7b0;
}
.tv-dialog input,
.tv-dialog textarea,
._tv-dialog input,
._tv-dialog textarea,
.tv-control-select__control {
	border: 1px solid transparent!important;
	background: #2f2f40;
	color: #d5d6e1!important;
	-webkit-transition: color .3s;
	transition: color .3s;
	border-radius: 5px;
}
.tv-dialog input:hover,
.tv-dialog textarea:hover,
._tv-dialog input:hover,
._tv-dialog textarea:hover,
.tv-control-select__control:hover {
	border-color: #606375!important;
	color: white!important;
}
.feature-no-touch .tv-dialog input:focus, 
.tv-dialog input:focus,
.tv-dialog textarea:focus,
._tv-dialog input:focus,
._tv-dialog textarea:focus,
.tv-control-select__control:focus {
	border-color: #54c7b0!important;
	color: #d5d6e1!important;
}
.tv-control-number-input__controls {
	border: 0;
}
.tv-control-number-input__controls .tv-control-number-input__wrapper {
	border-radius: 3px;
	width: 75%;
}
.tv-control-number-input__controls .tv-control-number-input__wrapper:hover {
	background: #606375;
}
.tv-control-number-input__splinter {
	border: 0;
	width: 75%;
}
.tv-control-number-input__controls .tv-control-number-input__wrapper:hover .tv-control-number-input__up {
	border-bottom-color: #54c7b0;
}
.tv-control-number-input__controls .tv-control-number-input__wrapper:hover .tv-control-number-input__down {
	border-top-color: #54c7b0;
}
.tv-control-number-input__controls .tv-control-number-input__wrapper:active {
	background: #d5d6e1;
}
.tv-dialog button,
._tv-dialog button,
.tv-button,
button.select {
	border-radius: 5px;
	border: 0;
	cursor: pointer;
}
.tv-button--default {
	border: 0;
	background: #606375;
	color: #d5d6e1;
}
.tv-button--default:hover {
	background: #d5d6e1!important;
	color: #606375!important;
}
.tv-button--default:active {
	background: white!important;
	color: #606375!important;
}
.tv-button--primary,
button.select {
	border: 0;
	background: #48c3cf!important;
	color: white!important;
}
.tv-button--primary:hover,
button.select:hover {
	color: white;
	background: #40aeb8!important;
}
.tv-button--primary:active,
button.select:active {
	background: #3a9da6!important;
}
.tv-button--danger {
	border: 0;
	background: #d5464a;
}
.tv-button--danger:hover {
	border: 0;
	background: #fc3b26;
}
.tv-button--danger:active {
	background: #b53c40;
}
.tv-control-select__control {
	border-radius: 5px;
	background: #2f2f40;
	border: 0;
	color: #606375;
}
.tv-control-select__option .tv-control-select__option-wrap {
	color: #606375;
}
.feature-no-touch .tv-alert-dialog__sound-toggle:hover {
	background-color: #48c3cf;
}
.feature-no-touch .tv-alert-dialog__sound-toggle:hover svg path {
	fill: white;
}
.tv-control-select__option:hover .tv-control-select__option-wrap,
.tv-control-select__option.i-active .tv-control-select__option-wrap {
	color: #d5d6e1;
	background: none!important;
}
.help-menu span,
.help-menu .external {
	color: #606375;
}
.help-menu span:hover,
.help-menu .external:hover {
	color: #d5d6e1!important;
	background: none!important;
}
.help-menu span.external:after {
	opacity: .5;
}
.help-menu span.external:hover:after {
	opacity: 1;
}
.context-menu-wrapper tr.context-menu:hover,
.context-menu-wrapper tr.context-menu-hover {
	background: #2f2f40;
	color: #d5d6e1;
}
.context-menu-wrapper td.shortcut {
	color: #606375;
}
.context-menu-wrapper td.submenu-arrow {
	-webkit-filter: brightness(.6);
	        filter: brightness(.6);
}
.context-menu-wrapper tr.context-menu:hover td.submenu-arrow,
.context-menu-wrapper tr.context-menu-hover td.submenu-arrow {
	-webkit-filter: brightness(1.5);
	        filter: brightness(1.5);
}
.context-menu-wrapper tr.action-separator td {
	background: #1e1e2a;
	border: 0;
}
.ui-datepicker-header {
	background: #1e1e2a!important;
}
.ui-datepicker-prev,
.ui-datepicker-next {
	opacity: .3!important;
}
.ui-datepicker-prev:hover,
.ui-datepicker-next:hover {
	opacity: 1!important;
}
table.ui-datepicker-calendar {
	border: 0;
}
table.ui-datepicker-calendar thead,
table.ui-datepicker-calendar thead tr,
table.ui-datepicker-calendar tbody,
table.ui-datepicker-calendar tbody tr {
	border: 0;
}
table.ui-datepicker-calendar th {
	background: #2f2f40;
	border: 0;
	color: #606375;
	font-weight: bold;
}
table.ui-datepicker-calendar tbody tr td .ui-state-default {
	background: none;
	color: #606375!important;
	border: none!important;
}
.ui-datepicker .ui-datepicker-other-month .ui-state-default {
	background: none!important;
}
table.ui-datepicker-calendar th.ui-datepicker-week-end,
table.ui-datepicker-calendar td.ui-datepicker-week-end,
table.ui-datepicker-calendar .ui-state-disabled {
	opacity: .5;
}
table.ui-datepicker-calendar  tbody tr td .ui-state-default:hover,
table.ui-datepicker-calendar  tbody tr td .ui-state-hover {
	background: #2f2f40!important;
	border-radius: 50%;
