css:
@-moz-document domain("hdrezka.ag")
{
.b-wrapper,
body
  {
    width: auto;
  }

  body > img
  {
    display: none !important;
  }

  .b-content__sidebar,
.b-post__lastepisodeout,
.b-post__social_holder_wrapper,
.b-post__rating_table,
.b-post__actions,
.b-sidetitle,
.b-sidelist,
.b-post__schedule,
.b-post__qa_list_block,
.b-post__mtitle,
.comments-form,
#hd-comments-list,
.b-post__origtitle,
.b-post__infotable,
.b-post__infotable_left,
.b-post__infotable_right,
.b-post__description,
.b-post__infolast,
.b-content__crumbs,
.b-footer,
.b-wrapper.nopadd,
.b-post__mixedtext,
.b-content__inline_sideprobar,
#vk_groups,
*[id^="heipf"],
#hd-comments-list,
#hd-comments-navigation,
.b-translators__title
  {
    display: none !important;
  }

  body[class~="has-brand"].has-brand.active-brand.pp.fixed-header.no-touch
  {
    padding-top: 0 !important;
  }

  body.active-brand.pp,
.b-content__columns,
.b-content__inline_inner_mainprobar,
#main
  {
    padding: 0 !important;
  }

  body.active-brand #wrapper
  {
    width: 100%;
    min-width: 100%;
  }

  body.has-brand #wrapper
  {
    background-color: #292121 !important;
  }

  .b-post__title h1
  {
    padding: 12px 0;
    color: rgb(222, 220, 220);
    text-align: center;
    font-size: 24px;
  }

  .b-newest_slider__title,
.b-content__main_filters_link,
.b-content__filters_types .filter-link,
.b-content__htitle,
.b-navigation a
  {
    color: rgb(222, 220, 220);
  }

  .b-newest_slider__list,
.b-newest_slider,
.b-newest_slider .cntrl
  {
    background: #2B2929;
  }

  body.active-brand .b-wrapper
  {
    padding-right: 24px;
    padding-left: 24px;
  }

  .b-simple_seasons__list
  {
    display: inline-block;
  }

  .b-post__mixedtext + div
  {
    display: none !important;
  }

  #translators-list, #simple-episodes-list-2
  {
    display: table;
    margin: 0 auto;
  }

  .b-translator__item, .b-simple_season__item
  {
    width: auto;
    font-size: 11px;
    padding: 4px;
  }

  .b-simple_seasons__list
  {
    padding: 0;
  }

  #ownplayer, #videoplayer, #cdn-player
  {
    height: 550px !important;
    width: 980px !important;
  }

.b-topnav_wrapper {
    background: rgb(225, 225, 225);    
}
.b-search__field {
   
    background: rgb(225, 225, 225);
    
}
.b-topnav__sub_inner {
   
    background: rgba(225, 225, 225);
   
}
.b-newest_slider_wrapper {
    background: #2b2b2b;
}
.b-tophead-dropdown ul {
    
    background: #E1E1E1;  
}
.b-newest_slider__title span {
    
    color: #d0d0d0;
    
}

.b-newest_slider__title span.active {
   
    color: #8a8a8a;
   
}
.b-info__title {
   color: #8a8a8a;
}

.b-content__bubble_rating .current {
    background-color: #eee;
}
.b-content__bubble {
    
    background: #eee;
    
}

.b-content__inline_item-cover {
    
    background: #e4e4e4;
    border: 1px solid #e4e4e4;
    
}

.b-content__inline_item .info {
    
    background-color: #e4e4e4;
    color: #777;
    
}
.b-search__live {
    background: #e5e5e5;
  
}

.b-topnav__sub_inner {
    
    background: #e5e5e5;
    
}


}
