css:
@-moz-document domain("youtube.com") {


/* Got some content from "youtube simple, without restyle" */

.watch-stage-mode #theater-background {background-color:#F1F1F1!important}

.watch-stage-mode .player-api.player-width.player-height {background-color:#FFF; margin-top:9px!important; padding: 15px 15px 54px 15px; box-shadow: 0 1px 2px rgba(0,0,0,.1)}

.watch-stage-mode .html5-video-player {overflow:visible!important; background-color:#181818}
.ytp-autohide:not(.ytp-autohide-active) .ytp-chrome-bottom {display:initial!important}

.watch-stage-mode #ytp-id-18 {margin-bottom:-43px!important}
.watch-stage-mode .ytp-tooltip.ytp-text-detail.ytp-preview.ytp-has-duration.ytp-bottom {top:379px!important}
.watch-stage-mode .ytp-tooltip.ytp-bottom {top:448px!important}
.watch-stage-mode .ytp-tooltip.ytp-bottom.ytp-preview {top:309px!important}

.watch-stage-mode .ytp-chrome-bottom {background-color:#FFF; left:0!important; width:854px!important; margin-bottom:-39px!important; opacity:1!important}

.watch-stage-mode .ytp-button, .watch-stage-mode .ytp-volume-panel, .watch-stage-mode .ytp-left-controls, .watch-stage-mode .ytp-progress-bar-container {filter:invert(50%)}
.watch-stage-mode .ytp-button {opacity:1!important}

.watch-stage-mode .ytp-volume-panel, .watch-stage-mode .ytp-tooltip {transition:none!important}

.watch-stage-mode .ytp-progress-list {height:6px!important; transform:initial!important}

.watch-stage-mode .ytp-chrome-bottom .ytp-play-progress.ytp-swatch-background-color {opacity:0}
.watch-stage-mode .ytp-chrome-bottom:hover .ytp-play-progress.ytp-swatch-background-color {opacity:1}

.watch-stage-mode .html5-video-player.ytp-hide-info-bar.playing-mode.iv-module-loaded.ytp-autohide .ytp-time-current
{visibility:collapse}

.watch-stage-mode .html5-video-player.ytp-hide-info-bar.playing-mode.iv-module-loaded.ytp-autohide .ytp-time-current:before
{content:"------";
margin-right:-21.5px;
visibility:visible}

.watch-stage-mode .ytp-gradient-bottom,
.branding-img, /* user image over video left bottom corner */
.ytp-cards-button, /* (i) button over video right top corner */
.ytp-cards-teaser, /* ads over video right top corner */
.iv-promo /* video suggestion pop-up */
{display:none!important}


 /* progress bar */
.ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox
{background:#167AC6!important}

}
