css:
@-moz-document regexp("https://www.dofus.com/.*/fo.*"), regexp("https://www.wakfu.com/.*/fo.*"), regexp("https://www.krosmaga.com/.*/fo.*"), regexp("http://www.dofus-touch.com/.*/fo.*"), regexp("http://www.dofus-pets.com/.*/fo.*") {
/* TOP PAGE */
/* ------------ */
.ak-container.container .row .col-sm-3 {
    float: right;
}
.ak-container.container .row .col-sm-9 {
    width: inherit;
}
a.ak-btn-big.ak-btn-play {
    display: none;
}
@media (min-width: 768px) {
    .ak-container.container .row .col-sm-3::after {
        position: absolute;
        top: 4px;
        right: 20px;
        content: "Thème Stylish pour le forum dofus";
        background: none repeat scroll 0 0 rgba(0, 0, 0, 0.6);
        padding: 8px 8px;
        border-radius: 5px;
        text-align: center;
        font-size: 18px;
        text-transform: uppercase;
        text-overflow: ellipsis;
        white-space: nowrap;
        font-family: "bebas_neueregular", sans-serif;
        color: #FFFFFF;
        text-shadow: 0 2px 2px #000;
    }
}
.ak-breadcrumb-spacer {
    min-height: 0px !important;
    margin-top: 20px;
}
nav .breadcrumb {
    position: relative;
    background-color: rgba(0, 0, 0, 0.4);
    padding: 5px 4px;
    border-radius: 5px 5px 0 0;
    margin-bottom: 0;
    border-top: 2px solid #bacc3b;
    border-left: 2px solid #85a017;
    border-right: 2px solid #809c13;
}

/* CORPS DU FORUM */
/* ------------ */
.container, .container.ak-main-container {
 max-width: 98%;    
}
.ak-backtotop {
    width: 0px;
    height: 0px;
}
/* INDEX DU FORUM */
    /* Sujets Suivis */
.ak-table-thread .ak-table tr th, .ak-thread .ak-table tr th, .ak-followed .ak-table tr th {
    background: linear-gradient(#BBCD3C, #7F9B12);
    padding: 0 15px;
}
.ak-followed .ak-panel-footer {
    background: #f2e6c0;
    text-align: center;
    padding: 12px;
    border-top: 1px dashed #533a22;
}
    /* Affichage des sections et leurs titres */
.ak-table-thread .ak-table tr th:first-child a,
.ak-table-thread .ak-table tr th {
    color: #FFFFFF;
}
.ak-table-thread .ak-table tr.ak-not-read {
    background: #F2E6C0;
}
.ak-table-thread .ak-table tr.ak-not-read td:first-child,
.ak-table-thread .ak-table tr.ak-not-read td:nth-last-child(2) {
    background-color: #FCF7DF;
}
.ak-table-thread .ak-table tr td ul li {
    color: #a58461;
}
.ak-table-thread .ak-table tr th:first-child a {
    text-transform: uppercase;
    font-size: 1.8rem;
    font-size: 18px;
}
.ak-table-thread .ak-table tr th:first-child a::before {
    content: "\e080";
    font-family: 'Glyphicons Halflings';
    font-size: 15px;
    color: #303030;
    margin-right: 5px;
}
.ak-table-thread .ak-table tr td ul li a,
.ak-table-thread .ak-table tr td .ak-title-thread a {
    color: #5C4124;
}
.ak-table-thread .ak-table tr th {
    font-size: 15px;
    font-size: 1.5rem;
}
.ak-table-thread .ak-table tr th:first-child {
    height: 40px; 
}
/* GLOBAL */
/* ------------ */
.btn-default {
    font-family: "bebas_neueregular",sans-serif;
    font-size: 23px;
    font-size: 2.3rem;
    line-height: 22px;
    line-height: 2.2rem;
    background: #b5c838;
    border-color: #3d291c;
    color: #ffffff;
    text-shadow: 0 2px 2px #000;
}
.btn-default:hover, aside .btn-default:focus {
    background-color: #bacc3b;
    color: #fcf7df;
    border: 1px solid #fcf7df;
}
/* Titres principaux des sections */
.ak-title-container h1 {
    color: #fff;
    background: linear-gradient(#BBCD3C 35%, #7F9B12);
    border-bottom-color: #6f780d;
    background-size: auto auto;
    text-shadow: 0 2px 2px #888;
}
.ak-title-container h1 {
    border-bottom: none;
    font-size: 26px;
    font-size: 2.6rem;
    padding-left: 24px;
    padding-left: 2.4rem;
    min-height: 52px;
}
.ak-title-container.ak-backlink a, .ak-title-container h1 .ak-icon-big {
    display: none;
}

    /* Icone discussion ajouté devant le titre de section */
.ak-title-container h1::before {
    content: "\e111";
    font-size: 25px;
    font-family: 'Glyphicons Halflings';
    color: #FFFFFF;
    margin-right: 10px;
}
    /* Bouton créer nouveau sujet */
.ak-title-container.ak-backlink a.ak-backlink-button {
    background-color: rgba(0, 0, 0, 0.5);
    border-radius: 5px;
    top: -2px;
    margin: 8px 0;
}

/* Résultats Trackers */
.ak-container.ak-panel.ak-topics-list-search .ak-block-topic .col-xs-10, .ui-widget .ak-modal .ak-form .ak-block-topic .col-xs-10 {
    background: #fcf7df;
}
.ak-container.ak-panel.ak-topics-list-search .ak-block-topic, .ui-widget .ak-modal .ak-form .ak-block-topic {
    background: #f2e6c0;
}

/* Choix déroulants du forum (sections, trackers etc.) */
.ak-main-content .ak-action-button .dropdown-menu, .ak-form select, .ak-form form select.form-control, .ak-table-thread .ak-table tr td .form-group select, .ak-thread .ak-table tr td .ak-form .form-group select, .ak-followed .ak-table tr td .ak-form .form-group select, aside .ak-form form select.form-control, .ak-container.ak-panel.ak-form-create-topic .form-group select.form-control, .ak-container.ak-panel.ak-form-create-topic .form-group input.form-control  {
    color: #555;
    border-color: #5C4124;
    background: #f2e6c0;
}
/* Menu de marquage lu/non lu */
table .dropdown-menu {
    background-color: #fcf7df;
    border-color: #3b271b;
}
table .dropdown-menu li a {
    color: #422f14;
}
/* Barre latérale de navigation */
aside .ak-panel .ak-panel-title {
    background: linear-gradient(#3A261B,#5A4025);
    text-shadow: 0 1px 2px rgba(0,0,0,0.1);
}
aside .ak-form {
    background-color: #fcf7df;
}
aside .ak-aside-search .ak-form input {
    height:45px;
}
aside .ak-aside-search .ak-form input[type="submit"] {
    right: 0px;
    top: 10px;
}
/* ----- Suivi de la barre avec le scroll de page ----- */
aside.col-md-3 {
    position: -webkit-sticky;
    position: sticky;
    top: 43px;
}
@media (min-width: 992px) {
    aside.col-md-3 {
        overflow-y: auto;
        height: calc(100vh - 43px);
        margin-bottom: 12px;
    }
    div.ak-aside-problems {
        margin-bottom: 0px;
    }
    aside.col-md-3::-webkit-scrollbar-track {
        -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.1);
        background: none repeat scroll 0 0 rgba(255, 255, 255, 0.2);
        border-radius: 10px;
        margin-top: 3px;
        margin-bottom: 3px;
    }
    aside.col-md-3::-webkit-scrollbar {
        width: 10px;
        background: none repeat scroll 0 0 transparent;
        margin-top: 3px;
        margin-bottom: 3px;
    }
    aside.col-md-3::-webkit-scrollbar-thumb {
        border-radius: 10px;
        background: none repeat scroll 0 0 rgba(0, 0, 0, 0.6);
        margin-top: 3px;
        margin-bottom: 3px;
    }
}
/* Catégories du forum dans la barre latérale */
.ak-tree ul li.level-1 a {
    color: #4a3320;
    font-size: 14px;
}
.ak-tree ul li.level-1 {
    background: #EEDEAB;
}
.ak-tree ul li.level-1:hover {
    background: #fcf7df;
}
.ak-tree.ak-tree-forum li.level-2 {
    background: #F2E6C0;
}
.ak-tree.ak-tree-forum li.level-2:hover {
    background: #f9f3d6;
}
.ak-tree.ak-tree-forum li.level-2 li.level-3 {
    background: #f3ead0;
    padding: 0;
}
.ak-tree.ak-tree-forum li.level-2 li.level-3 li.level-4 {
    background: #fbf7ea;
    text-indent: 25px;
}
.ak-tree.ak-tree-forum li.level-2 li.level-3 li.level-4:hover {
    background: #d4cfc4;    
}
.ak-tree.ak-tree-forum li.level-4:hover {
    background: #cccccc;
}
.ak-tree li.level-1.ak-closed > a::after, .ak-tree li.level-1.ak-haschild.ak-closed > a::after {
    content: "\e114";
    right: 15px;
}
.ak-tree li.level-1.ak-haschild.ak-open > a::after {
    content: "\e113";
    right: 15px;
}
.ak-tree li.level-1.ak-open > a::after {
    content: "\e165";
    right: 15px;
}
.ak-tree li.level-2.ak-closed > a::after, .ak-tree li.level-3.ak-closed > a::after {
    content: "\e165";
    right: 15px;
}
.ak-tree li.level-4.ak-closed > a::after {
    content: "\e165";
    font-style: normal;
    right: 10px;
}
 .ak-tree.ak-tree-forum li.level-4 a {
        color: #4a3320;
        font-style: normal;
        margin-left: 10px;
}
.ak-tree li.level-2.ak-closed.ak-haschild > a::after, .ak-tree li.level-3.ak-closed.ak-haschild > a::after {
    content: "\e114";
    right: 15px;
}
.ak-tree li.level-2.ak-open > a::after, .ak-tree li.level-3.ak-open > a::after {
    content: "\e113";
    right: 15px;
} 
.ak-tree li.level-1.ak-closed > a::after, .ak-tree li.level-1.ak-haschild.ak-closed > a::after, .ak-tree li.level-1.ak-haschild.ak-open > a::after, .ak-tree li.level-1.ak-open > a::after
{
    color: #8aa41a;
}
.ak-tree li.level-2.ak-closed > a::after, .ak-tree li.level-3.ak-closed > a::after, .ak-tree li.level-4.ak-closed > a::after, .ak-tree li.level-2.ak-closed.ak-haschild > a::after, .ak-tree li.level-3.ak-closed.ak-haschild > a::after, .ak-tree li.level-2.ak-open > a::after, .ak-tree li.level-3.ak-open > a::after
{
    color: #d0bf8b;
}
.ak-tree li.level-1.ak-closed > a::after, .ak-tree li.level-1.ak-haschild.ak-closed > a::after,.ak-tree li.level-1.ak-haschild.ak-open > a::after, .ak-tree li.level-1.ak-open > a::after,.ak-tree li.level-2.ak-closed > a::after, .ak-tree li.level-3.ak-closed > a::after, .ak-tree li.level-4.ak-closed > a::after, .ak-tree li.level-2.ak-closed.ak-haschild > a::after, .ak-tree li.level-3.ak-closed.ak-haschild > a::after, .ak-tree li.level-2.ak-open > a::after, .ak-tree li.level-3.ak-open > a::after
{
    font-family: "Glyphicons Halflings";
    position: absolute;
    top: 5px;
}
.ak-tree ul .ak-haschild .ak-picto-close {
    display: none;
}

/* TOP ANNONCE FORUM */
.ak-announce {
    background: #f2e6c0;
    color: #5c4124;
}
.ak-announce .ak-panel-title {
    color: #572f14;
    font-weight: bold;
}
.ak-announce .ak-announce-toggle {
    color: #809c00;
    font-weight: bold;
}
.ak-panel {
    background: #fcf7df;
    color: #5C4124;
}
/* CONTENU SECTION (liste sujets) */
/* ------------ */
.ak-thread .ak-table tr th:first-child, .ak-followed .ak-table tr th:first-child {
    height: 40px;
}
.ak-thread .ak-table tr.ak-not-read, .ak-followed .ak-table tr.ak-not-read {
    background: #FCF7DF;
}
.ak-thread .ak-table tr.ak-not-read:hover, .ak-followed .ak-table tr.ak-not-read:hover {
    background: #F2E6C0;
}
.ak-thread .ak-table tr:first-child, .ak-thread .ak-table tr:first-child:hover {
    background: #FCF7DF;
}

.ak-thread .ak-table tr.ak-read td .ak-topictitle-container .ak-text-topic .ak-content-text-topic .ak-title-topic, .ak-followed .ak-table tr.ak-read td .ak-topictitle-container .ak-text-topic .ak-content-text-topic .ak-title-topic {
    background: none;
    font-size: 15px;
    font-size: 1.5rem;
    line-height: 15px;
    line-height: 1.5rem;
    color: #5C4124;
    font-weight: normal;
    max-width: 441px;
}
.ak-thread .ak-table tr.ak-not-read td .ak-topictitle-container .ak-text-topic .ak-content-text-topic .ak-title-topic, .ak-followed .ak-table tr.ak-not-read td .ak-topictitle-container .ak-text-topic .ak-content-text-topic .ak-title-topic {
    background: none;
    font-size: 15px;
    font-size: 1.5rem;
    line-height: 15px;
    line-height: 1.5rem;
    color: #5C4124;
    font-weight: bold;
    max-width: 441px;
}
.ak-thread .ak-table tr td:nth-child(4) a, .ak-followed .ak-table tr td:nth-child(4) a {
    color: #8e614d;
}
.ak-thread .ak-table tr td .ak-dropdown-actions, .ak-followed .ak-table tr td .ak-dropdown-actions {
    padding: 0 24px;
    font-size: 0;
    background-color: #f2e6c0;
    border-color: #5c4124;
}
.ak-thread .ak-table tr td .ak-dropdown-actions:hover, .ak-thread .ak-table tr td .ak-dropdown-actions:focus, .ak-followed .ak-table tr td .ak-dropdown-actions:hover, .ak-followed .ak-table tr td .ak-dropdown-actions:focus {
    box-shadow: none;
    background-color: #f2e6c0;
    border-color: #9BA712;
}
/* Icones de sujets (fermé, épinglé) */
    /* Sujet fermé */
.ak-thread .ak-table tr td .ak-topictitle-container .ak-text-topic .ak-list-pictos .ak-picto.ak-lock::before, .ak-followed .ak-table tr td .ak-topictitle-container .ak-text-topic .ak-list-pictos .ak-picto.ak-lock::before {
    content: "\e033";
    font-family: "Glyphicons Halflings";
    color: #de6b6b;
}
.ak-tree ul .ak-haschild .ak-picto-close {
    display: none;
}
.ak-thread .ak-table tr td .ak-topictitle-container .ak-text-topic .ak-list-pictos .ak-picto.ak-lock, .ak-followed .ak-table tr td .ak-topictitle-container .ak-text-topic .ak-list-pictos .ak-picto.ak-lock { 
    background: transparent;    
}
    /* Sujet épinglé */
.ak-thread .ak-table tr td .ak-topictitle-container .ak-text-topic .ak-list-pictos .ak-picto.ak-pin::before, .ak-followed .ak-table tr td .ak-topictitle-container .ak-text-topic .ak-list-pictos .ak-picto.ak-pin::before {
    content: "\e146";
    font-family: "Glyphicons Halflings";
    color: #6b73de;
    font-weight: bold;
}
.ak-thread .ak-table tr td .ak-topictitle-container .ak-text-topic .ak-list-pictos .ak-picto.ak-pin, .ak-followed .ak-table tr td .ak-topictitle-container .ak-text-topic .ak-list-pictos .ak-picto.ak-pin { 
    background: transparent;    
}

/* CREATION SUJET */
/* ------------ */
.ak-container.ak-panel.ak-form-create-topic {
    background: #fcf7df;
}

/* LECTURE SUJET */
/* ------------ */
.ak-title-topic.ak-title-container .ak-h1 {
    position: relative;
}
.ak-main-container .ak-admin-text {
    background: #FCF7DF;
    color: #5C4124;
}
.ak-title-container .ak-h1 .ak-block-score {
    color: #f2e6c0;
    font-weight: bold;
    font-size: 14px;
    padding: 12px 0;
    position: absolute;
    right: 50px;
    z-index: 99;
    top: 10px;
    max-width: 80px;
}
    /* Titre nombre réponses*/
.ak-posts-list.ak-nocontentpadding .ak-panel-title, .ak-panel-form-post .ak-panel-title {
    background: linear-gradient(#BBCD3C, #7F9B12);
    color: #422f14;
}
    /* Nombre de réponses*/
.ak-nb-answers {
    background: #F2E6C0;
    color: #422f14;
}
    /* Tri des réponses */
.ak-panel .ak-panel-header-list-post {
    background: rgba(181, 200, 56, 0.59);
}
    /* Choix de page d'un sujet */
.ak-pagination {
    background: #f2e6c0;
}
	/*Citations*/
  .ak-posts-list.ak-nocontentpadding .ak-post blockquote, blockquote {
    background-color: rgba(0,0,0,0.05);
    border-left: 5px solid rgb(80,80,80);
    font-size: .95em;
    margin: 1em 0 .2em 0;
    color: #51492c;
  }
 
  .ak-posts-list.ak-nocontentpadding .ak-post blockquote cite, blockquote cite {
    font-weight: bold;
    margin-bottom: 1em;
    font-size: inherit;
    color: #000;
  }
  .ak-posts-list.ak-nocontentpadding .ak-post blockquote cite + div, blockquote cite + div {
    padding: 5px 0;
    font-size: .9em;
  }

/* REPONSES SUJET */
    /* Par un joueur*/
.ak-posts-list.ak-nocontentpadding .ak-post .col:first-child {
    background: #f2e6c0;
    text-align: center;
}
.ak-posts-list.ak-nocontentpadding .ak-post .col:first-child .ak-block-score {
    text-align: center;
    font-weight: bold;
    background: rgba(191, 203, 22, 0.53);
    border-radius: 5px;
    max-width: 100%;
    padding: 2px 0;
}
.ak-posts-list.ak-nocontentpadding .ak-post .ak-subpost .ak-post .col:first-child .ak-block-score {
    text-align: center;
    font-weight: normal;
    background: rgba(191, 203, 22, 0.53);
    border-radius: 5px;
    max-width: 100%;
    padding: 1px 0;
}
.ak-posts-list.ak-nocontentpadding .ak-post .col:first-child.ak-administrator-block .ak-block-score,.ak-posts-list.ak-nocontentpadding .ak-post .ak-subpost .ak-post .col:first-child.grpadmin-block .ak-block-score,.ak-posts-list.ak-nocontentpadding .ak-post .col:first-child.grpmodo-block .ak-block-score,.ak-posts-list.ak-nocontentpadding .ak-post .ak-subpost .ak-post .col:first-child.grpmodo-block .ak-block-score {
    display: none;
}
.ak-posts-list.ak-nocontentpadding .ak-post .col:last-child {
    background: #fcf7df;
}
    /* Par un membre du staff ANKAMA */
.ak-posts-list.ak-nocontentpadding .ak-post .ak-subpost .ak-post .col:first-child.grpadmin-block,
.ak-posts-list.ak-nocontentpadding .ak-post .col:first-child.ak-administrator-block {
    background: #c7d359;
}
.ak-posts-list.ak-nocontentpadding .ak-post .col-sm-10.ak-admin-text .grpadmin-block, .ak-posts-list.ak-nocontentpadding .ak-post .ak-subpost .ak-post .col:last-child.ak-admin-text .grpadmin-block {
    background-color: rgba(255, 232, 144, 0.35)
}
    /* Par un modérateur/maître du jeu */
.ak-posts-list.ak-nocontentpadding .ak-post .ak-subpost .ak-post .col:first-child.grpmodo-block,
.ak-posts-list.ak-nocontentpadding .ak-post .col:first-child.grpmodo-block {
    background-color: rgba(255, 220, 87, 0.66);
}
.ak-posts-list.ak-nocontentpadding .ak-post .col-sm-10.ak-admin-text .grpmodo-block, .ak-posts-list.ak-nocontentpadding .ak-post .ak-subpost .ak-post .col:last-child.ak-admin-text .grpmodo-block{
    background-color: rgba(255, 232, 144, 0.35)
}
.ak-avatar.grpmodo {
    border: 4px solid #eac01b;;
    border-radius: 2px;
}
/* Afficher réponses aux réponses */
.ak-posts-list.ak-nocontentpadding .ak-post .ak-subpost .ak-see-more-comments a {
    border-radius: 10px 0 0 10px;
}
.ak-posts-list.ak-nocontentpadding .ak-post .ak-subpost .ak-see-more-comments a:hover {
    color: #b3c100;
    background-color: #F2E6C0;
}
.ak-posts-list.ak-nocontentpadding .ak-post .ak-subpost .ak-see-more-comments a span {
    padding: 13px 0px 13px 0px;
    font-weight: bold;
}
.ak-posts-list.ak-nocontentpadding .ak-post .ak-subpost .ak-see-more-comments > a > span::before {
    padding: 13px 0px 13px 5px;
    display: inline-block;
    content: "\e080";
    font-family: 'Glyphicons Halflings';
    font-size: 12px;
    color: #aab700;
    margin-right: 3px;
}

.ak-posts-list.ak-nocontentpadding .ak-post .ak-subpost .ak-see-more-comments > a:hover > span::before {
    content: "\e114";
    font-family: 'Glyphicons Halflings';
}

    /* REPONSES AUX REPONSES */
.ak-posts-list.ak-nocontentpadding .ak-post .ak-subpost .ak-post .col:first-child {
    background: #f2e6c0;
    padding: 5px 4px;
}
.ak-posts-list.ak-nocontentpadding .ak-post .ak-subpost .ak-post .col:last-child {
    background: #fcf7df;
}
.ak-posts-list.ak-nocontentpadding .ak-post .ak-subpost {
    border: none;
    margin-left: 6px;
    padding: 0;
}
.ak-posts-list.ak-nocontentpadding .ak-post .ak-subpost .ak-post {  
    border-left: 3px solid #5d4a37;
    border-bottom: 2px Solid #f2e6c0;
    border-top: 2px Solid #f2e6c0;
}
}
