css:
@-moz-document domain("todoist.com") {
div#top_bar_inner[style] {
    margin-left: 0 !important
}
div#all_holder[style] {
    margin-left: 0 !important
}
div#content[style] {
    width: calc(100vw - 350px) !important
}
div#editor[style] {
    width: calc(100vw - 375px) !important
}
.arrow {
    margin-left: -15px !important
}
#top_filters .filter,
#left_menu_tabs .control,
.generic_left_list .name,
.left_list_man .action,
.task_item .task_content_item {
    font-family: "/*[[font]]*/" !important
}
#left_menu {
    border-right: /*[[color]]*/ 1px solid !important
}
}
