css:
@-moz-document domain("geeksforgeeks.org") {
.site {
  background-color: #222;
}

body {
  color: #bbb;
}

a {
  color: #019a01; /* #060; from /color-palette/1339 */
}

hr {
  background-color: #666;
}

/*************** Main Page ***************/

/***** Top Header *****/
.site-header {
  border-left-color: #333;
  border-right-color: #333;
  border-top-color: #333;
}

.header--navbar {
  background-color: #282828;
}

/*** Search ***/
.cse .gsc-control-cse, .gsc-control-cse {
  border-color: #222;
}

table.gsc-search-box {
  background-color: #222;
}

#gsc-iw-id2 {
  background-color: #222;
  border-color: #222;
}

input#gsc-i-id2 {
  background-color: #222 !important;
}

/** 'Courses', 'Hire with us' **/
.ButtonContribute {
  color: #ddd;
}

/*** Search Results overlay ***/

.gsc-results-wrapper-overlay {
  background-color: #222;
}

.gsc-modal-background-image {
  background-color: #222;
}

.gsc-webResult.gsc-result, .gsc-results .gsc-imageResult {
  background-color: #222;
  border-color: #222;
}

.gsc-webResult.gsc-result:hover, .gsc-imageResult:hover {
  background-color: #282828;
  border-color: #282828;
}

.gsc-webResult.gsc-result {
  border-color: #222;
}

/*** 'Login' button ***/
.ButtonLogin {
  background-color: #282828;
}

/*** User Profile ***/

.dropdown-content {
  background-color: #222;
}

.dropdown-content a {
  color: #aaa;
}

.dropdown-content a:hover {
  background-color: #282828;
}

/*** User Profile dropdown ***/

.header--dropdown__menu {
  background-color: #282828;
  border-color: #333;
}

.header--dropdown__item .header--username {
  color: #aaa;
}

.header--dropdown__item > a {
  color: #aaa;
}

/***** Login Pop-up Pane *****/

.login-modal-div .modal-content {
  background-color: #333;
  border-color: #333;
}

.login-modal-div .white-bg {
  background-color: #282828;
}

.login-modal-div .modal-header input:checked + label.tab-label {
  background-color: #282828;
  border-color: #666;
  border-bottom-color: #282828;
}

.login-modal-div .modal-header label.tab-label {
  background-color: #333;
}

.login-modal-div .modal-header section {
  border-color: #666;
}

.login-modal-div .social-signin-div {
  border-color: #666;
}

.login-modal-div input[type="text"], .login-modal-div input[type="email"], .login-modal-div input[type="password"] {
  border-color: #666;
  color: #aaa;
  background-color: #333;
}

/* 'or' below Sign-in button */
.login-modal-div .social-signin-div .social-divider span {
  background-color: #282828;
}

.login-modal-div .social-signin-div .social-divider {
  border-bottom-color: #666;
}


/***** Middle Header *****/
.themonic-nav ul.nav-menu, .themonic-nav div.nav-menu > ul {
  background-color: #282828;
  border-top-color: #333;
}

.themonic-nav li a {
  color: #ccc;
}

/** Dropdown **/

.themonic-nav li ul {
  border-color: #333;
  background-color: #282828;
}

.themonic-nav li ul li a {
  background-color: #282828;
  color: #bbb;
}

.menu-item-has-children ul li a {
  color: #bbb;
}

/***** Body *****/

.wrapper {
  background-color: #222;
  border-color: #333;
}

/**** Left Panel ****/

.leftBarList li {
  background-color: #282828;
}

.leftBarList a {
  color: #aaa;
}

.leftBarList a:visited {
  color: #647e97; /* modified #506478; */
}

.leftBarList a:visited:hover {
  color: white;
}

/**** Middle Panel ****/

.site-content article.sticky {
  background-color: #282828;
  border-color: #333;
  box-shadow: .01em .1em .1em .1em #282828;
}

.entry-header .entry-title a {
  color: #019a01; /* #060; from /color-palette/1339 */
}

/* Person icon (optional, top-right corner) e.g. /find-range-value-of-the-expression/ */
.tooltip > i {
  color: #ddd;
}

/* Tags */
.practiceButton {
  background-color: #085731; /* #0f9d58; */
}

.entry-meta .practiceButton a {
  color: #ccc !important;
}

/*** Input/Output block ***/

.entry-content blockquote, .comment-content blockquote {
  background-color: #303030;
  border-left-color: #666;
  border-right-color: #666;
}

/*** Images ***/

/** e.g. /eulerian-number/ **/

img {
  opacity: 0.6;
}

img[src*="CodeCogsEqn"] {
  filter: invert(1.0);
  box-shadow: none;
  opacity: 1.0;
}

img.quicklatex-auto-format {
  filter: invert(0.8);
  box-shadow: none;
  opacity: 1.0;
}

/*** Recommended Posts ***/

.recommendedPostsDiv li a {
  color: #019a01; /* #060; from /color-palette/1339 */
}

/*** Author ***/
.author_details a {
  color: #aaa !important;
}

.author_bio {
  color: #aaa;
}

/*** Upvote button ***/
.upvoteText {
  background-color: #282828;
}

/*** Scroll-up hovering button ***/
#scrollTopBtn {
  background-color: #333;
  color: #666;
}

button, input, textarea {
  border-color: #666;
}

/**** Right Panel ****/

/*** Widget ***/
.widget-area .widget li, .widget-area .widget .textwidget {
  border-bottom-color: #666;
}

/** Header e.g. /find-range-value-of-the-expression/ **/
.rightbar_head {
  color: #aaa;
}

/*************** Individual Page e.g. /interview-preparation-for-software-developer/ ***************/

/**** Middle Panel ****/

.site-content article {
  background-color: #282828;
}

/** Ads **/
/* ins.adsbygoogle {
  width: 0px;
  height: 0px;
}

div[id*="google_ads_iframe"] {
  visibility: hidden;
} */

/*** Code Snippet block - C++, Java, Python3, C#, PHP ***/

.responsive-tabs .responsive-tabs__panel {
  background-color: #222;
  border-color: #333;
}

/* Language Tab */
.responsive-tabs .responsive-tabs__list__item {
  color: #019a01; /* #060; from /color-palette/1339 */
}

.responsive-tabs .responsive-tabs__list__item--active, .responsive-tabs .responsive-tabs__list__item--active:hover {
  background-color: #222;
  border-color: #333;
  color: #EC4E20; /* restoring original */
}

/* Icons */
.code-sidebar-button {
  background-color: #333;
  color: #999;
  box-shadow: 1px 1px 2px 2px #333;
}

.ring-load {
  border-color: #666;
}

/*** Code e.g. /sum-of-bit-differences-among-all-pairs/ ***/
pre {
  background-color: #222; /* #272822; */
  color: #bbb;
}

/*************** Individual Page e.g. /google-interview-preparation/ ***************/

#content {
  background-color: #282828;
}

/* Heading */
.headingCompany {
  color: #aaa;
}
}

@-moz-document domain("practice.geeksforgeeks.org") {
body {
  background-color: #222;
}

/*************** Main Page ***************/

/***** Top Header *****/
.header--navbar {
  background-color: #282828;
}

/* Titles */
.header--nav__link {
  color: #aaa;
}

.header--nav__link:hover {
  color: #ccc;
}

/* Bell icon */
.header--nav__link > i {
  color: #aaa;
}

.header--nav__link > i:hover {
  color: #ccc;
}

/***** Middle Header *****/

/* Dots */

.slick-dots li button::before {
  color: #aaa;
}

.slick-dots li.slick-active button::before {
  color: #ccc;
}

/***** Body *****/

/* 'Data Structures' */
h2 {
  color: #aaa !important;
}

/** Card **/
.card {
  background-color: #282828;
}

.card-title {
  background-color: #333;
}

.card .card-text {
  color: #aaa;
}

.card-image img {
  opacity: 0.8;
}

/* 'More Data Structures' */
.showMoreDiv .card {
  background-color: #34854b; /* rgba(76,185,107,0.8); */
}

.big-circle i {
  color: #ccc;
}

/*** Footer ***/

#footer .footer-column li {
  color: #aaa;
}

#footer li a, #footer li {
  color: #aaa;
}

/*************** Explore - /explore/ ***************/

/**** Left Panel ****/

/* 'Solved', 'Unsolved' toggle buttons */

.status {
  background-color: #333;
}

.btn.btn-secondary.status[style*="border-right:1px solid #d7d7d7;"] {
  border-right-color: #222 !important;
}

.status.active {
  background: #1f4634; /* #459f75; */
  color: #ccc;
}

/* 'Problem Type' */

#panel-group-1 {
  background-color: #282828 !important;
  box-shadow: 2px 2px 15px 0 #aaa3 !important;
}

.panel {
  background-color: #282828;
}

div[style*="background-color:#d7d7d7;"] {
  background-color: #666 !important;
}

/** 'Company Tags' **/
#accordion {
  background-color: #282828 !important;
}

/* 'more>>' Pop-up Panel */
.modal-content {
  background-color: #282828;
}

input#searchCompanies {
  border-color: #333 !important;
  background-color: #222 !important;
}

hr {
  border-top-color: #666;
}

.close {
  color: #ccc;
}


/**** Middle Panel ****/

/* Selected Tags */

.btn-default {
  border-color: #333;
}

.filterInnerDiv {
  background-color: #282828;
  color: #aaa;
}

.filterInnerDiv:hover {
  background-color: #333;
  color: #ccc;
}

/* Cards */

.progress {
  background-color: #333 !important;
}

.label{
  color: #ccc;
}

.panel-content > .label {
  background-color: #1b512d !important; /* #349d58 */
}

/*************** Problem Page e.g. /problems/c-data-types/0 ***************/

/**** Left Panel ****/

.whiteBgColor {
  background-color: #282828 !important;
}

.tree {
  border-bottom-color: #333;
}

.treeElements > li > a {
  color: #aaa;
}

/* 'show more' */
a[style*="color:#4cb96b;text-shadow: 1px 1px #ccc;"] {
  color: #34854b !important; /* #4cb96b */
  text-shadow: none !important;
}

/**** Middle Panel ****/

/** Card **/

.well {
  border-color: #333;
  box-shadow: 0px 0px 5px 0px #333 !important;
}

/* Tags */
.btn-info {
  background-color: #0e3945; /* #5bc0de; */
  border-color: #0e3945; /* #46b8da; */
  color: #bbb;
}

.btn-info:hover {
  background-color: #1b6d85; /* #31b0d5; */
  color: #ddd;
}

.btn-warning {
  color: #ccc;
}

/* 'Problems' header */
.nav-tabs > li.active > a, .nav-tabs > li.active > a:focus, .nav-tabs > li.active > a:hover {
  background-color: #282828;
  border-color: #333;
}

.nav-problem > li.active {
  box-shadow: 0px 0px 5px 0px #333;
}

/* 'My Submissions', 'All Submissions' buttons */
.editor-action {
  color: #aaa;
  background-color: #282828;
  border-color: #666;
}

/*** Code Editor - without Login ***/

div[style*="media.geeksforgeeks.org/img-practice/editor_image.png"] {
  filter: invert(0.9);
}

div[style*="media.geeksforgeeks.org/img-practice/editor_image.png"] > .btn-primary {
  filter: invert(1.0);
}

/*** Code Editor ***/

#border {
  box-shadow: 0px 0px 5px 0px #333;
}

/* Theme dropdown */
.form-control {
  background-color: #282828;
  border-color: #333;
  color: #aaa;
}

/* Editor */
pre {
  border-color: #333;
}

#border[style*="background: #fff;"] {
  background-color: #282828 !important;
}

/* 'Enter Custom Input' pop-up Pane */
textarea#expectedArea {
  background-color: #222;
}

.modal-header {
  border-bottom-color: #666;
}

.modal-footer {
  border-top-color: #666;
}

.btn-default {
  background-color: #333;
  color: #ccc;
}

.btn-warning {
  background-color: #995e0d; /* #f0ad4e; */
  border-color: #81500b; /* #eea236; */
}

/** Output **/

.out pre {
  background-color: #282828;
}

.out pre > span[style="color:green"] {
  color: #00b300 !important;
}

/**** Right Panel ****/

.nav-tabs {
  border-bottom-color: #333;
}

.nav > li > a {
  color: #aaa;
}

.nav-tabs > li > a:hover {
  color: #aaa;
}

li.active > a.whiteBorder {
  border-color: #333 !important;
}

.nav > li > a:focus, .nav > li > a:hover {
  background-color: #333;
  border-color: #666;
}

table.table.whiteBgColor > tbody > tr {
  border-color: #335127 !important; /* #6AA84F; */
}

tr:hover {
  color: #ddd;
}

#userRow {
  background-color:#003600 !important; /* #b3ffb3; */
}

#userRow > td {
  border-color: #497437 !important; /* #6AA84F */
}

/* LeaderBoard */
.btn-success {
  background-color:#333;
  border-color: #666;
}

.whiteLink {
  color: #ccc !important;
}

/* 'Your Rank In Institute' */

.panel {
  border-color: #333 !important;
}

.table > tbody > tr > td, .table > tbody > tr > th, .table > tfoot > tr > td, .table > tfoot > tr > th, .table > thead > tr > td, .table > thead > tr > th {
  border-top-color: #333;
}

.table > thead > tr > th {
  border-bottom-color: #333;
}

table.well.table.whiteBgColor > thead > tr {
  background: linear-gradient(to right, #1e4e2c, #0e2d21) !important; /* linear-gradient(to right, #4cb96b, #32a176); */
}

th {
  color: #bbb;
  background-color: #1a481f; /* #3BA647; */
}

table.well.table.whiteBgColor > tbody > tr {
  background-color:#282828 !important;
}

/*************** Solution Page e.g. /viewSol.php?subId=<id>&pid=<id>&user=<userId> ***************/

/**** Editor ****/

.ace-tm {
  background-color: #282828;
  color: #bbb;
}

/* Line No.s */
.ace-tm .ace_gutter {
  background-color: #333;
  color: #aaa;
}

/* Active line */
.ace-tm .ace_marker-layer .ace_active-line {
  background-color: #333;
}

.ace-tm .ace_gutter-active-line {
  background-color: #444;
}

/* Cursor */
.ace-tm .ace_cursor {
  color: #aaa;
}

/* Folded block */
.ace_line .ace_fold {
  opacity: 0.5;
  border-color: #666;
}

/*** Syntax highlighting ***/

.ace-tm .ace_storage, .ace-tm .ace_keyword {
  color: #1f74ec; /* blue */
}

/**** 'Related Problems' ****/

fieldset {
  background-color: #282828;
  border-color: #333 !important;
}

legend {
  background-color: #282828;
  border-color: #265626; /* #5CB85C */
  color: #999;
}
}

@-moz-document domain("auth.geeksforgeeks.org") {
