@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(mail.google.com) {


table[class*="PwUwPb XoqCub MMcQxe"]{
 width : 99% !important;
}

/* back to inbox link */
.vwcZUe {
display:none !important;
}

/* next / prev  */
/*
.z8N7nd {
 position:absolute !important;
 right:0px !important;
 top:20px !important;
 font-size:10px !important;
}
*/

/* new window / print all / expand all / forward all */
.yMuNaf {
 position:absolute !important;
 right:0px !important;
 top:25px !important;
}

/* height of top button thing */
.jsbnre {
 padding-bottom:28px !important;
}

/* new window and print all buttons */
.OZly4d, .m2U49e {
 float:left !important;
 margin-right:10px !important;
}


/* ads */
.yxEQwb, .iFOJMb {
 display:none !important;
}

td.eWTfhb > div.XoqCub {
 width:0px !important;
 overflow:visible !important;
}

td.eWTfhb > div.ice3Ad, div.f3hr3b > table.PwUwPb > tr > td.eWTfhb > div.XoqCub {
 width:100% !important;
}

[class*="gV2Gwc"]{
