css:
@-moz-document regexp("http://www.dofus.com/.*/fo.*") {
/* Global */

.container.ak-main-container {
    max-width: 98%;
}
.ak-announce {
    background: #f2e6c0;
    color: #5c4124;
}
.ak-announce .ak-panel-title {
    color: #572f14;
    font-weight: bold;
}
.ak-announce .ak-announce-toggle {
    color: #809c00;
    font-weight: bold;
}
aside .ak-panel .ak-panel-title {
    background: linear-gradient(#3A261B, #5A4025);
    text-shadow: 0 1px 2px rgba(0, 0, 0, 0.1);
}
aside .ak-form {
    background-color: #f2e6c0;
}
.ak-panel {
    background: #fcf7df;
    color: #5C4124;
}
/* Index du forum */

.ak-title-container h1 {
    color: #fff;
    background: linear-gradient(#BBCD3C, #7F9B12);
    border-bottom-color: #6f780d;
    background-size: auto auto;
    text-shadow: 0 2px 2px #888;
}
.ak-title-container h1 {
    border-bottom: none;
    font-size: 26px;
    font-size: 2.6rem;
    padding-left: 84px;
    padding-left: 8.4rem;
    min-height: 52px;
}
.ak-title-container h1 .ak-icon-big {
    display: block;
    left: 5px;
    position: absolute;
    text-align: center;
    top: 0;
    background-image: url('http://staticns.ankama.com/dofus/www/game/items/200/18112.png');
    background-size: 45px auto;
    width: 45px;
    height: 45px;
    background-position: left top;
}
.ak-table-thread .ak-table tr th,
.ak-thread .ak-table tr th,
.ak-followed .ak-table tr th {
    background: linear-gradient(#BBCD3C, #7F9B12);
    padding: 0 15px;
}
.ak-table-thread .ak-table tr th:first-child a,
.ak-table-thread .ak-table tr th {
    color: #FFFFFF;
}
.ak-table-thread .ak-table tr.ak-not-read {
    background: #F2E6C0;
}
.ak-table-thread .ak-table tr.ak-not-read td:first-child,
.ak-table-thread .ak-table tr.ak-not-read td:nth-last-child(2) {
    background-color: #FCF7DF;
}
.ak-table-thread .ak-table tr td ul li {
    color: #a58461;
}
.ak-table-thread .ak-table tr th:first-child a {
    text-transform: uppercase;
    font-size: 1.8rem;
    font-size: 18px;
}
.ak-table-thread .ak-table tr th:first-child a::before {
    content: "\e080";
    font-family: 'Glyphicons Halflings';
    font-size: 15px;
    color: #303030;
    margin-right: 5px;
}
.ak-table-thread .ak-table tr td ul li a,
.ak-table-thread .ak-table tr td .ak-title-thread a {
    color: #5C4124;
}
.ak-table-thread .ak-table tr th {
    font-size: 15px;
    font-size: 1.5rem;
}
.ak-table-thread .ak-table tr th:first-child {
    height: 40px;
}
aside .ak-form form select.form-control {
    color: #555;
    border-color: #5C4124;
    background: #fcf7df;
}
aside .btn-default {
    font-family: "bebas_neueregular", sans-serif;
    font-size: 23px;
    font-size: 2.3rem;
    line-height: 22px;
    line-height: 2.2rem;
    background: #b5c838;
    border-color: #3d291c;
    color: #ffffff;
    text-shadow: 0 2px 2px #000;
}
.ak-tree ul li.level-1 a {
    color: #4a3320;
    font-size: 14px;
}
.ak-tree ul li.level-1 {
    background: #f2e6c0;
}
.ak-tree ul li.level-1:hover {
    background: #fcf7df;
}
.ak-tree.ak-tree-forum li.level-2 {
    background: #fcf7df;
}
.ak-tree.ak-tree-forum li.level-2:hover {
    background: #f9f3d6;
}
.ak-tree ul .ak-haschild a::after {
    content: "\e165";
    font-family: "Glyphicons Halflings";
    /* float: right; */
    
    position: absolute;
    right: 5px;
    top: 5px;
    color: #8aa41a;
}
.ak-tree ul .ak-haschild .ak-picto-close {
    display: none;
}
.ak-table-thread .ak-table tr td .form-group select,
.ak-thread .ak-table tr td .ak-form .form-group select,
.ak-followed .ak-table tr td .ak-form .form-group select {
    font-size: 13px;
    font-size: 1.3rem;
    width: auto;
    display: inline-block;
    background: #f2e6c0;
    color: #555;
    border-color: #5C4124;
    margin: 6px 0;
    margin: .6rem 0;
}
/* Liste des sujets d'une section */

.ak-title-container.ak-backlink a.ak-backlink-button {
    top: -2px;
    margin: 8px 0;
}
.ak-thread .ak-table tr th:first-child,
.ak-followed .ak-table tr th:first-child {
    height: 40px;
}
.ak-thread .ak-table tr.ak-not-read,
.ak-followed .ak-table tr.ak-not-read {
    background: #FCF7DF;
}
.ak-thread .ak-table tr.ak-not-read:hover,
.ak-followed .ak-table tr.ak-not-read:hover {
    background: #F2E6C0;
}
.ak-thread .ak-table tr:first-child,
.ak-thread .ak-table tr:first-child:hover {
    background: #FCF7DF;
}
.ak-thread .ak-table tr.ak-read td .ak-topictitle-container .ak-text-topic .ak-content-text-topic .ak-title-topic,
.ak-followed .ak-table tr.ak-read td .ak-topictitle-container .ak-text-topic .ak-content-text-topic .ak-title-topic {
    background: none;
    font-size: 15px;
    font-size: 1.5rem;
    line-height: 15px;
    line-height: 1.5rem;
    color: #5C4124;
    font-weight: normal;
    max-width: 441px;
}
.ak-thread .ak-table tr.ak-not-read td .ak-topictitle-container .ak-text-topic .ak-content-text-topic .ak-title-topic,
.ak-followed .ak-table tr.ak-not-read td .ak-topictitle-container .ak-text-topic .ak-content-text-topic .ak-title-topic {
    background: none;
    font-size: 15px;
    font-size: 1.5rem;
    line-height: 15px;
    line-height: 1.5rem;
    color: #5C4124;
    font-weight: bold;
    max-width: 441px;
}
.ak-thread .ak-table tr td:nth-child(4) a,
.ak-followed .ak-table tr td:nth-child(4) a {
    color: #8e614d;
}
.ak-thread .ak-table tr td .ak-dropdown-actions,
.ak-followed .ak-table tr td .ak-dropdown-actions {
    padding: 0 24px;
    font-size: 0;
    background-color: #f2e6c0;
    border-color: #5c4124;
}
.ak-thread .ak-table tr td .ak-dropdown-actions:hover,
.ak-thread .ak-table tr td .ak-dropdown-actions:focus,
.ak-followed .ak-table tr td .ak-dropdown-actions:hover,
.ak-followed .ak-table tr td .ak-dropdown-actions:focus {
    box-shadow: none;
    background-color: #f2e6c0;
    border-color: #9BA712;
}
/* Lecture Sujet */

.ak-title-topic.ak-title-container .ak-h1 {
    position: relative;
}
/* Réponses */

.ak-posts-list.ak-nocontentpadding .ak-post .ak-subpost .ak-post {
    border-top: 1px solid #5d4a37;
    border-left: 2px solid #5d4a37;
}
.ak-posts-list.ak-nocontentpadding .ak-post .ak-subpost .ak-post .col:first-child.grpadmin-block,
.ak-posts-list.ak-nocontentpadding .ak-post .col:first-child.ak-administrator-block {
    background: #c7d359;
}
.ak-posts-list.ak-nocontentpadding .ak-post .ak-subpost .ak-post .col:last-child.ak-admin-text .grpadmin-block {
    background-color: rgba(199, 211, 89, 0.36);
}
.ak-posts-list.ak-nocontentpadding .ak-post .col:first-child.grpmodo-block {
    background-color: rgba(255, 220, 87, 0.66);
}
.ak-posts-list.ak-nocontentpadding .ak-post .col:first-child {
    background: #f2e6c0;
}
.ak-posts-list.ak-nocontentpadding .ak-post .col:last-child {
    background: #fcf7df;
}
/* Réponses aux réponses */

.ak-posts-list.ak-nocontentpadding .ak-post .ak-subpost .ak-post .col:first-child {
    background: #f2e6c0;
}
.ak-posts-list.ak-nocontentpadding .ak-post .ak-subpost .ak-post .col:last-child {
    background: #fcf7df;
}
.ak-posts-list.ak-nocontentpadding .ak-post .ak-subpost {
    border: none;
    margin-left: 6px;
    padding: 0;
}
/* Général */

.ak-main-container .ak-admin-text {
    background: #FCF7DF;
    color: #5C4124;
}
.ak-title-container .ak-h1 .ak-block-score {
    font-size: 11px;
    text-shadow: none;
    color: #000;
    text-align: left;
    padding: 5px 0;
    position: absolute;
    right: 50px;
    z-index: 99;
    top: 10px;
    max-width: 80px;
}
.ak-posts-list.ak-nocontentpadding .ak-panel-title,
.ak-panel-form-post .ak-panel-title {
    background: linear-gradient(#BBCD3C, #7F9B12);
    border: none;
    text-shadow: none;
    color: #422f14;
    padding: 12px;
    padding: 1.2rem;
    margin-top: 0;
}
.ak-nb-answers {
    background: #F2E6C0;
    color: #422f14;
    font-size: 13px;
    padding: 0 12px;
    vertical-align: middle;
    border-radius: 5px;
    border: 1px solid #BBAD86;
    margin-left: 12px;
    display: inline-block;
    font-family: "Roboto", sans-serif;
}
.ak-main-container {
    width: 100%;
}
.ak-main-content {
    width: 90%;
    margin: 0 auto;
}


/* Trackers */

.ak-container.ak-panel.ak-topics-list-search .ak-block-topic .col-xs-10,
.ui-widget .ak-modal .ak-form .ak-block-topic .col-xs-10 {
    background: #fcf7df;
}
.ak-container.ak-panel.ak-topics-list-search .ak-block-topic,
.ui-widget .ak-modal .ak-form .ak-block-topic {
    background: #f2e6c0;
}
/* Créer un sujet */

.ak-container.ak-panel.ak-form-create-topic {
    background: #fcf7df;
}
.ak-container.ak-panel.ak-form-create-topic .form-group select.form-control,
.ak-container.ak-panel.ak-form-create-topic .form-group input.form-control {
    background: #f2e6c0;
    color: #555;
    border-color: #5C4124;
}
/******* MISE A JOUR *********/

.col-md-3 {
    width: 100%;
}
.container .ak-main-page {
    padding: 0px 0px 0px 0px;
}
.col-sm-3 {
    display: none;
}
nav .ak-breadcrumb-spacer {
    display: inline;
}
.container {
    max-width: none;
    padding: 0;
}
.container {
    margin-right: 0;
    margin-left: 0;
    padding-left: 0px;
    padding-right: 0px;
}
.col-md-9 {
    box-sizing: content-box;
    order: 2;
}
.container .ak-main-page {
    padding: 0px 0px 0px 0px;
}
.row {
    display: flex;
    flex-flow: row wrap;
}
.row > * {
    flex: 1 100%;
}
}
