css:
@-moz-document domain("neatorama.com")
{

/* Lets get rid of junk */

#page-sidebar,
.commlist,
#oauth2relay504581201,
.fbiframe,
.img-hover-share-overlay,
.postbody-text table a[href*="neatoshop.com/product/"],
.cb,
.shoprotator
{
    display: none !important;
}
/* Lets get rid tshirt ads */

br,
body b
{
    display: none;
}

.post br,
.post b
{
    display: block !important;
}
/* New color scheme */

body
{
    background: rgb(27, 31, 40);
}

body,
text,
p,
h1,
h2,
h3,
h4,
h5,
h6,
span,
li
{
    color: #e5e5e5;
}

.post h1 a,
.post .postbody a,
a,
a:link
{
    color: #0084d3;
}

#navbar .dropdown-trigger span
{
    color: #333;
}
/* Dotted line seperating articles */

.post .postbody-text:after
{
    content: "";
    display: inline-block;
    height: 0.5em;
    vertical-align: top;
    width: 100%;
    border-bottom: 1px dotted #aaa;
    border-style: none none dotted;
}
/* Full width articles */

#page-content
{
    float: none !important;
    width: 100% !important;
}
/* center images and embedded frames */

.img-hover-share-wrapper,
.post iframe
{
    display: block !important;
    text-align: center !important;
    margin: auto !important;
}
/* break text after an article image */

.postbody-text a[style*="float: left"] {
    display: block !important;
    float: none !important;
}
/* larger videos */

.post iframe[src*="youtube.com"],
.post iframe[src*="vimeo.com"],
.post iframe[src*="player"]
{
    width: 935px !important;
    height: 526px !important;
    margin: auto !important;
}
/* outline dark prev/next button */

.icon.next-page,
.icon.prev-page
{
    -webkit-filter: drop-shadow(0px 0px 1px rgba(255, 255, 255, 1));
}
/* make hot topics text easier to read */

.bestposts-listing > li > span > a strong
{
    background-color: rgba(0, 0, 0, 0.5);
}
}
