css:
@-moz-document domain(watch.impress.co.jp){
#global-nav{
position:relative !important;
left:auto !important;
margin:auto !important}

#category-nav{position:relative !important}

#extra{
display:none;
float:none;
width:auto;
padding:0}

/*
#extra>aside>aside:not(.ranking),
#extra aside.ranking.topics.list~*{display:none}
#extra aside.ranking.topics.list, #extra .list{width:auto}
*/

#main{
overflow-x:none;
float:none;
width:auto;
padding:0}

#main article[role="main"]{background-color:transparent}

#main article[role="main"]>div.contents{
margin:0 auto 20px;
max-width:700px;
background-color:white}

div.image-wrap{margin:0 0 20px}

div.image-wrap div.row *{
width:auto !important;
height:auto !important}

div.image-wrap div.column{
float:none;
margin:0 0 20px}

div.image-wrap>div.inner{
display:block;
margin:0;
border:none;
padding:20px;
background-color:ivory}

div.image-wrap div.column .img-wrap-h{float:none}
div.image-wrap div.column .img-wrap-w{position:relative}

div.image-wrap div.column .img-wrap-h ~ .caption{
margin:10px auto 0;
max-width:480px;
padding:0}

#daily-block-0.list-02.daily-block li.group.type-1,
#summary-block,
#main article div.site.static.day1,
#main article aside.recommend.topics.list{display:none}
}
