css:
@-moz-document domain(polycount.com){
  html{background:#020!important;}
  body{background:#020!important;color:#bbb!important;line-height:110%!important;}
  /*global*/::-moz-selection{background:#063!important;color:#ccc!important;font-weight:bold!important;}
  /*global*/*{text-shadow:none!important;text-decoration:none!important;border:none!important;box-shadow:none!important;}
  /*link*/a,a:link{color:#c60!important;}
  /*link*/a:visited{color:#860!important;}
  /*link*/a:hover{color:#f60!important;}
  
  /*top menu*/
  /*top menu*/
  /*nav*/     .navbar{background:#111!important;min-height:initial!important;}
  /*nav*/     .navbar-fixed-top, .navbar-fixed-bottom{position:static!important;}
  /*nav icon*/.navbar{margin:30px 0px 0px 0px!important;}
  /*nav icon*/.navbar>.container{}
  /*nav icon*/.navbar>.container>.navbar-header{padding:5px 10px!important;}
  /*nav icon*/.navbar>.container>.navbar-header:hover{background:#333!important;}
  /*nav icon*/.navbar>.container>.navbar-header>a{padding:0px!important;height:40px!important;}
  /*nav*/     .navbar>.container .navbar-right>ul{}
  /*nav*/     .navbar>.container .navbar-right>ul>li{}
  /*nav*/     .navbar>.container .navbar-right>ul>li>a{font-size:25px!important;line-height:50px!important;margin:0px!important;padding:0px 10px!important;}
  /*nav*/     .navbar>.container .navbar-right>ul>li>a:hover{background:#063!important;}
  /*nav*/     .navbar>.container .navbar-right>ul+div{}
  /*signIn*/  .navbar>.container .navbar-right>ul+div .MeBox{}
  /*signIn*/  .navbar>.container .navbar-right>ul+div .MeBox>a{margin:0px!important;padding:9px 10px!important;}
  /*signIn*/  .navbar>.container .navbar-right>ul+div .MeBox>a:hover{background:#333!important;}
  /*signIn*/  .navbar>.container .navbar-right>ul+div .MeBox-SignIn{padding:5px 0px!important;}
  /*signIn*/  .navbar>.container .navbar-right>ul+div .MeBox-SignIn>.SignInLinks{margin:0px!important;}
  /*signIn*/  .navbar>.container .navbar-right>ul+div .MeBox-SignIn>.SignInLinks a{color:#fff!important;font-size:14px!important;line-height:20px!important;padding:2px 3px!important;margin:8px 5px!important;}
  /*signIn*/  .navbar>.container .navbar-right>ul+div .MeBox-SignIn>.SignInIcons{margin:0px!important;}
  /*signIn*/  .navbar>.container .navbar-right>ul+div .MeBox-SignIn>.SignInIcons>a{margin:0px!important;background:#111!important;margin-left:0px!important;}
  /*signIn*/  .navbar>.container .navbar-right>ul+div .MeBox-SignIn>.SignInIcons>a .Icon{font-size:30px!important;line-height:40px!important;}
  /*account*/ .navbar>.container .navbar-right>ul+div .MeMenu{}
  /*account*/ .navbar>.container .navbar-right>ul+div .MeMenu a:hover{background:#063!important;}
  /*search*/  .navbar>.container .navbar-right>.navbar-form{margin:0px!important;}
  /*search*/  .navbar>.container .navbar-right>.navbar-form input{height:50px!important;}
  
  
  /*location*/.Breadcrumbs{margin:0px!important;padding:10px 0px!important;}
  /*location*/.Breadcrumbs span{font-size:30px!important;}
  /*ad*/.col-md-12>[id*="_ad"]{display:none!important;}
  /*title*/.H{color:#a11!important;}
  
  
  /*content header ad*/.adInfo{display:none!important;}
  /*content header collapse*/.page-header, h1.H, .HomepageTitle, .PageTitle{margin:0px!important;padding-bottom:0px!important;}
  
  /*news box*/   .Tiles{margin:0px!important;}
  /*news box*/   .Tiles .Tile{background:transparent!important;padding:10px!important;}
  /*news img*/   .Tiles .Tile .Image,.Tiles .Tile .Image img{border-radius:30px 30px 0px 0px!important;}
  /*news title*/ .Tiles .Tile .Title{background:#000!important;padding:5px 0px 0px 10px!important;}
  /*news title*/ .Tiles .Tile .Title a{color:#a11!important;font-size:22px!important;line-height:100%!important;}
  /*news text*/  .Tiles .Tile .Body{padding:0px!important;margin-bottom:33px!important;}
  /*news text*/  .Tiles .Tile .Message{background:#050!important;font-size:24px!important;line-height:120%!important;padding:0px 5px!important;}
  /*news author*/.Tiles .Tile .AuthorWrap{padding:0px!important;background:transparent!important;}
  /*news author*/.Tiles .Tile .AuthorWrap>.AuthorLink{padding:0px!important;background:#000!important;}
  /*news author*/.Tiles .Tile .AuthorWrap>.AuthorLink>.Username{float:left!important;font-size:20px!important;line-height:30px!important;color:#0af!important;}
  /*news author*/.Tiles .Tile .AuthorWrap>.AuthorLink>.Meta{float:right!important;font-size:20px!important;line-height:30px!important;}
  
    
  /*pages*/
  /*pages*/
  /*page*/  .Pager.NumberedPager .polycon-arrow-left,.Pager.NumberedPager .polycon-arrow-right,
  /*page*/  .Pager.NumberedPager .Ellipsis,
  /*page*/  .Pager.NumberedPager>a{font-size:50px!important;line-height:100%!important;}
  /*page*/  .Pager.NumberedPager>a{background:#000!important;}
  /*page*/  .Pager.NumberedPager>span{background:transparent!important;}
  /*page*/  .Pager.NumberedPager>a.Highlight{color:#a11!important;}
  /*search*/.Pager.NumberedPager>.QuickSearch{background:#333!important;border-radius:100%!important;padding:8px 10px!important;margin:13px 5px!important;}
  /*page*/  .Pager.NumberedPager>*:hover{background:#060!important;}
}
@-moz-document regexp("http(s)?://polycount\.com/forum.*"){
  h1.H.HomepageTitle{text-align:center!important;}
  /*category*/.CategoryGroup{margin-bottom:30px!important;}
  /*category*/.CategoryGroup>h2{color:#a11!important;font-size:28px!important;line-height:100%!important;padding-left:5px!important;}
  /*category match table th*/.DataTable{margin-top:-30px!important;}
  /*th*/   .DataTable>thead{background:#000!important;}
  /*th*/   .DataTable>thead>tr>td{color:#ddd!important;font-size:16px!important;line-height:30px!important;padding:0px!important;}
  /*th*/   .DataTable>thead>tr .LatestPost>div{display:none!important;}
  /*forum*/.DataTable>tbody .EntryLink h3{color:#c60!important;font-size:28px!important;line-height:100%!important;width:100%!important;background:inherit!important;padding-left:10px!important;}
  /*forum*/.DataTable>tbody .CategoryDescription{color:#bbb!important;font-size:20px!important;line-height:100%!important;margin-top:0px!important;padding-left:10px!important;}
  /*info count*/.DataTable>tbody .CountDiscussions .Number{color:#0a0!important;font-size:22px!important;line-height:100%!important;}
  /*info count*/.DataTable>tbody .CountComments .Number{color:#0af!important;font-size:22px!important;line-height:100%!important;}
  /*info lastPost*/.DataTable>tbody .LatestPost .PhotoWrap{display:none!important;}
  /*info lastPost*/.DataTable>tbody .LatestPost .LatestPostTitle{font-size:14px!important;line-height:100%!important;white-space:pre-wrap!important;font-weight:100!important;}
  /*info lastPost*/.DataTable>tbody .LatestPost .Meta>.CommentDate{font-size:16px!important;line-height:100%!important;color:#eee!important;}
  /*info lastPost*/.DataTable>tbody .LatestPost .Meta>.UserLink{display:none!important;}
  /*info lastPost*/.DataTable>tbody .LatestPost .Meta>span:last-child{display:none!important;}
  /*hover*/.DataTable tr.Item:hover td{background:#063!important;}
}
@-moz-document regexp("http(s)?://polycount\.com/categories/.*"){
  /*thead*/ .DataTable>thead{display:none!important;}
  /*resize*/.DataTable>tbody>tr>td *{min-width:0px!important;}
  /*resize*/.DataTable>tbody>tr>td:nth-child(1){width:82%!important;}
  /*resize*/.DataTable>tbody>tr>td:nth-child(2){width:2%!important;}
  /*resize*/.DataTable>tbody>tr>td:nth-child(3){width:6%!important;}
  /*resize*/.DataTable>tbody>tr>td:nth-child(4){width:10%!important;}
  /*title*/ .DataTable>tbody .EntryLink{padding:0px!important;}
  /*title*/ .DataTable>tbody .DiscussionName{padding:5px 0px 5px 10px!important;}
  /*title*/ .DataTable>tbody .DiscussionName h3{color:#c60!important;font-size:32px!important;line-height:100%!important;font-weight:500!important;}
  /*title*/ .DataTable>tbody .DiscussionName a:visited>h3{color:#660!important;font-weight:200!important;}
  /*title*/ .DataTable>tbody .DiscussionName .Tags span{color:#a11!important;font-size:24px!important;line-height:120%!important;margin:0px!important;font-weight:800!important;}
  /*info*/  .DataTable>tbody .DiscussionOptions .ImagePreview{color:#cc0!important;font-size:28px!important;line-height:100%!important;margin:0px!important;}
  /*info*/  .DataTable>tbody .CountComments .Number{color:#0af!important;font-size:28px!important;line-height:100%!important;}
  /*time*/  .DataTable>tbody .LastUser *{text-transform:initial!important;font-family:initial!important;}
  /*time*/  .DataTable>tbody .LastUser>*{display:inline-table!important;}
  /*time*/  .DataTable>tbody .LastUser .AuthorLink{width:100%!important;padding:10px 0px 0px 0px!important;}
  /*time*/  .DataTable>tbody .LastUser .AuthorLink .PhotoWrap{padding:0px 5px 0px 0px!important;}
  /*time*/  .DataTable>tbody .LastUser .AuthorLink .LatestPostTitle{color:#c60!important;font-size:20px!important;line-height:100%!important;padding:0px 0px 5px 0px!important;overflow:visible!important;}
  /*time*/  .DataTable>tbody .LastUser .Meta{overflow:visible!important;}
  /*time*/  .DataTable>tbody .LastUser time{color:#eee!important;font-size:16px!important;line-height:100%!important;}
  /*BG*/    .DataTable>tbody>tr>td{background:transparent!important;}
  /*BG*/    .DataTable>tbody>tr{background:#040!important;}
  /*BG*/    .DataTable>tbody>tr:nth-child(2n){background:#030!important;}
  /*BG*/    .DataTable>tbody>tr:hover{background:#000!important;}
}
@-moz-document regexp("http(s)?://polycount\.com/discussion/.*"){
  /*banner*/body>.banner{display:none!important;}

  /*post*/
  /*post*/
  .Author-Card~.sidePocketWrap{display:none!important;}

  /*resize*/
  /*resize OP*/.MessageList.Discussion .Item-Aside{left:initial!important;padding:0px!important;width:23%!important;}
  /*resize OP*/.MessageList.Discussion .Item-Aside+.Item-Content{right:initial!important;width:77%!important;}
  /*resize author*/.CommentsWrap{width:1000px!important;margin:auto!important;}
  /*resize author*/.CommentsWrap .Item .Author-Photo{position:initial!important;right:initial!important;width:15%!important;float:left!important;}
  /*resize author*/.CommentsWrap .Item .Author-Photo .ProfilePhoto{width:100%!important;max-width:100%!important;height:100%!important;border-radius:30px 0px 30px 30px!important;}
  /*resize content*/.CommentsWrap .Item .Item-Inner{width:85%!important;float:left!important;}
  /*resize content*/.CommentsWrap .Item-Aside+.Item-Content{right:0%!important;width:100%!important;}

  /*OP*/.MessageList.Discussion .Item-Aside *{background:transparent!important;}
  /*OP*/.MessageList.Discussion .Item-Aside .Author-Card{margin:0px!important;}
  /*OP*/.MessageList.Discussion .Item-Aside .ProfilePhoto{border-radius:30px 0px 30px 30px!important;}
  /*OP*/.MessageList.Discussion .Item-Aside .Author-Meta{display:none!important;}
  /*ad*/.CommentsWrap .Item-Aside{display:none!important;}
  /*author name*/.MessageList .Item-Header, .DataList-Search .Item-Header{padding:5px 0px 0px 10px!important;}
  /*author name*/.Item-Header .Username{font-size:28px!important;color:#0af!important;}
  /*author name*/.Item-Header .Rank{font-size:22px!important;}
  /*author name*/.Item-Header .DateCreated *{font-size:22px!important;color:#999!important;}
  /*author name*/.Item-Header .UserStatus *{font-size:22px!important;color:#444!important;}
  /*BG*/     .MessageList .Item-Header{background:#000!important;}
  /*BG*/     .MessageList .Item-Body{background:transparent!important;padding:0px!important;}
  /*content*/.MessageList .Item-Body .Message{background:#040!important;font-size:30px!important;line-height:120%!important;padding:5px 5px 0px 10px!important;}
  /*content*/.MessageList .Item-Body .Message>div>span{font-size:30px!important;line-height:120%!important;}
  /*content*/.MessageList .Item-Body strong,
  /*content*/.MessageList .Item-Body b{color:#a11!important;}
  /*content*/.MessageList .Item-Body h1,.MessageList .Item-Body h2,.MessageList .Item-Body h3{color:#a11!important;background:#000!important;padding:8px 0px 5px 10px!important;font-size:36px!important;line-height:100%!important;}
  /*content pre*/pre{background:#021!important;}
  /*content pre*/pre span{font-size:16px!important;}
  /*content pre*/pre>ol{padding-left:22px!important;}
  /*content pre*/pre>ol>li{background:transparent!important;}
  /*content pre*/pre>ol>li:hover{background:#030!important;}
  /*content pre*/pre>ol>li .pln{color:#aaa!important;}
  /*content pre*/pre>ol>li .kwd{color:#06a!important;}
  /*footer*/ .MessageList .Item-Body .Item-Footer{background:transparent!important;padding:0px 10px 0px 10px!important;margin:0px!important;}
  /*footer*/ .MessageList .Item-Body .Item-Footer .Reactions{display:none!important;}
  /*footer*/ .MessageList .Item-Body .Item-Footer .Signature br{display:block!important;margin:0px 0!important;line-height:0px!important;content:""!important;}
  /*footer*/ .MessageList .Item-Body .Item-Footer *{text-align:right!important;font-size:20px!important;line-height:100%!important;}

  /*quote font*/  blockquote{font-size:26px!important;line-height:110%!important;background:transparent!important;}
  /*quote BG*/    blockquote.Quote{color:#888!important;padding:0px 0px 0px 40px!important;margin:0px!important;overflow:visible!important;}
  /*quote author*/blockquote .QuoteAuthor{display:none!important;}
  /*quote br*/blockquote>div>br+br,
  /*quote br*/blockquote~br+br,
  /*quote br*/.Message>br:nth-child(-n+3){display:block!important;margin:0px 0!important;line-height:0px!important;content:""!important;}

  /*info*/.Item-Aside ul{color:#bbb!important;margin:0px!important;}
}
