css:
@-moz-document url-prefix("https://www.koreus.com/") {
/*@import url('https://fonts.googleapis.com/css?family=Roboto:300,400,500');*/

body{   
    background-color: #F0EFF7 !important;
}
table{
	background-color: transparent;
}
body table:first-child {
	background-color: transparent !important;
   
}
a{   
    font-family: 'Roboto', sans-serif;
    font-size: 15px;
    font-weight: 500;
}
img[src="//koreus.cdn.li/static/images/logo.jpg"]{
  width:406px;
  height:128px;
  box-sizing:border-box;
  padding-left: 406px;
	background : transparent  url('data:image/png;base64,');
}
form[action="/search.php"] input[type="text"]{
    border: 2px solid #EAE7F4;
    height: 30px;
    border-radius: 3px;
    padding: 2px 10px;
}
form[action="/search.php"] input[type="submit"]{
    border: 0px solid #330066;
    height: 34px;
    border-radius: 3px;
    background: #AEA0D4;
    color: #330066;
    cursor: pointer;
    padding: 3px 10px;
}
td#leftcolumn,
td#rightcolumn {
    background: none;
    border: 0px;
    width: 250px;
}
td#leftcolumn {
	padding-left: 5px;
}
td#rightcolumn {
	padding-right: 5px; 
}
td#leftcolumn div.blockTitle,
td#rightcolumn div.blockTitle {
    font-family: 'Roboto', sans-serif;
    text-align: left;
    font-weight: 500;
    background-image: none;
    background-color: #EDA261;
    color: #fff;
    line-height: 30px;
    padding-left: 10px;
    font-size: 13px;
    text-transform: uppercase;
    border-radius: 3px 3px 0px 0px;
    margin-top: 15px;
}
td#leftcolumn div.blockContent,
td#rightcolumn div.blockContent {   
    padding: 0;
    background: #fff;
    border-radius: 0px 0px 3px 3px;
}
/* QUI EST EN LIGNE */
td#leftcolumn div.blockContent a {
    font-weight: 500;
    font-family: 'Roboto', sans-serif;
}

/** MENU UTILISATEUR **/
td#leftcolumn div.blockContent td#usermenu,
td#rightcolumn div.blockContent td#usermenu{
    font-size:0;
    padding:0;
    border: 0px solid #ccc;
}
td#leftcolumn div.blockContent #usermenu a,
tdrightcolumn div.blockContent #usermenu a{
    line-height: 30px;
    font-weight: 300;
    font-family: 'Roboto', sans-serif;
    color: #330066;
    border-bottom: 0px solid rgba(0,0,0,.1);
    padding-left: 10px;
    border-right: 0;
}

/** MENU PRINCIPAL **/
td#leftcolumn div.blockContent td#mainmenu,
td#rightcolumn div.blockContent td#mainmenu{
    font-size:0;
    padding:0;
    padding-left: 8px;
}
td#leftcolumn div.blockContent .menuMain,
td#rightcolumn div.blockContent .menuMain{
    line-height: 30px;
    font-weight: 300;
    font-family: 'Roboto', sans-serif;
    color: #330066;
    border-top: 1px solid rgba(255,255,255,.5);
    border-bottom: 0;
    border-right: 0;
}
td#leftcolumn div.blockContent .menuTop,
td#rightcolumn div.blockContent .menuTop{
    line-height: 30px;
    font-weight: 300;
    font-family: 'Roboto', sans-serif;
    color: #330066;
    border-top: 0px solid #fff;
    border-bottom: 0;
}
td#leftcolumn div.blockContent .menuSub,
td#rightcolumn div.blockContent .menuSub{
    line-height: 30px;
    font-weight: 300;
    font-family: 'Roboto', sans-serif;
    color: #330066;
    border-top: 1px solid rgba(255,255,255,.5);
    margin-left: 10px;
    border-left: none;
    border-bottom: 0;
    border-right: 0;
}
/* COMMUNAUTE */
td#leftcolumn div.blockContent ul,
td#rightcolumn div.blockContent ul{
    padding: 8px;
}
/* COMMUNAUTE */
td#leftcolumn div.blockContent ul li,
td#rightcolumn div.blockContent ul li{
    list-style-type: none;
    line-height: 25px;
}
td#leftcolumn div.blockContent ul li a,
td#rightcolumn div.blockContent ul li a{
    font-weight: 300;
    font-family: 'Roboto', sans-serif;
}
/* NOUVEAU MEMBRES */
td#rightcolumn div.blockContent table tr td img{
	display: none;
}
td#rightcolumn div.blockContent table tr td {
	text-align: left;   
	line-height: 40px;
}
td#rightcolumn div.blockContent table tr td br{
    display: none;
}
/* CONTENU CENTRE */
td#centerCcolumn fieldset{
    border: 0;
    background: #CDC8F0;
}
td#centerCcolumn fieldset legend.blockTitle{
    font-weight: 300;
    font-family: 'Roboto', sans-serif;
    font-size: 30px;
    color: #330066;
    padding-bottom: 40px;
}
td#centerCcolumn fieldset .blockContent{
    border: 0;
    margin-top: -35px;
}
td#centerCcolumn fieldset .blockContent table {
    background: none;
    border-spacing: 0px;
    padding-bottom: 10px;
}
td#centerCcolumn fieldset .blockContent table tr td {
    font-family: 'Roboto', sans-serif;
    font-weight: 300;
    font-size: 15px;
}
td#centerCcolumn fieldset .blockContent table .itemHead {
 	border:0;
    background: none;
    font-weight: 500;
    font-family: 'Roboto', sans-serif;
    
}
td#centerCcolumn fieldset .blockContent table .itemHead h1{
    font-weight: 400;
    font-family: 'Roboto', sans-serif;
    font-size: 22px;   
}
td#centerCcolumn fieldset .blockContent table .even,
td#centerCcolumn fieldset .blockContent table .even .itemPoster {
    font-weight: 300;
    font-family: 'Roboto', sans-serif;
	background: none;   
    font-size: 13px;
}
td#centerCcolumn fieldset .blockContent table img.thumb{
	border:0;
    margin-left:0px;
}
td#centerCcolumn fieldset .blockContent center{
	background: rgba(51, 0, 102, .2);
    margin: -12px;
}
td#centerCcolumn fieldset .blockContent center h2{
    font-weight: 300;
    font-family: 'Roboto', sans-serif;
	color: #fff;
    line-height: 40px;
}
td#centerCcolumn fieldset .blockContent table.outer{
	border: 0;
}
td#centerCcolumn fieldset .blockContent table.outer tr{
    line-height: 20px;	   
}
td#centerCcolumn fieldset .blockContent table.outer tr td img {
	vertical-align: middle;
    width: 0px;
}
td#centerCcolumn fieldset .blockContent table.outer tr td a{
    font-family: 'Roboto', sans-serif;
    font-weight: 400;
    font-size: 14px;
}
}

@-moz-document url-prefix("https://www.koreus.com/modules/newbb/") {
/**FORUM**/
.comText{
	word-wrap:break-word;
    word-break: break-all;
}
}

@-moz-document url-prefix("https://www.koreus.com/video/") {
/** VUE D'UNE VIDEO**/
.container {
	border: none !important;   
}
.container h1.h2 {
    text-align: left;
}
.container h1.h2 span{
    font-weight: 300;
    font-family: 'Roboto', sans-serif;
}
.container .btn-group .btn-default{
	background: #ccc;
    border-radius: 3px 0px 0px 3px;
}
.container .btn-group.btn-group-justified .btn-group button{
	border-radius: 0px;   
}
.container p {
    font-family: 'Roboto', sans-serif;
    font-weight: 400;
    font-size: 15px;
}
.container p a.btn.btn-default.btn-sm {
    font-family: 'Roboto', sans-serif;
    background: #190033;
    color: #fff;
    border:0;
    border-radius: 2px;
    padding: 2px 6px;
    font-weight: 300;
}
}

@-moz-document url-prefix("https://www.koreus.com/modules/news/") {
/** TOUS LES ARTICLES OU 1 AVEC COMMENTAIRE **/
#centercolumn {
	padding: 10px;   
}
#centercolumn #content{
	padding: 0;   
}
#centercolumn .item{
    border:0;
    background: #fff;
    margin: 0;
    padding: 15px;
    border-top: 4px solid #524F81;
    border-radius: 3px;
    
}
#centercolumn .item .itemInfo{
    text-align:left;
    padding-bottom: 15px;
}
#centercolumn .item .itemInfo .itemPoster, #centercolumn .item .itemInfo .itemPoster a,
#centercolumn .item .itemInfo .itemPostDate, #centercolumn .item .itemInfo .itemPostDate a {
    background: none;
    font-family: 'Roboto', sans-serif;
    font-size: 14px;
}
#centercolumn .itemFoot{
    border:0;
    background: rgba(174,160,212,.1);
    border-radius: 4px;
    padding: 10px 15px;
}
#centercolumn .itemFoot .itemPermaLink{
    font-family: 'Roboto', sans-serif;
    font-weight: 400;
    color: #524F81;
    font-size: 14px;
}
#centercolumn .itemFoot .itemPermaLink a{
    font-family: 'Roboto', sans-serif;
    font-weight: 500;
    color: #524F81;
    font-size: 14px;    
}
#centercolumn .item p {
	display: none;
}
#centercolumn .item br {
	display: none;
}
#centercolumn .item p.itemText {
	display:block;  
    min-height: 100px;
    font-family: 'Roboto', sans-serif;
    font-weight: 400;
    font-size: 15px;
}
#centercolumn .item p.itemText a img {
	border: 0px solid #fff;   
}
#centercolumn .item .itemHead{
    border:0;
    background: transparent;
}
#centercolumn .item .itemHead h1.itemTitle a{
    font-family: 'Roboto', sans-serif;
    font-weight: 500;
    font-size: 17px;
}
#centercolumn .item .itemHead h1.itemTitle a:first-child{
	background: #EDA261;  
    padding: 2px 5px;
    color: #fff;
    border-radius: 3px;
}
#centercolumn .item .itemInfo{
    background: transparent;
}

/** 1 ARTICLE **/
#centercolumn div table.outer tr th {
 	 display:none;
}
#centercolumn div table.outer {
    border-spacing: 0px;
}
#centercolumn div table.outer tr td.odd {
 	 background: #CDC8F0;  
}
#centercolumn div table.outer tr td.odd .comText {
    font-family: 'Roboto', sans-serif;
    font-weight: 400;
    font-size: 15px;
    padding-top: 10px;
}
#centercolumn div table.outer tr td.odd .comTitle {
    font-family: 'Roboto', sans-serif;
    font-weight: 400;
    font-size: 17px;
}
#centercolumn div table.outer tr td.odd:first-child{
	background: rgba(174, 160, 212, .6);
}
.comUserStat, comUserStatus {
    font-family: 'Roboto', sans-serif;
    font-size: 14px;
	background: transparent;
    border: 0;
}
.comUserRankText{
    font-family: 'Roboto', sans-serif;
    font-size: 14px;
    font-weight: 500;
    text-transform: uppercase;
}
.comUserRankImg{
 display: none;   
}
.comUserImg {
	background: url('https://d3dbtvmfwwhlf2.cloudfront.net/pub/Marketing/WEBSITE+images/Web+content+pictures/image-placeholder-w.jpg?c=product_card') no-repeat;
    background-size: cover;
    max-width: 100px;
    max-height: 100px;
    min-width: 50px;
    min-height: 50px;
    
}
}
