css:
@-moz-document regexp("https://www.dofus.com/.*/fo.*") {
/* Global */
a.ak-btn-big.ak-btn-play {
	display: none;
}
aside.col-md-3 {
    position: -webkit-sticky;
    position: sticky;
    top: 43px;
}
.ak-backtotop {
    width: 0px;
    height: 0px;
}

nav .breadcrumb {
    position: relative;
    background-color: rgba(0, 0, 0, 0.1);
    padding: 5px 4px;
    border-radius: 5px 5px 0 0;
    margin-bottom: 0;
    border-top: 1px solid;
    border-left: 1px solid;
    border-right: 1px solid;
}


.container.ak-main-container {
 max-width: 80%;	
}
.ak-announce .ak-panel-title {
	font-weight: bold;
}
.ak-announce .ak-announce-toggle {
	font-weight: bold;
}
aside .ak-panel .ak-panel-title {
    text-shadow: 0 1px 2px rgba(0,0,0,0.1);
}
aside .ak-aside-search .ak-form input {
    height:45px;
}
aside .ak-aside-search .ak-form input[type="submit"] {
    right: 0px;
    top: 10px;
}

/* Index du forum */
.ak-title-container h1 {
	background-size: auto auto;
	text-shadow: 0 2px 2px #888;
}
.ak-title-container h1 {
	border-bottom: none;
	font-size: 26px;
	font-size: 2.6rem;
	padding-left: 84px;
	padding-left: 8.4rem;
	min-height: 52px;
}

.ak-table-thread .ak-table tr th, .ak-thread .ak-table tr th, .ak-followed .ak-table tr th {
	padding: 0 15px;
}
.ak-table-thread .ak-table tr th:first-child a {
	text-transform: uppercase;
	font-size: 1.8rem;
	font-size: 18px;
}
.ak-table-thread .ak-table tr th:first-child a::before {
	content: "\e080";
	font-family: 'Glyphicons Halflings';
	font-size: 15px;
	margin-right: 5px;
}
.ak-table-thread .ak-table tr th {
	font-size: 15px;
	font-size: 1.5rem;
}
.ak-table-thread .ak-table tr th:first-child {
    height: 40px; 
}
aside .btn-default {
    font-family: "bebas_neueregular",sans-serif;
    font-size: 23px;
    font-size: 2.3rem;
    line-height: 22px;
    line-height: 2.2rem;
}
aside .btn-default:hover, aside .btn-default:focus {
    border: 1px solid;
}
.ak-tree ul li.level-1 a {
	font-size: 14px;
}
.ak-tree li.level-1.ak-closed > a::after, .ak-tree li.level-1.ak-haschild.ak-closed > a::after {
    content: "\e114";
    font-family: "Glyphicons Halflings";
    position: absolute;
    right: 15px;
    top: 5px;
}
.ak-tree li.level-1.ak-haschild.ak-open > a::after {
    content: "\e113";
    font-family: "Glyphicons Halflings";
    position: absolute;
    right: 15px;
    top: 5px;
}
.ak-tree li.level-1.ak-open > a::after {
	content: "\e165";
    font-family: "Glyphicons Halflings";
    position: absolute;
    right: 15px;
    top: 5px;
}
.ak-tree li.level-2.ak-closed > a::after, .ak-tree li.level-3.ak-closed > a::after {
	content: "\e165";
    font-family: "Glyphicons Halflings";
    position: absolute;
    right: 15px;
    top: 5px;
}
.ak-tree li.level-4.ak-closed > a::after {
	content: "\e165";
    font-family: "Glyphicons Halflings";
    position: absolute;
    right: 10px;
    top: 5px;
}
.ak-tree li.level-2.ak-closed.ak-haschild > a::after, .ak-tree li.level-3.ak-closed.ak-haschild > a::after {
	content: "\e114";
    font-family: "Glyphicons Halflings";
    position: absolute;
    right: 15px;
    top: 5px;
}
.ak-tree li.level-2.ak-open > a::after, .ak-tree li.level-3.ak-open > a::after {
	content: "\e113";
    font-family: "Glyphicons Halflings";
    position: absolute;
    right: 15px;
    top: 5px;
}
.ak-tree ul .ak-haschild .ak-picto-close {
	display: none;
}
.ak-table-thread .ak-table tr td .form-group select, .ak-thread .ak-table tr td .ak-form .form-group select, .ak-followed .ak-table tr td .ak-form .form-group select {
    font-size: 13px;
    font-size: 1.3rem;
    width: auto;
    display: inline-block;
    margin: 6px 0;
    margin: .6rem 0;
}

/* Liste des sujets d'une section */
.ak-thread .ak-table tr td .ak-topictitle-container .ak-text-topic .ak-list-pictos .ak-picto.ak-lock::before, .ak-followed .ak-table tr td .ak-topictitle-container .ak-text-topic .ak-list-pictos .ak-picto.ak-lock::before {
	content: "\e033";
	font-family: "Glyphicons Halflings";
}
.ak-thread .ak-table tr td .ak-topictitle-container .ak-text-topic .ak-list-pictos .ak-picto.ak-lock, .ak-followed .ak-table tr td .ak-topictitle-container .ak-text-topic .ak-list-pictos .ak-picto.ak-lock { 
	background: transparent;	
}
.ak-thread .ak-table tr td .ak-topictitle-container .ak-text-topic .ak-list-pictos .ak-picto.ak-pin::before, .ak-followed .ak-table tr td .ak-topictitle-container .ak-text-topic .ak-list-pictos .ak-picto.ak-pin::before {
	content: "\e146";
	font-family: "Glyphicons Halflings";
	font-weight: bold;
}
.ak-thread .ak-table tr td .ak-topictitle-container .ak-text-topic .ak-list-pictos .ak-picto.ak-pin, .ak-followed .ak-table tr td .ak-topictitle-container .ak-text-topic .ak-list-pictos .ak-picto.ak-pin { 
	background: transparent;	
}
.ak-followed .ak-panel-footer {
    text-align: center;
    padding: 12px;
    border-top: 1px dashed;
}
.ak-title-container.ak-backlink a.ak-backlink-button {
    top: -2px;
	margin: 8px 0;
}
.ak-thread .ak-table tr th:first-child, .ak-followed .ak-table tr th:first-child {
    height: 40px;
}

.ak-thread .ak-table tr.ak-read td .ak-topictitle-container .ak-text-topic .ak-content-text-topic .ak-title-topic, .ak-followed .ak-table tr.ak-read td .ak-topictitle-container .ak-text-topic .ak-content-text-topic .ak-title-topic {
    background: none;
    font-size: 15px;
    font-size: 1.5rem;
    line-height: 15px;
    line-height: 1.5rem;
	font-weight: normal;
    max-width: 441px;
}
.ak-thread .ak-table tr.ak-not-read td .ak-topictitle-container .ak-text-topic .ak-content-text-topic .ak-title-topic, .ak-followed .ak-table tr.ak-not-read td .ak-topictitle-container .ak-text-topic .ak-content-text-topic .ak-title-topic {
    background: none;
    font-size: 15px;
    font-size: 1.5rem;
    line-height: 15px;
    line-height: 1.5rem;
	font-weight: bold;
    max-width: 441px;
}
.ak-thread .ak-table tr td .ak-dropdown-actions, .ak-followed .ak-table tr td .ak-dropdown-actions {
    padding: 0 24px;
    font-size: 0;
}
.ak-thread .ak-table tr td .ak-dropdown-actions:hover, .ak-thread .ak-table tr td .ak-dropdown-actions:focus, .ak-followed .ak-table tr td .ak-dropdown-actions:hover, .ak-followed .ak-table tr td .ak-dropdown-actions:focus {
    box-shadow: none;
}

/* Lecture Sujet */
.ak-title-topic.ak-title-container .ak-h1 {
	position: relative;
}
/* Réponses */
.ak-posts-list.ak-nocontentpadding .ak-post .ak-subpost .ak-post {
	border-top: 1px solid #e4ebb3;	
	border-left: 1px solid #e4ebb3;
}

.ak-posts-list.ak-nocontentpadding .ak-post .ak-subpost {
    border: none;
	margin-left: 6px;
	padding: 0;
}


/* Général */

.ak-panel .ak-panel-header-list-post {
    background: rgba(181, 200, 56, 0.59);
}

.ak-posts-list.ak-nocontentpadding .ak-panel-title, .ak-panel-form-post .ak-panel-title {
    border: none;
    text-shadow: none;
    padding: 12px;
    padding: 1.2rem;
    margin-top: 0;
}
.ak-nb-answers {
    font-size: 13px;
    padding: 0 12px;
    vertical-align: middle;
    border-radius: 5px;
	border: 1px solid;
    margin-left: 12px;
    display: inline-block;
    font-family: "Roboto", sans-serif;
}

/* Sidebar */
.ak-tree li.level-2.ak-closed > a::after, .ak-tree li.level-3.ak-closed > a::after {
    content: "";
}

/* Sidebar : scrollbar */
@media (min-width: 992px) {
    aside.col-md-3 {
        overflow-y: auto;
        height: calc(100vh - 43px);
        margin-bottom: 12px;
    }
    div.ak-aside-problems {
        margin-bottom: 0px;
    }
    aside.col-md-3::-webkit-scrollbar-track {
        -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.1);
        background: none repeat scroll 0 0 rgba(255, 255, 255, 0.2);
        border-radius: 10px;
        margin-top: 3px;
        margin-bottom: 3px;
    }
    aside.col-md-3::-webkit-scrollbar {
        width: 10px;
        background: none repeat scroll 0 0 transparent;
        margin-top: 3px;
        margin-bottom: 3px;
    }
    aside.col-md-3::-webkit-scrollbar-thumb {
        border-radius: 10px;
        background: none repeat scroll 0 0 rgba(0, 0, 0, 0.6);
        margin-top: 3px;
        margin-bottom: 3px;
    }
}

/*suppression marcassin*/
.ak-backtotop {
     width: 0px;
     height: 0px;
 } 

/* mise en valeur des réponses incrémentées */
.ak-posts-list.ak-nocontentpadding .ak-post .ak-subpost .ak-post .col:last-child {
    background: #fafbf4;
    
} 
/*Mise à gauche du menu supérieur*/
div.ak-container.container {
    margin-left: 10%;
    margin-right: 10%;
    max-width: 80%;
}
/*longueur auto menu supérieur*/
    .ak-container.container .row .col-sm-9 {
    width: inherit;
}
.ak-container.container .row .col-sm-3 {
    float: right;
}
/* réduction taille zone de recherche*/
aside .ak-aside-search .ak-form input {
    height:52px
}
aside .ak-aside-search .ak-form input[type="submit"] {
    right: 0px;
    top: 13px;
}
/*Couleur "dernier message"*/
.ak-table-thread .ak-table tr td ul li a {
    color: #636b20;
}
/*Couleur 1ere colonne*/
.ak-table-thread .ak-table tr td:first-child {
    background: #fcfdf4;
}
/*Couleur dernière colonne*/
.ak-table-thread .ak-table tr td:last-child {
    background: #fcfdf4;
    }
/*Couleur encadré post dev*/
.ak-posts-list.ak-nocontentpadding .ak-post .col.ak-administrator-block:first-child {
    background: #c7d359;
    border-top: 1px solid #909a36;	
	border-left: 1px solid #909a36;
}
/*Mise en valeur messages modos*/
div.ak-content-post.grpmodo-block {
  background: #ffffff;
  background: linear-gradient(#f3de96, #ffffff);
}
.ak-posts-list.ak-nocontentpadding .ak-post .ak-subpost .ak-post .col.grpmodo-block:first-child, .ak-posts-list.ak-nocontentpadding .ak-post .col.grpmodo-block:first-child {
  background: #ffffff;
  background: linear-gradient(#f3de96, #ffffff 84%);
}
/*Amélioration lisibilité panneau latéral nv4*/
    .ak-tree.ak-tree-forum li.level-4 a {
        Color: #6c6c6c;
        margin-left: 10px;
    }
/*Ankama header*/
@media (min-width: 768px) {
	.ak-idbar .ak-idbar-content, .navbar .navbar-container {
		margin: 0 0 0 auto;
		max-width: 98%;
	}
}

/*Log header*/
.ak-idbar .ak-idbar-right {
    float: right;
    margin-right: 10%;
}
/* Cadenas sujet fermé */
    .ak-thread .ak-table tr td .ak-topictitle-container .ak-text-topic .ak-list-pictos .ak-picto.ak-lock::before,
.ak-followed .ak-table tr td .ak-topictitle-container .ak-text-topic .ak-list-pictos .ak-picto.ak-lock::before {
    color: #de6b6b;
    
}
/*Couleur sujet lus*/
    .ak-thread .ak-table tr.ak-read td .ak-topictitle-container .ak-text-topic .ak-content-text-topic .ak-title-topic, .ak-followed .ak-table tr.ak-read td .ak-topictitle-container .ak-text-topic .ak-content-text-topic .ak-title-topic {
        color: #727272;
    }
    
/*Séparation sujets épinglés*/
    .ak-thread .ak-table > tbody > tr.ak-last-pinned-topic {
    border-bottom: 7px double #6b6b6b;
}
/*Citations*/
  .ak-posts-list.ak-nocontentpadding .ak-post blockquote, blockquote {
    background-color: rgba(0,0,0,0.03);
    border-color: rgba(0,0,0,0.03);
    border-left: 2px solid rgb(80,80,80);
    font-size: .95em;
    margin: 1em 0 .2em 0;
    color: #51492c;
  }
 
  .ak-posts-list.ak-nocontentpadding .ak-post blockquote cite, blockquote cite {
    margin-bottom: 1em;
    font-size: inherit;
    color: #505050;
  }
  .ak-posts-list.ak-nocontentpadding .ak-post blockquote cite + div, blockquote cite + div {
    padding: 5px 0;
    font-size: 15px;
  } 
}
