css:
@-moz-document domain("bittrex.com") {
    html.js.flexbox.flexboxlegacy.canvas.canvastext.webgl.no-touch.geolocation.postmessage.websqldatabase.indexeddb.hashchange.history.draganddrop.websockets.rgba.hsla.multiplebgs.backgroundsize.borderimage.borderradius.boxshadow.textshadow.opacity.cssanimations.csscolumns.cssgradients.cssreflections.csstransforms.csstransforms3d.csstransitions.fontface.generatedcontent.video.audio.localstorage.sessionstorage.webworkers.applicationcache.svg.inlinesvg.smil.svgclippaths.gr__bittrex_com,
    body,
    .content,
    #footmainbkg,
    .footermlinkl,
    .footermlinkr {
        background: black!important;;
        color: white!important;
    }
    
    select {
        color: black;
    }
    
    #market-wrapper #pad-wrapper {
        padding:0 15px;
    }

    .login-wrapper .box {
        background: black;
        border: solid 1px white;
    }

    img.logo {
        -webkit-filter: invert(100%);
        filter:invert(100%);
    }

    .tab-content>.active {
        height: 600px;
    }

    button.g-recaptcha.btn.btn-primary.login {
        width: 100%;
        background-color: #333;
        border-color: white;
    }

    h1, h2, h3, h4, h5 {
        color: #aaa;
    }

    a {
        color: #337ab7;
    }

    input.form-control {
        background-color: black;
        border: 1px solid #333;
    }
    
    .input-group .form-control:not(:first-child):not(:last-child), 
    .input-group-addon:not(:first-child):not(:last-child), 
    .input-group-btn:not(:first-child):not(:last-child) {
        color: white;
    }
    
    .input-group
    .input-group-addon {
        background-color: black;
        border: 1px solid #333;
        color: white;
    }

    .form-control[disabled],
    .form-control[readonly],
    fieldset[disabled] .form-control {
        background-color: #333;
        color: black!important;
    }

    .menu-wrapper {
        background-color: #111111;
    }

    .menu-table tr td.even {
        background-color: #111;
    }

    .menu-table tr td.odd {
        background-color: #111111;
    }

    .menu-table tr td {
        min-width: 12.5%;
        padding: 2px;
        border: 1px solid #252525;
    }

    .stx-nav {
        background: black;
    }

    #rowChart {
        margin:0;        
    }
    
    #rowChart > .col-md-8 {
        width:100%;
    }
    
    #rowChart > .col-md-4 {
        width:100%;
        padding:0;
        margin-top:20px;
    }
    
    #rowChart .market-stats .wrapper {
        border-color:#333;
    }

    #rowChart .market-stats .col-md-12 {
       width: 33%;
    }
    
    #rowChart .market-stats .col-md-12:first-child {
        width: 100%;
        border-bottom: 1px solid #333;
        padding:0;
    }

    #rowChart .market-stats .col-md-12:first-child H1 {
        padding: 5px;
    }
    
    .market-stats .title-none {
        color:white;
    }
    
    .market-stats img.market-logo {
        padding-top:0;
        padding-bottom:0;
        margin-bottom:5px;
    }
    
    .market-stats .wrapper .base-market {
        top: 5px;
    }
    
    .market-stats .wrapper .base-market-subtitle {
        top:25px;
        right:40px;
        padding:0;
    }

    .amcharts-main-div text  {
        fill: white;
    }
    
    .amcharts-main-div line,
    .amcharts-main-div .line,
    .amcharts-main-div path
    {
        stroke: #333;
    }
    
    .amcharts-chart-div path {
        stroke-opacity: 0.5;
    }
    
    .nav-tabs {
        border-color: #333;
    }
    
    .nav-tabs > li.active > a, 
    .nav-tabs > li.active > a:focus,
    .nav-tabs > li.active > a:hover {
        background: #333;
        color: white;
        border-color: #333;
    }
    
    .nav > li > a:focus,
    .nav > li > a:hover {
        background-color: #666;
        border-color: #333;
        color: white;
    }

    .navbar-inverse {
        background: black;
    }

    .connected-carousels .carousel-stage {
        border: 1px solid #333;
        -webkit-box-shadow: 0 0 2px black;
        -moz-box-shadow: 0 0 2px black;
        box-shadow: 0 0 2px black;
    }
    
    .connected-carousels .carousel-navigation {
        background: black;
        border: 1px solid #333;
        -webkit-box-shadow: 0 0 2px black;
        -moz-box-shadow: 0 0 2px black;
        box-shadow: 0 0 2px black;
    }

    .connected-carousels .carousel-navigation li img{
        border: none;
        border-left: 1px solid #333;
        border-right: 1px solid #333;
        -webkit-filter: saturate(4);
        filter: saturate(4);
    }
    
    .table-bordered {
        border: 1px solid #333;
    }  

    .table-striped > tbody > tr:nth-of-type(2n+1),
    .table-striped > tbody > tr:nth-of-type(odd) {
        background-color: #111;
    }

    .table-bordered > tbody > tr > td,
    .table-bordered > tbody > tr > th,
    .table-bordered > tfoot > tr > td,
    .table-bordered > tfoot > tr > th,
    .table-bordered > thead > tr > td,
    .table-bordered > thead > tr > th {
       border: 1px solid #333;
    }
    
    .table-hover tbody tr:hover>td,
    .table-hover tbody tr:hover>th,
    .table-hover tbody tr.odd:hover,
    .table-hover tbody tr.odd td.highlighted,
    .table-hover tbody tr.odd:hover td.sorting_1,
    .table-hover tbody tr.odd:hover td.sorting_2,
    .table-hover tbody tr.odd:hover td.sorting_3,
    .table-hover tbody tr.even:hover,
    .table-hover tbody tr.even td.highlighted,
    .table-hover tbody tr.even:hover td.sorting_1,
    .table-hover tbody tr.even:hover td.sorting_2,
    .table-hover tbody tr.even:hover td.sorting_3 {
        background-color: #545454;
    }

    .panel {
        background-color: black;
        -webkit-box-shadow: 0 1px 1px rgba(0,0,0,.05);
        box-shadow: 0 1px 1px rgba(0,0,0,.05);
    }  

    .panel-default {
        border-color: #333;
    }

    .panel-default > .panel-heading {
        color: #aaa;
        background-color: black;
        border-color: #333;
    }  

    .modal-content {
        background-color: #151515;
    }

    .modal-header {
        background-color: #252525;
        border-bottom: 1px solid black;
    }

    .bs-callout-info,
    .bs-callout-warning,
    .well {
        background-color: #333;
        border-color: #f0ad4e;
    }
    
    .bs-callout-danger {
        background-color: #333;
        border-color: #d9534f;
    }  

    .alert-info,
    pre {
        color: #aaa;
        background-color: #313535;
        border-color: #9cc8d1;
    }

    .dyn-tr-add {
        animation:none!important;
        -webkit-animation:none!important;
    }
    
    .pagination > li > a, 
    .pagination > li > span,
    .pagination > .disabled > a, 
    .pagination > .disabled > a:focus, 
    .pagination > .disabled > a:hover, 
    .pagination > .disabled > span, 
    .pagination > .disabled > span:focus, 
    .pagination > .disabled > span:hover {
        background-color:#333;
        border: 1px solid #666;
    }
    
    .list-group-item {
        background-color:#333;
        border: 1px solid #666;
        color: white;
    }
    
    a.list-group-item, button.list-group-item {
        color: white;
    }
    
    @media (max-width: 992px) {
        .market-stats .stat-right {
          padding-left:0;
        }
        
        .market-stats .stat-left .base-market,
        .market-stats .wrapper .base-market {
            position:relative;
            top:0;
            right:0;
        }
        
        .market-stats .stat-left .base-market-subtitle,
        .market-stats .wrapper .base-market-subtitle {
            top:35px;
            right:10px;
        }
    }
}

@-moz-document url-prefix("https://support.bittrex.com") {
    .layout {
        background-color:black;        
    }
    .subtle-wrap,
    .article-sidebar section {
        background-color:black;
    }
}
