body{	
  background: #E9EAED url("http://i.imgur.com/xwGTKUh.jpg") repeat fixed center center;
  color: #333;
  font: 10pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
  padding: 0px;
  margin:0px;
}



.thead-76 { background-color: #B9350D !important; }
.thead-77, .thead-88 { background-color: #024C9E !important; }
.thead-78, .thead-164, .thead-164, .thead-143 { background-color: #D59F1B !important; }
.thead-79, .thead-116 { background-color: #057D83 !important; }
.thead-80 { background-color: #3F8612 !important; }
.thead-82 { background-color: #452684 !important; }
.thead-81 { background-color:#757575  !important; }
.thead-169, .thead-172 { background-color:#795548 !important; }

#profile_tabs .tab_list .tcat { text-shadow: none; }
#profile_tabs .tab_list .thead { color: #000; }

#forocielo {
    height: 311px;
    margin-bottom: -80px;
    position: absolute;
    width: 100%;
    z-index: -1;
}

.body.forumhome #cabecera
{
    background: transparent url("https://wahackforo.com/estilo/vbulletin5_logo.png") no-repeat   no-repeat scroll center 20px;  /*BANNER*/ /*SE PUEDE USAR COMO WAHLOGO*/
    width: 400px;
    height: 180px;
    bottom: 0px;
    left: 0px;
    box-shadow: none;
    }


a.mini_button {
  background-color: #F4511E; 
  height: 17px;
  font-size: 10px;
  border: medium none;
  padding: 4px 6px 4px;
  color: #FFF;
  border-radius: 4px;
  transition: .3s; 
  color: #FFF; 
  text-decoration: none;
}

a.mini_button img { 
  margin-left:-6px; 
  margin-right:-40px; 
  padding-left:6px; 
  padding-right:40px;
  box-sizing: content-box;
  padding-bottom:20px;
  position:absolute;
  vertical-align: top;
}

a.mini_button:hover { background-color: #FF5722; color: white; }
.mini_button.off::after { content: "Cita +"; }
.mini_button.on::after { content: "Cita -"; }
.forum_tablon { background: rgba(0, 0, 0, 0.4) }
.forum_tablon a, .forum_tablon span { color: #FFF !important }
.forum_tablon tr, .forum_tablon td, .forum_tablon .tcat, .forum_tablon .thead { background: transparent !important; color: #FFF !important}
 
/* ------ START ------ */

*::-webkit-scrollbar {-webkit-appearance: none; width: 6px}
*::-webkit-scrollbar-track-piece {background-color: #E4E4E4}
*::-webkit-scrollbar-thumb {background-color: #D84315; border-radius: 0px; -moz-border-radius: 5px}
}
