.moneda, .dado, .dado100 {
    -webkit-transition:all 0.5s ease-in;
    -moz-transition:all 0.5s ease-in;
    transition:all 0.5s ease-in;
    -webkit-transform-style: preserve-3d;
    -moz-transform-style: preserve-3d;
    transform-style: preserve-3d;
    cursor: pointer;
    display: block;
	text-align: center;
}
.moneda {
    background-image: url("http://wahackforo.com/images/style/moneda-rol-duda.jpg");
    background-size: 100%;
    background-position:top;
    border-radius: 100%;
    height: 100px;
    margin: 50px auto;
    position: relative;
    width: 100px;
    box-shadow: 2px 2px 10px rgba(0,0,0,0.4);
    z-index: 10;
}
.moneda b {
	display: block;
    background-color: #a37131;
    background-image: url("http://wahackforo.com/images/style/moneda-rol-caracruz.jpg");
    background-size: 100%;
    background-position:bottom;
    border-radius: 100%;
    content: '';
    height: 100px;
    left: 0;
    position: absolute;
    top: 0;
    width: 100px;
    z-index: -12;
    -webkit-transform: translateZ(-5px);
    -moz-transform: translateZ(-5px);
    transform: translateZ(-5px);
}
.moneda.cruz, .moneda.cara {
    cursor: default;
    background-image: url('http://wahackforo.com/images/style/moneda-rol-caracruz.jpg');
}
.moneda.cruz {
    -webkit-transform: rotateX(360deg) rotateY(720deg);
    -moz-transform: rotateX(360deg) rotateY(720deg);
    transform: rotateX(360deg) rotateY(720deg);
}
.moneda.cara {
    -webkit-transform: rotateX(360deg) rotateY(540deg);
    -moz-transform: rotateX(360deg) rotateY(540deg);
    transform: rotateX(360deg) rotateY(540deg);
}
.dado.lanzado {
    cursor: default;
    -webkit-transform:rotateY(720deg);
    -moz-transform:rotateY(720deg);
    transform:rotateY(720deg);
}
.dado100.lanzado {
    cursor: default;
    -webkit-transform:rotate(720deg);
    -moz-transform:rotate(720deg);
    transform:rotate(720deg);
}

