css:
@-moz-document domain(zh.moegirl.org) {
/*黑幕模糊化，改自https://userstyles.org/styles/118277/bangumi-mask*/
 
.heimu,
.heimu a {
    background-color: transparent !important;
    color: #252525 !important;
    border: none !important;
    filter: blur(3px);
    transition: filter 100ms 200ms;
}
.heimu:hover,
.heimu a:hover {
    filter: blur(0.2px);
}
/*选中背景色*/
 
::selection {
    background: #ADD8E6;
}
/*顶端*/
 
div#p-personal {
    width: 100%;
    position: fixed;
    top: 0;
    right: 0;
    padding: 12px !important;
    background: #fff;
    box-shadow: 0 1px 3px 0 rgba(0, 34, 77, .05);
    border-bottom: 1px solid rgba(30, 35, 42, .06);
    justify-content: space-between;
}
#p-personal ul {
    float: right;
    color: rgba(0, 0, 0, 0.87);
    background: transparent;
}
#p-personal a {
    color: #8590a6 !important;
}
#pt-notifications-alert .mw-echo-notifications-badge,
#pt-notifications-notice .mw-echo-notifications-badge {
    color: transparent !important;
}
.topTitle {
    color: #1e1e1e;
    float: left;
    font-size: 22px;
    position: absolute;
    left: 18%;
    font-weight: 700;
    line-height: 32px;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}
/*主体*/
 
.ns-0 div#content,
.ns-0 div#p-cactions li a:hover,
.ns-0 div#p-cactions li.selected a,
.ns-0 div#content div.thumb,
div#content,
div#p-cactions li a:hover,
div#p-cactions li.selected a,
div#content div.thumb {
    background: white;
}
 
#content {
    background: white;
    margin-left: auto;
    margin-right: auto;
    margin-top: 20px;
    width: 70%;
    border: none;
    box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.2);
    border-radius: 3px;
}
h1,
h2,
h3,
h4,
h5,
h6 {
    font-family: "方正宋黑简体", serif !important;
    color: rgba(0, 0, 0, 0.87);
}
span.subpages {
    margin-left: 10px;
}
.mw-body-content {
    font-family: Helvetica Neue, Helvetica, PingFang SC, Hiragino Sans GB, Microsoft YaHei, Noto Sans CJK SC, WenQuanYi Micro Hei, Arial, sans-serif !important;
    color: rgba(0, 0, 0, 0.7) !important;
}
body {
    background: #F6F6F6 !important;
    font-family: Helvetica Neue, Helvetica, PingFang SC, Hiragino Sans GB, Microsoft YaHei, Noto Sans CJK SC, WenQuanYi Micro Hei, Arial, sans-serif !important;
    color: rgba(0, 0, 0, 0.7) !important;
}
pre,
code,
tt,
kbd,
samp,
.mw-code {
    font-family: Menlo, Monaco, Consolas, "Andale Mono", "lucida console", "Courier New", monospace;
}
a,
.mw-body-content a.extiw,
.mw-body-content a.extiw:active,
.mw-body-content a.external {
    color: #1976D2;
    text-decoration: none !important;
}
a:visited,
.mw-body-content a.external:visited,
.mw-body-content a.extiw:visited {
    color: #5E35B1;
}
a:active {
    color: #FFC107 !important;
}
h1,
h2 {
    border-bottom: none;
}
.comment-user a {
    color: #777 !important;
}
#toc,
.toc {
    background-color: #f8f9fa;
    border: 0 !important;
    border-radius: 3px;
    padding: 8px;
    box-shadow: 0 1px 5px -1px rgba(0, 0, 0, 0.2);
}
#catlinks {
    border: 0 !important;
    border-radius: 3px;
}
#wpSave,
#wpPreview,
#wpDiff {
    font-family: inherit;
    font-size: 1em;
    display: inline-block;
    min-width: 4em;
    max-width: 28.75em;
    padding: 0.546875em 1em;
    line-height: 1.286;
    margin: 0;
    border-radius: 2px;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    -webkit-appearance: none;
    zoom: 1;
    vertical-align: middle;
    background-color: #f8f9fa;
    color: #222222;
    border: 1px solid #a2a9b1;
    text-align: center;
    font-weight: bold;
    cursor: pointer;
}
.infoBox {
    border: 0 !important;
    border-radius: 3px;
    box-shadow: 0 1px 5px -1px rgba(0, 0, 0, 0.2);
}
.infoBoxContent {
    background: #ECEFF1;
    line-height: 1.3;
    border: 0 !important;
    border-radius: 3px;
}
/*表格相关*/
 
table.wikitable > tr > th,
table.wikitable > tr > td,
table.wikitable > * > tr > th,
table.wikitable > * > tr > td {
    border: none;
}
table.wikitable {
    border: none;
    box-shadow: 0 1px 5px -1px rgba(0, 0, 0, 0.2);
    border-radius: 3px;
    color: rgba(0, 0, 0, 0.7);
}
table.wikitable > tr > th,
table.wikitable > * > tr > th {
    background-color: #eaecf0;
}
 
/*navbox*/
 
table.navbox {
    border: none;
    font-size: smaller;
    box-shadow: 0 1px 5px -1px rgba(0, 0, 0, 0.2);
}
/*infobox*/
 
.infotemplatebox,
.acg-works-information {
    border: none !important;
    border-radius: 3px;
    box-shadow: 0 1px 5px -1px rgba(0, 0, 0, 0.2);
}
/*滚动条*/
 
::-webkit-scrollbar-thumb {
    background-color: #03A9F4;
    height: 50px;
    outline-offset: -2px;
    outline: 2px solid #fff;
    -webkit-border-radius: 4px;
    border: 2px solid #fff;
}
::-webkit-scrollbar-thumb:hover {
    background-color: #FFC107;
    height: 50px;
    -webkit-border-radius: 4px;
}
::-webkit-scrollbar {
    width: 8px;
    height: 8px;
}
::-webkit-scrollbar-track-piece {
    background-color: #FFF;
    -webkit-border-radius: 0;
}
/*隐藏内容*/
 
#localNotice,
#footer-info-copyright,
.mw-editTools,
#mw-panel,
#left-navigation,
#right-navigation {
    display: none;
}
}
