css:
@-moz-document domain("golang.org") {
html {
    background-color: #ddd;
    line-height: 1.5em;
    margin: 0 auto;
}

/* horizontally center */
.container p {
    margin: 0 auto;
    line-height: 1.7rem;
	font-family: Cambria, Cochin, Georgia, Times, Times New Roman, serif;
    font-size: 110%;
}


pre {
    /* background-color: #fff; */
    line-height: 1.5rem;
    margin: 30px auto 30px auto;
}


/* center */

.container p,
.container > h1, 
.container > h2, 
.container > h3, 
.container > h4,
.container > h5,
.container > h6,
.container > pre,
.container > ul,
.container > div#nav
{
    max-width: 800px;
}

.container > h1, 
.container > h2, 
.container > h3, 
.container > h4,
.container > h5,
.container > h6,
.container > ul
{
    margin: 20px auto 20px auto;
    
}


div#nav {
    margin: 0 auto;
}
}
