css:
@-moz-document domain("toutiao.io") {
/* force blk bg. */

html,
html[class],
html[id],
body,
body[class],
body[id],
input {
    background: #000 !important;
}
/* force blk bgc */

div:last-child,
:not(span),
:not(a) {
    background-color: #000 !important;
    border-color: #4AF !important;
}
/* force alp bgc */

:link,
:visited,
a,
h1,
h2,
h3,
h4,
h5,
h6,
p,
img,
video,
div,
span,
img~*,
img *,
video *,
video~*>*,
div[style*="text-align:"] *,
button,
button *,
div[style*="text-align:"],
[style*="background-image:"]~*,
div[style*="background-image:"] *,
[style*=".gif"],
[style*=".jpg"],
[style*=".png"] {
    background-color: transparent !important;
}
/*      a */

a,
a[class] {
    color: #9CF !important;
}
/* ahover */

a:hover {
    color: #39F !important;
}
/*     li */

li {
    color: #8BF !important;
}
/*     tr */

tr {
    color: #F00 !important;
}
/*   cite */

cite {
    color: #268 !important;
}
/*  input */

input {
    color: #5BF !important;
}
/*     em */

em {
    color: #DEF !important;
}
/*     ul */

ul {
    color: #F00 !important;
}
/* red */
/*   nobr */

nobr {
    color: #F00 !important;
}
/* red */
/*    div */

div {
    color: #BDF !important;
}
/* teal */
/* strong */

strong {
    color: #9CC !important;
}
/* greenish */
/*      s */

s {
    color: #F00 !important;
}
/* grey */
/*     h1 */

h1 {
    color: #ABC !important;
}
/*     h2 */

h2 {
    color: #DEF !important;
}
/*     h3 */

h3 {
    color: #CFF !important;
}
/*   rest */

* {
    color: #7CF !important;
}
/* Title/Headline. */

b {
    color: #7AF !important;
}
/* light blue-ish */
/* Text / Comments */

a,
span,
p,
div,
textarea,
i,
td {
    color: #ACE !important;
    text-shadow: -1px -1px #000, 1px -1px #000, -1px 1px #000, 1px 1px #000 !important;
}
/* white-ish */
/* ff select------ */

::-moz-selection {
    color: #0FF !important;
    background-color: #333 !important;
}
/* txt mark chrome */

::selection {
    color: #0FF !important;
    background-color: #333 !important;
}
/* txt mark others */

::-webkit-selection {
    color: #0FF !important;
    background-color: #333 !important;
}
/* temp: usf icons */

a > span.Sprite16 {
    filter: invert(100%);
    -webkit-filter: invert(100%);
}
}
