css:
@-moz-document domain("fanfiction.net") {


div[class*="_texture"] .img{
height: 55px!important;
width: 55px!important;
box-shadow: 0 0 5px #aaa!important;
}

/* Texture IMG's Preview */

img[src*="exclusive_paper.png"],img[src*="ricepaper.png"],img[src*="ricepaper2.png"],img[src*="lightpaperfibers.png"],img[src*="paper.png"],img[src*="paper_2.png"],img[src*="paper_3.png"],img[src*="paper_fibers.png"]{
width: 0!important;
padding-left: 55px!important;
border: 1px solid #ddd!important;
box-shadow: 0 0 2px #ddd!important;
}

/* Exclusive Paper */

#_fontastic_reading_preview[style*="exclusive_paper.png"], #content_wrapper_inner[style*="exclusive_paper.png"], img[src*="exclusive_paper.png"]{
background: url('http://i.imgur.com/mt1uSyw.png') center!important;
}

/* Rice Paper */

#_fontastic_reading_preview[style*="ricepaper.png"], #content_wrapper_inner[style*="ricepaper.png"], img[src*="ricepaper.png"]{
background: url('http://i.imgur.com/6F66YOi.png') center!important;
}

/* Rice Paper 2 */

#_fontastic_reading_preview[style*="ricepaper2.png"], #content_wrapper_inner[style*="ricepaper2.png"], img[src*="ricepaper2.png"]{
background: url('http://i.imgur.com/Q1Lv5iL.png') center!important;
}

/* Light Paper Fibers */

#_fontastic_reading_preview[style*="lightpaperfibers.png"], #content_wrapper_inner[style*="lightpaperfibers.png"], img[src*="lightpaperfibers.png"]{
background: url('http://i.imgur.com/J78NV4R.png') center!important;
}

/* Paper 1 */

#_fontastic_reading_preview[style*="/paper.png"], #content_wrapper_inner[style*="/paper.png"], img[src*="/paper.png"]{
background: url('http://i.imgur.com/HXcprRc.png') center!important;
}

/* Paper Fibers */

#_fontastic_reading_preview[style*="/paper_fibers.png"], #content_wrapper_inner[style*="/paper_fibers.png"], img[src*="/paper_fibers.png"]{
background: url('http://i.imgur.com/wYri1q8.png') center!important;
}

/* Paper 2 replaced by Groove Paper */

#_fontastic_reading_preview[style*="/paper_3.png"], #content_wrapper_inner[style*="/paper_2.png"], img[src*="/paper_2.png"]{
background: url('http://i.imgur.com/B8O3szs.png') center!important;
}

/* Paper 3 replaced by Lined Paper */

#_fontastic_reading_preview[style*="/paper_3.png"], #content_wrapper_inner[style*="/paper_3.png"], img[src*="/paper_3.png"]{
background: url('http://i.imgur.com/yucv7jV.png') center!important;
}
}
