css:
@-moz-document url-prefix('https://www.dreadcast.net/Forum') {
/*  'Scroll pour les labels et favoris' by .1019  */
/*                   DreadCast                    */

/*Liste des labels. Supprimer si pas besoin*/

#list_labels ul {
    max-height: 150px; 				/*Taille maximum de votre liste de labels. En dépassant cette barre, un scroll s'affiche*/
    overflow: auto; 				/*Scrollbar*/
    padding-right: 5px; 			/*Séparer le texte de la scrollbar*/
}

#list_labels ::-webkit-scrollbar {
    width: 8px 						/*Largeur de la barre de scroll. Pas recommandé de faire +10px*/;
}

#list_labels ::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3); /*Ombre du scroll, vous pouvez retirer si vous ne voulez voir que le truc à déplacer*/
    border-radius: 10px; 			/*Faire de jolis bords arrondis hihihi*/
}

#list_labels ::-webkit-scrollbar-thumb {
    border-radius: 10px 			/*Faire de jolis bords arrondis :3 !!*/;
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.8) /*Ombre dans la barre*/; 
    background: rgb(66, 127, 175) 	/*Couleur de la barre: changer par la couleur souhaitée*/ ;
}

/*Liste des favoris. Supprimer si pas besoin*/

#list_favoris ul {
    max-height: 150px; 				/*Taille maximum de votre liste de labels. En dépassant cette barre, un scroll s'affiche*/
    overflow: auto; 				/*Scrollbar*/
}

#list_favoris ::-webkit-scrollbar {
    width: 8px 						/*Largeur de la barre de scroll. Pas recommandé de faire +10px*/;
}

#list_favoris ::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3) /*Ombre du scroll, vous pouvez retirer si vous ne voulez voir que le truc à déplacer*/; 
    border-radius: 10px 			/*Faire de jolis bords arrondis hihihi*/;
}

#list_favoris ::-webkit-scrollbar-thumb {
    border-radius: 10px 			/*Faire de jolis bords arrondis :3 !!*/;
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.8) /*Ombre dans la barre*/; 
    background: rgb(66, 127, 175) 	/*Couleur de la barre: changer par la couleur souhaitée*/ ;
}
}
