css:
@-moz-document regexp("https://www.dofus.com/.*/fo.*"), regexp("https://www.wakfu.com/.*/fo.*"), regexp("https://www.krosmaga.com/.*/fo.*"), regexp("http://www.dofus-touch.com/.*/fo.*"), regexp("http://www.dofus-pets.com/.*/fo.*") {
/* ------------------------------------------------------ */
/* --------------- !!! COMMON SECTION !!! --------------- */
/* ------------------------------------------------------ */


/* ---------------------------------------- */
/* ------------- Ankama Header ------------ */
/* ---------------------------------------- */
@media (min-width: 768px) {
	.ak-idbar .ak-idbar-content, .navbar .navbar-container {
		margin: 0 0 0 auto;
		max-width: 98%;
	}
}

/* ----------------------------------------- */
/* ------------- Forum Top Page ------------ */
/* ----------------------------------------- */
div.ak-container.container {
    margin-left: 16px;
    margin-right: 16px;
    max-width: 98%;
}
.ak-container.container .row .col-sm-9, .ak-container.container .row .col-sm-12 {
    width: inherit;
}
.ak-container.container .row .col-sm-3 {
    float: right;
}
a.ak-btn-big.ak-btn-play {
	display: none;
}
.ak-breadcrumb-spacer {
    min-height: 0px !important;
    margin-top: 20px;
}
nav .breadcrumb {
    position: relative;
    background-color: rgba(0, 0, 0, 0.4);
    padding: 5px 4px;
    border-radius: 5px 5px 0 0;
    margin-bottom: 0;
}
@media (min-width: 768px) {
    .ak-container.container .row .col-sm-3::after {
        position: absolute;
        top: 4px;
        right: 0px;
        background: none repeat scroll 0 0 rgba(0, 0, 0, 0.6);
        padding: 8px 8px;
        border-radius: 5px;
        text-align: center;
        font-size: 18px;
        text-transform: uppercase;
        text-overflow: ellipsis;
        white-space: nowrap;
        font-family: "bebas_neueregular", sans-serif;
        color: #FFFFFF;
        text-shadow: 0 2px 2px #000;
    }
}

/* ------------------------------------------ */
/* ------------ Contenu principal ----------- */
/* ------------------------------------------ */
.container.ak-main-container {
    max-width: 98%;
}
.ak-title-container .ak-h1 .ak-block-score {
    font-size: 11px;
    text-shadow: none;
    color: #fff;
    text-align: left;
    padding: 5px 0;
    position: absolute;
    z-index: 99;
    max-width: 80px;
}
.ak-posts-list.ak-nocontentpadding .ak-panel-title,
.ak-panel-form-post .ak-panel-title {
    border: none;
    text-shadow: none;
    padding: 12px;
    padding: 1.2rem;
    margin-top: 0;
}
.ak-nb-answers {
    font-size: 13px;
    padding: 0 12px;
    vertical-align: middle;
    border-radius: 5px;
    margin-left: 12px;
    display: inline-block;
    font-family: "Roboto", sans-serif;
}

	/* ----- Annonces Ankama ----- */
.ak-announce .ak-panel-title, .ak-announce .ak-announce-toggle {
    font-weight: bold;
}

	/* Barre de titre */
.ak-title-container h1 {
    background-size: auto auto;
    text-shadow: 0 2px 2px #888;
    border-bottom: none;
    font-size: 26px;
    font-size: 2.6rem;
    padding-left: 84px;
    padding-left: 8.4rem;
    min-height: 52px;
}
.ak-title-container h1 .ak-icon-big {
    display: block;
    left: 5px;
    position: absolute;
    text-align: center;
    top: 0;
    background-image: url('http://staticns.ankama.com/dofus/www/game/items/200/18112.png');
    background-size: 45px auto;
    width: 45px;
    height: 45px;
    background-position: left top;
}

	/* ----- Index du forum ----- */
.ak-table-thread .ak-table tr th,
.ak-thread .ak-table tr th,
.ak-followed .ak-table tr th {
    padding: 0 15px;
}
.ak-table-thread .ak-table tr th:first-child a {
    text-transform: uppercase;
    font-size: 1.8rem;
    font-size: 18px;
}
.ak-table-thread .ak-table tr th:first-child a::before {
    content: "\e080";
    font-family: 'Glyphicons Halflings';
    font-size: 15px;
    margin-right: 5px;
}
.ak-table-thread .ak-table tr th {
    font-size: 15px;
    font-size: 1.5rem;
}
.ak-table-thread .ak-table tr th:first-child {
    height: 40px;
}
.ak-table-thread .ak-table tr td .form-group select,
.ak-thread .ak-table tr td .ak-form .form-group select,
.ak-followed .ak-table tr td .ak-form .form-group select {
    font-size: 13px;
    font-size: 1.3rem;
    width: auto;
    display: inline-block;
    margin: 6px 0;
    margin: .6rem 0;
}

	/* ----- Liste des sujets d'une section ----- */
.ak-thread .ak-table tr td .ak-topictitle-container .ak-text-topic .ak-list-pictos .ak-picto.ak-lock::before,
.ak-followed .ak-table tr td .ak-topictitle-container .ak-text-topic .ak-list-pictos .ak-picto.ak-lock::before {
    content: "\e033";
    font-family: "Glyphicons Halflings";
}
.ak-thread .ak-table tr td .ak-topictitle-container .ak-text-topic .ak-list-pictos .ak-picto.ak-lock,
.ak-followed .ak-table tr td .ak-topictitle-container .ak-text-topic .ak-list-pictos .ak-picto.ak-lock {
    background: transparent;
}
.ak-thread .ak-table tr td .ak-topictitle-container .ak-text-topic .ak-list-pictos .ak-picto.ak-pin::before,
.ak-followed .ak-table tr td .ak-topictitle-container .ak-text-topic .ak-list-pictos .ak-picto.ak-pin::before {
    content: "\e146";
    font-family: "Glyphicons Halflings";
    font-weight: bold;
}
.ak-thread .ak-table tr td .ak-topictitle-container .ak-text-topic .ak-list-pictos .ak-picto.ak-pin,
.ak-followed .ak-table tr td .ak-topictitle-container .ak-text-topic .ak-list-pictos .ak-picto.ak-pin {
    background: transparent;
}
.ak-followed .ak-panel-footer {
    text-align: center;
    padding: 12px;
}
.ak-title-container.ak-backlink a.ak-backlink-button {
    top: -2px;
    margin: 8px 0;
}
.ak-thread .ak-table tr th:first-child,
.ak-followed .ak-table tr th:first-child {
    height: 40px;
}
.ak-table tr td .ak-topictitle-container .ak-text-topic .ak-content-text-topic .ak-title-topic {
    background: none;
    font-size: 15px;
    font-size: 1.5rem;
    line-height: 15px;
    line-height: 1.5rem;
    font-weight: normal;
    max-width: 441px;
}
.ak-table tr.ak-not-read td .ak-topictitle-container .ak-text-topic .ak-content-text-topic .ak-title-topic {
    font-weight: bold;
}
.ak-thread .ak-table tr td .ak-dropdown-actions,
.ak-followed .ak-table tr td .ak-dropdown-actions {
    padding: 0 24px;
    font-size: 0;
}
.ak-thread .ak-table tr td .ak-dropdown-actions:hover,
.ak-thread .ak-table tr td .ak-dropdown-actions:focus,
.ak-followed .ak-table tr td .ak-dropdown-actions:hover,
.ak-followed .ak-table tr td .ak-dropdown-actions:focus {
    box-shadow: none;
}

	/* ----- Lecture Sujet ----- */
.ak-title-topic.ak-title-container .ak-h1 {
    position: relative;
}
	/* ----- Réponses ----- */
.ak-posts-list.ak-nocontentpadding .ak-post .ak-subpost .ak-see-more-comments a {
    border-radius:10px 0 0 10px;
    font-size: 17px;
}
.ak-posts-list.ak-nocontentpadding .ak-post .ak-subpost .ak-see-more-comments a span {
    padding: 13px 0px 13px 0px;
    font-weight: bold;
}
.ak-posts-list.ak-nocontentpadding .ak-post .ak-subpost .ak-see-more-comments > a > span::before {
    padding: 13px 0px 13px 5px;
    display:inline-block;
    content: "\e080";
    font-family: 'Glyphicons Halflings';
    font-size: 17px;
    margin-right: 5px;
}
.ak-posts-list.ak-nocontentpadding .ak-post .ak-subpost .ak-see-more-comments > a:hover > span::before {
    padding: 13px 0px 13px 5px;
    display:inline-block;
    content: "\e114";
    font-family: 'Glyphicons Halflings';
    font-size: 17px;
    margin-right: 5px;
}
.ak-posts-list.ak-nocontentpadding .ak-post .ak-subpost .ak-see-more-comments > a > span.ak-hide-comments::before {
    vertical-align:inherit;
}
.ak-posts-list.ak-nocontentpadding .ak-post .ak-subpost .ak-see-more-comments > a:hover > span.ak-hide-comments::before {
    content: "\e113";
    vertical-align:inherit;
}

	/* ----- Réponses aux réponses ----- */
.ak-posts-list.ak-nocontentpadding .ak-post .ak-subpost {
    border: none;
    margin-left: 6px;
    padding: 0;
}

/* ------------------------------------- */
/* ---------- Menu Latéral ---------- */
/* ------------------------------------- */
aside.col-md-3 {
    position: -webkit-sticky;
    position: sticky;
    top: 43px;
}

	/* ----- Recherche ----- */
aside .ak-aside-search .ak-form input {
    height: 52px;
}
aside .ak-aside-search .ak-form input[type="submit"] {
    right: 0px;
    top: 10px;
}

	/* ----- Tous les sujets ----- */
aside .btn-default {
    font-family: "bebas_neueregular", sans-serif;
    font-size: 23px;
    font-size: 2.3rem;
    line-height: 22px;
    line-height: 2.2rem;
}

	/* ----- Menu de navigation ----- */
.ak-tree ul li.level-1 a {
    font-size: 14px;
}
.ak-tree li.level-1.ak-closed > span::after,
.ak-tree li.level-1.ak-haschild.ak-closed > span::after,
.ak-tree li.level-1.ak-open > span::after,
.ak-tree li.level-2.ak-closed > span::after,
.ak-tree li.level-2.ak-closed.ak-haschild > span::after,
.ak-tree li.level-2.ak-open > span::after,
.ak-tree li.level-3.ak-closed > span::after,
.ak-tree li.level-3.ak-closed.ak-haschild > span::after,
.ak-tree li.level-3.ak-open > span::after,
.ak-tree li.level-4.ak-closed > span::after,
.ak-tree li.level-4.ak-closed.ak-haschild > span::after,
.ak-tree li.level-4.ak-open > span::after {
    font-family: "Glyphicons Halflings";
    position: absolute;
}
.ak-tree li.level-1 > span::after {
    right: 0px;
    top: 2px;
    font-size: 20px;
}
.ak-tree li.level-2 > span::after {
    right: 1px;
    top: 3px;
    font-size: 15px;
}
.ak-tree li.level-3 > span::after {
    right: 1px;
    top: 2px;
    font-size: 15px;
}
.ak-tree li.level-4 > span::after {
    right: 1px;
    top: 1px;
    font-size: 15px;
}
.ak-tree li.level-1.ak-closed > span::after,
.ak-tree li.level-2.ak-closed > span::after,
.ak-tree li.level-3.ak-closed > span::after,
.ak-tree li.level-4.ak-closed > span::after {
    content: "";
}
.ak-tree li.level-1.ak-haschild.ak-closed > span::after,
.ak-tree li.level-2.ak-haschild.ak-closed > span::after,
.ak-tree li.level-3.ak-haschild.ak-closed > span::after,
.ak-tree li.level-4.ak-haschild.ak-closed > span::after {
    content: "\e114";
}
.ak-tree li.level-1.ak-open > span::after,
.ak-tree li.level-2.ak-open > span::after,
.ak-tree li.level-3.ak-open > span::after,
.ak-tree li.level-4.ak-open > span::after {
    content: "\e113";
}
.ak-tree ul .ak-haschild .ak-picto-close {
    background-image: none;
}

	/* ----- Scrollbar ----- */
@media (min-width: 992px) {
    aside.col-md-3 {
        overflow-y: auto;
        height: calc(100vh - 43px);
        margin-bottom: 12px;
    }
    div.ak-aside-problems {
        margin-bottom: 0px;
    }
    aside.col-md-3::-webkit-scrollbar-track {
        -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.1);
        background: none repeat scroll 0 0 rgba(255, 255, 255, 0.2);
        border-radius: 10px;
        margin-top: 3px;
        margin-bottom: 3px;
    }
    aside.col-md-3::-webkit-scrollbar {
        width: 10px;
        background: none repeat scroll 0 0 transparent;
        margin-top: 3px;
        margin-bottom: 3px;
    }
    aside.col-md-3::-webkit-scrollbar-thumb {
        border-radius: 10px;
        background: none repeat scroll 0 0 rgba(0, 0, 0, 0.6);
        margin-top: 3px;
        margin-bottom: 3px;
    }
}

/* ---------- Bouton Back-To-Top ---------- */
.ak-backtotop {
    width: 0px;
    height: 0px;
}

/*Citations*/
.ak-posts-list.ak-nocontentpadding .ak-post blockquote, blockquote {
    border-radius:  0 10px 10px 0;
    font-size: .95em;
    margin: 1em 0 .2em 0;
}
.ak-posts-list.ak-nocontentpadding .ak-post blockquote cite, blockquote cite {
    margin-bottom: 1em;
    font-size: inherit;
}
.ak-posts-list.ak-nocontentpadding .ak-post blockquote cite + div, blockquote cite + div {
    padding: 5px 0;
    font-size: 15px;
}

@font-face {
    font-family: "FontAwesome";
    font-weight: normal;
    font-style : normal;
           src : url("https://maxcdn.bootstrapcdn.com/font-awesome/4.3.0/fonts/fontawesome-webfont.eot?v=4.3.0");
           src : url("https://maxcdn.bootstrapcdn.com/font-awesome/4.3.0/fonts/fontawesome-webfont.eot?#iefix&v=4.3.0") format("embedded-opentype"),
                 url("https://maxcdn.bootstrapcdn.com/font-awesome/4.3.0/fonts/fontawesome-webfont.woff2?v=4.3.0") format("woff2"),
                 url("https://maxcdn.bootstrapcdn.com/font-awesome/4.3.0/fonts/fontawesome-webfont.woff?v=4.3.0") format("woff"),
                 url("https://maxcdn.bootstrapcdn.com/font-awesome/4.3.0/fonts/fontawesome-webfont.ttf?v=4.3.0") format("truetype"),
                 url("https://maxcdn.bootstrapcdn.com/font-awesome/4.3.0/fonts/fontawesome-webfont.svg?v=4.3.0#fontawesomeregular") format("svg");
}

blockquote cite::after {
    content: "\f10e";
    float:right;
    font-family: "FontAwesome";
    font-size: 30px;
    color: rgba(0,0,0,0.15);

}

/*Page d'accueil : optimisation des widths des colonnes */
@media (min-width: 768px) {
    .ak-table-thread .ak-table tr th:last-child, .ak-table-thread .ak-table tr td:last-child {
        width: 5%;
        padding: 0;
    }
    .ak-table-thread .ak-table tr th:nth-child(3), .ak-table-thread .ak-table tr td:nth-child(3) {
    	width: 10%;
	}
    .ak-table-thread .ak-table tr th:nth-child(2), .ak-table-thread .ak-table tr td:nth-child(2) {
    	width: 39%;
	}
}
.main > .ak-main-center > .ak-container.ak-panel.ak-nocontentpadding {
    background: transparent; /* fix ligne blanche à droite du table, qui apparaissait selon la width du navigateur */
}

/* Liste des sujets : Avatars */
@media (min-width: 768px){
    .ak-table tr td .ak-topictitle-container .ak-block-avatar .ak-avatar {
        width: 100%;
        height: 45px;
    }
    .ak-table tr td .ak-topictitle-container .ak-block-avatar .ak-avatar img {
        object-fit: scale-down;
        width:100%;
        height:100%;
        margin-left: auto;
        margin-right: auto;
        display: block;
    }
    
    .ak-table tr td .ak-topictitle-container .ak-block-avatar .ak-avatar > a {
		display:block;
    }
}

/* Trakers : Avatars */
@media (min-width: 768px){
    .ak-panel-content .ak-block-topic .ak-avatar {
        width: 42px;
        height: 42px;
    }
    .ak-panel-content .ak-block-topic .ak-avatar img {
        object-fit: scale-down;
        width:100%;
        height:100%;
        margin-left: auto;
        margin-right: auto;
        display: block;
    }
    
    .ak-panel-content .ak-block-topic .ak-avatar > a {
		display:block;
    }
}

/* TITLE TOPIC : Avatars - Score */
@media (min-width: 768px){
    .ak-title-topic.ak-title-container .ak-h1 .ak-table-cell .ak-avatar {
        margin-top: 5px;
        width: 100%;
        height: 70px;
    }
    .ak-title-topic.ak-title-container .ak-h1 .ak-table-cell .ak-avatar.ak-administrator {
        height: 98px;
        width: 88%;
    }
    .ak-title-topic.ak-title-container .ak-h1 .ak-table-cell .ak-avatar img {
        object-fit: scale-down;
        width:100%;
        height:100%;
        margin-left: auto;
        margin-right: auto;
        display: block;
    }
    
    .ak-title-topic.ak-title-container .ak-h1 .ak-table-cell .ak-avatar > a {
		display:block;
    }
    
   .ak-title-topic.ak-title-container .ak-h1 .ak-table-cell > .ak-block-score {
        text-align: center;
    	font-weight: bold;
   		border-radius: 5px;
    	max-width: 100%;
    	padding: 4px 0;
        position:relative;
    }
}

/* POST : Avatars - Score */
@media (min-width: 768px){
    .ak-panel-content > .ak-post > .col-sm-2 > .ak-avatar {
        width: 100%;
        height: 70px;
    }
    .ak-panel-content > .ak-post > .col-sm-2 > .ak-avatar.ak-administrator {
        height: 98px;
    }
    .ak-panel-content > .ak-post > .col-sm-2 > .ak-avatar img {
        object-fit: scale-down;
        width:100%;
        height:100%;
        margin-left: auto;
        margin-right: auto;
        display: block;
    }
    
    .ak-panel-content > .ak-post > .col-sm-2 > .ak-avatar > a {
		display:block;
    }
    
    .ak-panel-content > .ak-post > .col-sm-2 > .ak-block-score {
        text-align: center;
    	font-weight: bold;
   		border-radius: 5px;
    	max-width: 100%;
    	padding: 2px 0;
        position:relative;
    }
}

/* SUBPOST : Avatars - Score */
@media (min-width: 768px){
    .ak-subpost > .ak-post > .col-sm-2,
    .ak-subpost > .ak-container > .ak-post > .col-sm-2 {
        padding-left: 0px !important;
    }
    .ak-subpost > .ak-post > .col-sm-2 > .ak-avatar,
    .ak-subpost > .ak-container > .ak-post > .col-sm-2 > .ak-avatar {
        width: 100%;
        height: 45px;
    }
    .ak-subpost > .ak-post > .col-sm-2 > .ak-avatar.ak-administrator,
    .ak-subpost > .ak-container  > .ak-post > .col-sm-2 > .ak-avatar.ak-administrator {
        height: 98px;
    }
    .ak-subpost > .ak-post > .col-sm-2 > .ak-avatar img,
    .ak-subpost > .ak-container > .ak-post > .col-sm-2 > .ak-avatar img {
        object-fit: scale-down;
        width:100%;
        height:100%;
        margin-left: auto;
        margin-right: auto;
        display: block;
    }
    
    .ak-subpost > .ak-post > .col-sm-2 > .ak-avatar > a,
    .ak-subpost > .ak-container > .ak-post > .col-sm-2 > .ak-avatar > a {
		display:block;
    }
    
    .ak-subpost > .ak-post > .col-sm-2 > .ak-block-score,
    .ak-subpost > .ak-container > .ak-post > .col-sm-2 > .ak-block-score {
