.ak-administrator .ak-block-score,
.ak-administrator-block .ak-block-score,
.grpadmin-block .ak-block-score,
.grpmodo-block .ak-block-score {
    display: none;
 }

/* Post Buttons and Voting system */
.ak-block-btns-post {
	height:55px;   
}

.ak-posts-list.ak-nocontentpadding .ak-post .col:last-child .ak-btn-actions-vote {
    border-radius:5px;
    margin-right:15px;
    margin-bottom: 10px;
    padding: 5px;
}

.ak-posts-list.ak-nocontentpadding .ak-post .col:last-child .ak-btn-actions-vote button {
    margin: 0 !important;
    border-radius:5px;
}

.ak-posts-list.ak-nocontentpadding .ak-post .col:last-child .ak-btn-actions-vote span {
    min-width: 30px;
	font-weight: bold;
    padding: 8px 0;
}

.ak-posts-list.ak-nocontentpadding .ak-post .col:last-child .ak-action-button {
    border-radius:5px;
    margin-left:10px;
    margin-bottom: 10px;
    padding: 7px 5px 6px 5px;
}
.ak-posts-list.ak-nocontentpadding .ak-post .col:last-child .ak-action-button > a,
.ak-posts-list.ak-nocontentpadding .ak-post .col:last-child .ak-action-button > div > button {
    border-radius: 5px;
}
.ak-posts-list.ak-nocontentpadding .ak-post .col:last-child .ak-action-button > a.ak-post-response {
    max-height: 32px;
    padding-top: 12px;
    background-position: 8px 9px;
}
.ak-posts-list.ak-nocontentpadding .ak-post .col:last-child .ak-action-button > a:hover,
.ak-posts-list.ak-nocontentpadding .ak-post .col:last-child .ak-action-button > div > button:hover {
    border-radius: 5px;
}
}

@-moz-document regexp("https://www.dofus.com/.*/fo.*") {
/* ------------------------------------------------------ */
/* --------------- !!! Dofus SECTION !!! --------------- */
/* ------------------------------------------------------ */


/* ---------------------------------------- */
/* ------------- Ankama Header ------------ */
/* ---------------------------------------- */
nav .breadcrumb {
    border-top: 2px solid #bacc3b;
    border-left: 2px solid #85a017;
    border-right: 2px solid #809c13;
}

/* ----------------------------------------- */
/* ------------- Forum Top Page ------------ */
/* ----------------------------------------- */
@media (min-width: 768px) {
    .ak-container.container .row .col-sm-3::after {
        content: "Thème Stylish pour le forum Dofus";
    }
}

/* ------------------------------------------ */
/* ------------ Contenu principal ----------- */
/* ------------------------------------------ */
.ak-panel, .ak-main-container .ak-admin-text  {
    background: #FCF7DF;
    color: #5C4124;
}
.ak-panel .ak-panel-header-list-post {
    background: rgba(181, 200, 56, 0.59);
}
.ak-pagination {
    background: #f2e6c0;
}
.ak-form select,
