css:
@-moz-document domain("jackbox.tv") {
.page.white {
    background: #1C1C1C;
}
.text-signin {
    color: white;
}
button,
input,
optgroup,
select,
textarea {
    margin: 0;
    font: inherit;
    color: white;
    background-color: #393F4C;
    border: 0px !important;
    box-shadow: none !important;
}
.jbg_logo {
    background: url(https://i.imgur.com/2XBBTs2.png);
    background-image: url(https://i.imgur.com/2XBBTs2.png);
    background-position-x: 100%;
    background-position-y: 100%;
    background-size: cover;
    background-repeat-x: no-repeat;
    background-repeat-y: no-repeat;
    background-attachment: initial;
    background-origin: initial;
    background-clip: initial;
    background-color: initial;
    background-size: cover;
    background-position: 100% 100%;
    background-repeat: no-repeat;
    border: none;
    border-top-color: initial;
    border-top-style: none;
    border-top-width: initial;
    border-right-color: initial;
    border-right-style: none;
    border-right-width: initial;
    border-bottom-color: initial;
    border-bottom-style: none;
    border-bottom-width: initial;
    border-left-color: initial;
    border-left-style: none;
    border-left-width: initial;
    border-image-source: initial;
    border-image-slice: initial;
    border-image-width: initial;
    border-image-outset: initial;
    border-image-repeat: initial;
    margin-left: auto;
    margin-right: auto;
    cursor: pointer;
    cursor: hand;
}
.canvas-compatibility {
    background-color: #1C1C1C !important;
}
.button-gray {
    color: white;
    background: #393F4C;
}
#state-answer-question {
    background-color: #1C1C1C !important;
}
#state-vote {
    background-color: #1C1C1C !important;
}
#player {
    background-color: #393F4C !important;
}
.quiplash2-button {
    background-color: #393F4C !important;
}
img {
    display: none !important;
}
h1 {
    color: white;
}
.quiplash2-logo-image {
    background-color: #1C1C1C !important;
    background-image: url(http://jackbox.tv/images/quiplash2/logo.png) !important;
    background-repeat: no-repeat, no-repeat;
    background-position: center, center;
    background-size: contain, cover;
    height: 80%;
    width: 90%;
    margin: auto;
}
.pure-u-1-3 > div {
    margin-left: -5px !important;
    margin-right: 5px;
}
.pure-form input[type=text][disabled] {
    cursor: not-allowed;
    background-color: #393F4C;
}
}
