css:
@-moz-document domain("overcast.fm") {
body {
  background-color: #000000;
  color: #FFFFFF;
}
a, .linkcolor, .extendedepisodecell.usernewepisode .title {
  color: #0381F0;
}
.tmreg {
  color: #333333;
}
.footer, .lighttext {
  color: #888888; }
.footer a {
  color: #555555;
}
.controller_admin {
  border: 8px solid #0B90B9;
}
.admin_link, .controller_admin a, .controller_admin .ocbutton, .controller_admin .ocborderedbutton, .controller_admin .ocsegmentedbutton {
  color: #0B90B9;
  border-color: #0B90B9;
}
.controller_admin .ocborderedbutton:active {
  background-color: #0B90B9;
}
.caption2 {
  color: #808080;
}
.ocbutton, .ocborderedbutton, .ocsegmentedbutton {
  color: #0381F0;
}
.ocborderedbutton, .ocsegmentedbutton {
  border: 1px solid #0381F0;
}
.ocborderedbutton:active, .ocsegmentedbuttonselected {
  background-color: #0381F0;
  color: #000000;
}
.destructivebutton, .controller_admin .destructivebutton, .alert, .errors {
  color: #03E4F0;
  border-color: #03E4F0;
} .destructivebutton:active, .controller_admin .destructivebutton:active {
  background-color: #03E4F0;
  color: #000000;
}
.purchasebutton, .controller_admin .purchasebutton {
  color: #F33CDD;
  border-color: #F33CDD;
} .purchasebutton:active, .controller_admin .purchasebutton:active {
  background-color: #F33CDD;
}
.alternatebutton, .altcolor {
  color: #F47266;
  border-color: #F47266;
} .purchasebutton:active {
  background-color: #F47266;
  color: #000000;
}
.feedcell, .episodecell {
  color: #FFFFFF;
}
.feedcell:hover, .episodecell:hover, .extendedepisodecell:hover {
  background-color: rgba(3, 129, 240, 0.05);
}
.extendedepisodecell {
  color: #FFFFFF;
}
.art {
  border: 1px solid #222222;
  background-color: #000000;
}
#autocomplete_results {
  background-color: #000000;
  color: #FFFFFF;
  border: 2px solid #333333;
}
a.autocomplete_result {
  color: #5B5B5B;
}
.autocomplete_result img {
  border: 2px solid #111111;
}
.autocomplete_result h4 {
  color: #f4f4f4;
}
.ocfeedlistinput .autocomplete_result h4 {
  color: #DDDDDD;
}
.ocfeedlistinput .wildcard_result {
  background-color: #002002;
}
.ocfeedlistinput .excluded_result {
  background-color: #022022;
}
#upload_progress {
  color: #0381F0;
}
#upload_progress::-moz-progress-bar {
  background-color: #0381F0;
}
#upload_progress::-webkit-progress-bar {
  background-color: #111111;
}
#upload_progress::-webkit-progress-value {
  background-color: #0381F0;
}
#progresssliderbackground {
  border: 1px solid #222222;
}
#progresssliderbackground::-webkit-progress-value {
  background-color: #024178;
}
#progresssliderbackground::-moz-progress-bar {
  background-color: #024178;
}
#progressslider::-webkit-slider-thumb {
  background: #0381F0;
}
#progressslider::-moz-range-thumb {
  background: #0381F0;
}
.progresssliderloadedrange {
  background-color: #0381F0;
}
#speedcontrol::-webkit-slider-runnable-track {
  background-color: #00243E;
}
#speedcontrol::-webkit-slider-thumb {
  background: #0381F0;
}
#speedcontrol::-moz-range-track {
  background-color: #00243E;
}
#speedcontrol::-moz-range-thumb {
  background: #0381F0;
}
table.adrates {
  background-color: #070707;
}
table.adrates td, table.adrates th {
  border: 1px solid #1F1F1F;
}
table.adrates th {
  background-color: #171717;
  border-color: #1F1F1F;
}
table.adrates td.altrowcolor {
  background-color: #0F0F0F;
}
table.adrates .nodata {
  color: #333333;
}
table.adrates .light, table.adrates .nodata {
  color: #666666;
}
table.adrates th.estimated {
  background-color: #777777;
  color: #000000;
}
.estimatefootnote {
  color: #088088;
}
a.estimatefootnote {
  color: #066066;
}
#apple-pay-button {
  background-color: white;
}
#stripe-checkout-button {
  background-color: #0381F0;
  color: #000000;
}
.adtext {
  color: #FFFFFF;
}
a.adtitle {
  color: #D0A59B;
}
table.adpreview {
  border: 1px solid #2F2F2F;
}
table.adpreview td {
  background-color: #0A0605;
}
table.adpreview .badge {
  background-color: #D0A59B;
  color: #000000;
}
table.boughtads {
  background-color: #0A0605;
}
table.boughtads td, table.boughtads th {
  border: 1px solid #0F0F0F;
}
table.boughtads th {
  background-color: #D46E54;
  border-color: #DF937F;
  color: #000000;
}
.adpreview_imgbox img {
  border: 1px solid #333333;
}
.pure-form input[type=text], .pure-form input[type=password], .pure-form input[type=email], .pure-form input[type=url], .pure-form input[type=date], .pure-form input[type=month], .pure-form input[type=time], .pure-form input[type=datetime], .pure-form input[type=datetime-local], .pure-form input[type=week], .pure-form input[type=number], .pure-form input[type=search], .pure-form input[type=tel], .pure-form input[type=color], .pure-form select, .pure-form textarea {
    border: 1px solid #333333;
    box-shadow: inset 0 1px 3px #222222;
    background-color: #000000;
    color: #ffffff;
}
.pure-form input[type=text]:focus, .pure-form input[type=password]:focus, .pure-form input[type=email]:focus, .pure-form input[type=url]:focus, .pure-form input[type=date]:focus, .pure-form input[type=month]:focus, .pure-form input[type=time]:focus, .pure-form input[type=datetime]:focus, .pure-form input[type=datetime-local]:focus, .pure-form input[type=week]:focus, .pure-form input[type=number]:focus, .pure-form input[type=search]:focus, .pure-form input[type=tel]:focus, .pure-form input[type=color]:focus, .pure-form select:focus, .pure-form textarea:focus {
    border-color: #0381F0;
}
.speedlabel {
	color: #FFFFFF;
}
.nav {
    border-bottom: 1px solid #111;   
}
#volumecontrol::-webkit-slider-runnable-track {
    width: 100%;
    height: 20px;
    background-color: #00243E;
	border-radius: 9px;
}
#volumecontrol::-webkit-slider-thumb {
    -webkit-appearance: none;
    height: 20px;
    width: 20px;
    border-radius: 50%;
    background: #0381F0;
}
#volumecontrol {
	width: 100%;
    -webkit-appearance: none;
    background-color: transparent;
    outline:none; 
}
}
