css:
@-moz-document domain("twitter.com") {


.bird-topbar-etched {
    color: #ffb15f;
}

.Icon--verified {
    color: #ff8300;
}

.typeahead button.clear-recent-searches {
    float: right;
    padding: 15px 13px 3px 3px;
    color: #ffb84a;
}

.typeahead-recent-search-item .Icon--close, .typeahead-saved-search-item .Icon--close {
    color: #f9b141;
}



h3 {
    color: #ffffff;
}


.global-nav {
    border-bottom: 1px solid rgb(0, 0, 0);
    height: 46px;
    position: relative;
    width: 100%;
}
body {
    background: #eee;
    color: #000000;
    font-size: 14px;
    line-height: 18px;
    overflow-y: scroll;
}


.AdaptiveFiltersBar-target {
    color: #7d7d7d;
    display: block;
    font-weight: bold;
    padding: 16px;
}
.typeahead-items li>a {
    display: block;
    padding: 7px 15px 7px 10px;
    overflow: hidden;
    color: #cccccc;
    text-overflow: ellipsis;
    white-space: nowrap;
    font-size: 14px;
    line-height: 32px;
}
.dropdown-menu {
    position: absolute;
    top: 100%;
    z-index: 900;
    display: none;
    float: left;
    padding: 10px 0;
    margin: 2px 0 0;
    background: #000000;
    border-radius: 4px;
    box-shadow: 0 1px 4px rgba(0, 0, 0, 0);
    background-clip: padding-box;
}

.Footer-item, .Footer-link {
    color: #807770;
}

.with-icn {
    font-size: 12px;
    color: #807770;
}

.tweet>.context .with-icn .Icon {
    color: #ffb15f;
}

.tweet .context .pretty-link s, .tweet .context .pretty-link b {
    color: #807770;
}

.Trends .context-trend-item .trend-item-stats {
    color: #807770;
    font-size: 12px;
    line-height: 16px;
}

.typeahead:before,
.form-search .typeahead:after {
    bottom: 0;
    box-shadow: 0 -3px 3px #000, 0 -6px 8px #000;
    content: "";
    height: 5px;
    position: absolute;
    width: 100%;
}
.form-search .typeahead:before,
.form-search .typeahead:after {
    bottom: 0;
    box-shadow: 0 -3px 3px #000, 0 -6px 8px #000;
    content: "";
    height: 5px;
    position: absolute;
    width: 100%;
}
.typeahead .dropdown-inner>.has-results ~ .has-results,
.typeahead .dropdown-inner>.has-items ~ .has-items {
    border-top: 1px solid #696969;
    padding-top: 0;
    margin-top: 0;
}

.global-nav .search-icon .Icon {
    background-color: transparent;
    color: #696969;
}

.dropdown-menu li>a,
.dropdown-menu .dropdown-link {
    clear: both;
    color: #7b7b7b;
}
.typeahead a,
.typeahead .fullname {
    color: #cccccc;
}
.account-group .username,
.time,
.time a,
.created-via {
    font-size: 14px;
    color: #505050;
}
.typeahead-accounts .typeahead-social-context {
    color: #444444;
    font-size: 12px;
    font-weight: normal;
    text-overflow: ellipsis;
    overflow: hidden;
}
.typeahead-topics strong {
    color: #efefef;
}
.top-timeline-tweetbox .timeline-tweet-box {
    border-top-left-radius: inherit;
    border-top-right-radius: inherit;
    border: 1px solid #eaeaea;
}
.Trends {
    background-color: #fff;
    background-clip: inherit;
    border: 1px solid #eaeaea;
    line-height: inherit;
    padding: 15px;
}
.ProfileCardStats-statLabel {
    color: #696969;
    font-size: 12px;
    font-weight: bold;
    letter-spacing: .02em;
    overflow: hidden;
    transition: color .15s ease-in-out;
}
.new-tweets-bar {
    font-size: 14px;
    display: block;
    padding: 10px 1px;
    cursor: pointer;
    text-align: center;
    font-weight: normal;
    background-color: #f5f5f5;
    border-top: 1px solid #efefef;
    border-bottom: 1px solid #efefef;
    z-index: 2;
}

.new-tweets-bar:hover {
    background-color: rgba(255, 174, 0, 0.15);
    border: 1px solid rgba(255, 174, 0, 0.15);
}


.stream-item:not(.no-header-background-module) {
    background: #fff;
    border-left: 1px solid #eaeaea;
    border-right: 1px solid #eaeaea;
    background-clip: padding-box;
    
}
                                        
.conversation-module .conversation-tweet-item .tweet:hover {
    background-color: rgba(255, 174, 0, 0.1);
}

.nav>li {
    color: #696969;
}
.global-nav .search-input {
    background-color: #f7f7f7;
    border-radius: 21px;
    border: 1px solid #ececec;
    -moz-box-sizing: border-box;
}

.tweet {
    border-bottom: 1px solid #ececec;
    cursor: pointer;
}

.tweet:hover {
    background-color:rgba(255, 174, 0, 0.1);
}

.conversation-module>li:after, .conversation-module>li:before {
    content: "";
    display: block;
    position: absolute;
    width: 0;
    border-style: solid;
    border-width: 2px;
    border-color: #ded7cd;
    left: 34px;
    z-index: 1;
    border-radius: 2px;
}

 .import-prompt p>span {
    color: #807770;
    display: block;
    font-size: 12px;
}   

.Icon--follower, .Icon--magicrecs, .Icon--colorLightning, .Icon--colorFollower {
    color: #ffb15f;
}

.ProfileTweet-actionButton, .ProfileTweet-actionButtonUndo {
    color: #807770;
}

  
.ProfileTweet-actionCount {
    color: #807770;
}

.btn-link.translate-button {
    color: #807770;
}
.btn-link.translate-button .Icon--translator {
    color: #807770;
}

		

.HeartAnimation {
    position: absolute;
    background-image: url('https://pbs.twimg.com/media/DF2CraVUAAAoLS3.png:large');
    background-position: left;
    background-repeat: no-repeat;
    background-size: 2900%;
    height: 50px;
    width: 50px;
    left: 50%;
    top: 50%;
    -ms-transform: translate(-50%,-47.5%);
    transform: translate(-50%,-47.5%);
}
	
/*letwit*/
.ProfileHeaderCard-screennameLink, .ProfileHeaderCard-screennameLink:hover {
    color: #696969;
}
.ProfileHeaderCard-birthdate, .ProfileHeaderCard-joinDate, .ProfileHeaderCard-location, .ProfileHeaderCard-onlineHours, .ProfileHeaderCard-responsivenessLevel, .ProfileHeaderCard-url, .ProfileHeaderCard-vineProfile, .ProfileHeaderCard-periscopeProfile {
    color: #696969;
}
.ProfileHeaderCard-locationText, .ProfileHeaderCard-onlineHoursText, .ProfileHeaderCard-responsivenessLevelText, .ProfileHeaderCard-urlText, .ProfileHeaderCard-vineProfileText, .ProfileHeaderCard-periscopeProfileText {
    color: #696969;
}
.ProfileNav-value {
    color: #696969;
}
.ProfileNav-label {
    color: #696969;
}
.metadata, .metadata button.btn-link, .metadata a {
    font-size: 12px;
    color: #807770;
}
.ProfileHeaderCard-birthdateText, .ProfileHeaderCard-joinDateText, .ProfileHeaderCard-locationText, .ProfileHeaderCard-onlineHoursText, .ProfileHeaderCard-responsivenessLevelText, .ProfileHeaderCard-urlText, .ProfileHeaderCard-vineProfileText, .ProfileHeaderCard-periscopeProfileText {
    color: #696969;
}
.TwitterCard .MomentCard-description p {
    color: #9a9a9a;
}
.TwitterCard .MomentCard-badge {
    color: #9a9a9a;
}
.TwitterCard .MomentCard-subtitle {
    color: #9a9a9a;
}
.ProfileNav-item.is-active .ProfileNav-label, .ProfileNav-item.is-active:hover .ProfileNav-label {
    color: #ffb15f;
}
.ProfileNav-item.is-active .ProfileNav-value, .ProfileNav-item.is-active:hover .ProfileNav-value {
    color: #ffb15f;
}

.EdgeButton--tertiary {
    background-color: #fff;
    border: 1px solid #696969;
    color: #696969;
}
.EdgeButton--tertiary:hover {
    background-color: rgba(255, 177, 95, 0.5);
    border: 1px solid rgba(255, 177, 95, 0.75);
    color: #fff;
}


.ProfileHeading-toggleItem.is-active, .ProfileHeading-toggleItem.is-active:hover, .ProfileHeading-toggleItem.is-active:focus {
    color: #ffb15f;
}

.PhotoRail-heading {
    color: #696969;
}

.EdgeButton--secondary:hover {
    background-color: rgba(255, 177, 95, 0.5);
    border-color: rgba(255, 177, 95, 0.75);
    color: #fff
}


.primary-btn, .notifying .device-follow-button, .button-group-item .content.selected {
    color: #fff;
    background-color: #ffc78c;
    background-image: linear-gradient(transparent,rgba(0,0,0,0.05));
    border: 1px solid #ffb15f;
    box-shadow: inset 0 1px 0 rgba(255,255,255,0.2);
}
.primary-btn:hover {
    color: #fff;
    background-color:#000;
    border-color: #fff;
}
.primary-btn, .notifying .device-follow-button, .button-group-item .content.selected:before {
    color: #fff;
    background-color: #ffc78c;
    background-image: linear-gradient(transparent,rgba(0,0,0,0.05));
    border: 1px solid #ffb15f;
    box-shadow: inset 0 1px 0 rgba(255,255,255,0.2);
}













}
