body {
background: url(https://img-fotki.yandex.ru/get/361493/500851312.0/0_142a7d_74c54efb_orig.png) center bottom repeat-x fixed, url(https://img-fotki.yandex.ru/get/237815/500851312.0/0_1429a3_268291fc_orig.png) top center fixed;
}


#page_header_cont .back {
background: url(https://img-fotki.yandex.ru/get/765674/500851312.0/0_1429a4_c4e764a9_orig.png) fixed center top repeat-x
}


.top_home_link .top_home_logo {
    background: url(https://img-fotki.yandex.ru/get/218579/500851312.0/0_1429ac_b44a2e8a_orig.png) no-repeat;
    height: 28px;
    width: 46px;
    margin: 8px 0 0 0;
}

#side_bar ol li .left_row:hover {
    background: transparent;
    }

#side_bar .left_icon {
    opacity: 1;
    filter: alpha(opacity=100);
    background: url(https://img-fotki.yandex.ru/get/229651/500851312.0/0_1429a9_82b6ffb6_orig.png) no-repeat 7px -441px;
}

#side_bar ol li .left_row .left_label, .top_audio_player .top_audio_player_title, .top_profile_name, .left_menu_nav {
    color: #fefefd;
    text-shadow: 1px 1px rgba(0,0,0,.4);
    font-weight: normal;
    }
    
    .left_count_wrap {
    background: transparent !important;
    color: #fefefd;
    text-shadow: 1px 1px rgba(0,0,0,.4);
    font-size: 15px;
    }
    
    .top_nav_link.active, .top_nav_link:hover, .top_audio_player:hover, .stl_active.over_fast #stl_bg, .top_nav_btn.active, .top_nav_btn:hover {
    background-color: transparent !important;
    }

	.left_menu_more:after {
    filter: brightness(2);
    opacity: 1;
    }
    
    
    .page_block, .top_profile_img {
	    box-shadow: 1px 1px rgba(0,0,0,.4);
	}
    
    
        .page_block {
    border-radius: 6px;
    background: url(https://img-fotki.yandex.ru/get/249307/500851312.0/0_1429ad_b755b791_orig.png), url(https://img-fotki.yandex.ru/get/206909/500851312.0/0_142a4c_bc842c0b_orig.png) right repeat-y, url(https://img-fotki.yandex.ru/get/237815/500851312.0/0_142a4e_44c9e99c_orig.png) left repeat-y,  url(https://img-fotki.yandex.ru/get/197756/500851312.0/0_142a4f_5facef1b_orig.png) bottom repeat-x, url(https://img-fotki.yandex.ru/get/235925/500851312.0/0_142a4d_734b7dca_orig.png) top repeat-x;
    background-color: #FFF
    }
    
    div#ts_wrap {
    background: url(https://img-fotki.yandex.ru/get/50666/500851312.0/0_142a47_8c12802a_orig.png) 0px 2px no-repeat;
    padding-left: 6px;
    padding-right: 6px;
}

input.text.ts_input {
    background: none !important;
    border-left: 14px solid transparent;
    color: #ebe0d8 !important;
    text-shadow: 1px 1px 1px rgba(0,0,0,0.6);
    font-weight: normal;
}

.ts_wrap .input_back_content {
    padding: 0 6px;
    color: #ebe0d8 !important;
    text-shadow: 1px 1px 1px rgba(0,0,0,0.6);
}


.fakeinput, div[contenteditable=true], input.big_text, input.dark, input.search, input.text, textarea {
    background: transparent
    }
    
    .submit_post {
    display: none;
    background: transparent;
    border-top: 1px solid rgba(0,0,0,.4);
    }
    
    .page_cover.crisp_image:after {
    content: '';
    position: absolute;
    height: 100%;
    width: 100%;
    background: url(https://img-fotki.yandex.ru/get/362774/500851312.0/0_142a77_46d19050_orig.png) no-repeat;
    background-size: 100% 200px;
    pointer-events: none;
}

div#page_avatar:after {
    content: '';
    position: absolute;
    height: 100%;
    width: 100%;
    background: url(https://img-fotki.yandex.ru/get/361493/500851312.0/0_142a78_913d109d_orig.png) bottom -60px left no-repeat;
    left: 0;
    top: 0;
    pointer-events: none;
}


.top_nav_btn#top_audio .top_nav_btn_icon {
    background: url(https://img-fotki.yandex.ru/get/206909/500851312.0/0_142a98_2f96313b_orig.png) -20px 0px no-repeat !important;
}

.top_nav_btn#top_notify_btn .top_nav_btn_icon {
    background: url(https://img-fotki.yandex.ru/get/206909/500851312.0/0_142a98_2f96313b_orig.png) 0 0 no-repeat !important;
}
}
