css:
/*
  Thunderbird Chris' Conversation Pane
*/

/* i really want this to be global */

/*
  Description:
  Changes the 'Message' window - which is where/how you see/read the message (e-mail) to something more straight and modern ;-)
*/


body { background-color: ghostwhite; }


/* Fixed Infos on top */
#conversationHeaderWrapper {
  box-shadow: none !important;
  border-bottom: 1px solid lavender;
}

#conversationHeader {
  padding-top: 6px;
  background-color: cornsilk !important;
  /* background-image: none; */ /* Gradient overlay */
  border: none !important;
}


.subject {
  font-size: 11pt;
  letter-spacing: 1px;
  font-weight: normal !important;
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
  color: #555555;
  margin-left: 5px;
}


/* OPTIONS BOX */

.messageHeader .options,
.messageHeader .options .date,
.messageHeader .options a
{
  font-size: 10pt !important;
}

/* ----------------------------- inside the messagebox ---------------------- */

/* Infos of sender, date etc. */
.messageHeader.hbox {
  padding-top: 10px;
  padding-bottom: 7px;
  border-bottom: 1px solid rgba(100,100,100,0.1);
}

/* "Remote images are blocked..." */
.remoteContent.notificationBar,
.message .notificationBar {
  padding: 5px 10px !important;
  box-shadow: none !important;
  margin: 0 0 10px 0;
  border-top: none !important;
  border-bottom: 1px solid #e6e5ea !important;
  color: rgb(80,80,90) !important;
}
.message .remoteContent {
  background-color: #fef1d8 !important;
}

.message {
  margin: 0 10px 10px 10px;
  background-color: white;
  border: 1px solid #e6e5ea !important;
  border-radius: 2px;
  box-shadow: none !important;
  list-style-type: none;
}

.message.highlight:-moz-focusring,
.message:focus:-moz-focusring {
  outline: none;
}


/* That's what makes the ellipsis appear */
.shrink-box {
  display:        inline-block;
  white-space:    nowrap;
  text-overflow:  ellipsis;
  overflow:       hidden;
  flex:           1;
}

.contactTooltipWrapper .tooltip {
  /* override generic tooltip, to get closer to contact name */
  left: 0px;
}

.tooltip {
  border: 1px solid lightgrey !important;
  border-radius: 2px !important;
  box-shadow: 0 0 1px rgba(100,100,100,0.2) !important;
}

.dropDown .tooltip {
  border: 1px solid lightgrey !important;
}

.dropDown li {
  color: gray;
  font-size: 10pt;
}

.dropDown li:hover,
.dropDown li:nth-child(2n):hover,
.dropDown li:focus,
.dropDown li:nth-child(2n):focus {
  background-color: rgb(235,243,254) !important;
}

.dropDown li:last-child {
  border-radius: 0 0 2px 2px;
}

.dropDown li:first-child {
  border-radius: 2px 2px 0 0;
}

.arrow {
  border-color: transparent transparent lightgrey transparent;
}

.smallEmail {
  font-size: 10pt;
}

.author {
  font-weight: normal;
  margin-right: 2rem;
  font-size: 11pt;
}

.authorInfo {
  font-size: 11pt;
  color: #c0392b !important;
}

.name {
  font-size: 12pt !important;
}

.tipFooter {
  background-color: #F8F8FF !important;
}

.options .date {
font-size: 11pt !important;
color: graytext !important;
}

.options a,
.attachments a,
.link {
  text-decoration: none;
  color: #619BC0 !important;
  cursor: pointer;
  font-size: 11pt;
}


/* Pure Text Message */
.moz-text-html {
  font-size: 11pt !important;
  color: #555555 !important;
  letter-spacing: 0.3pt;
}


.options a:hover,
.attachments a:hover ,
.link:hover,
.options a:focus,
.attachments a:focus ,
.link:focus {
  text-decoration: none;
}

span.downwardArrow {
  font-size: 7pt;
  vertical-align: baseline;
}

.message.collapsed {
  border-radius: 2px;
}


.snippet {
  color: #888a85;
}

/*
.message .messageBody {
  padding: 0 10px 5px 10px;
}
*/

.iframe-container {
  margin-top: 25px;
}

.detailsPlaceholder.show-with-details {
  border-bottom: 1px solid rgb(240,240,240) !important;
  background: ghostwhite; /* Alternative: floralwhite */
  margin-top: -2px !important;
}



.detailsLine {
  font-size: 10pt;
  padding-left: 45px;
  color: #888a85;
  margin-top: 2px;
  margin-bottom: 3px;
}

.attachmentThumb {
  width: 130px;
  padding: 4px;
  border: 1px solid #e6e5ea !important; /* -------------------------------------------------------- */
  background-color: #fff;
  background-image: linear-gradient(to bottom,#fff 0%,#f2f2f2 100%);
  margin: 0 20px 0 0;
  display: inline-block;
  float: left;
  text-align: center;
  line-height: 0px; /* Needed, otherwise there's an extra padding at the bottom... */
}

.messageFooter {
  padding: 0px 10px 5px 10px;
  background-color: rgba(160, 160, 160, 0.1);
  border-top: 1px solid  #e6e5ea !important; /* -------------------------------------------------------- */
  border-radius: 0 0 2px 2px;
}

.messageFooter button {
  background-image: none;
  background-color: -moz-default-background-color;
  color: -moz-default-color;

  float: left;
  border: 1px solid  #e6e5ea !important; /* -------------------------------------------------------- */
  border-top-color: -moz-default-background-color;
  border-radius: 0px;

  margin: -1px 3px 0 0;
  padding: 0 5px 3px;
}

.messageFooter button:hover {
  border-color:  #e6e5ea !important; /* -------------------------------------------------------- */
  border-top-color:  #e6e5ea !important; /* -------------------------------------------------------- */
}

.messageFooter button:active {
  background-image: none;
  background-color: -moz-default-background-color;
  box-shadow: none;
}

.messageFooter.hide .footerActions {
  display: none;
}

.messageFooter.hide {
  padding: 0;
}

.messageFooter:after {
  content: " ";
  display: block;
  height: 0;
  clear: both;
  visibility: hidden;
}

.star {
  display: inline-block;
  height: 16px;
  width: 16px;
  background-image: url("data:image/png;base64,");
  background-repeat: no-repeat;
  margin-right: 8px;
}

.star.starred {
  background-image: url("data:image/png;base64,");
}

.starred .star {
  background-image: url("data:image/png;base64,");
  background-repeat: no-repeat;
  background-position: center;
  height: 16px;
  width: 16px;
}

/* bottom links: forward this conversation, print this conversation */
.bottom-links {
  text-align: right;
  padding: 0 15px 10px 10px;
  color: lightgray !important;
}


.editDraftLinkWrapper > a, .replyLinkWrapper > a {
  background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAoAAAAKCAYAAACNMs+9AAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAFZJREFUeF59z4EJADEIQ1F36k7u5E7ZKXeUQPACJ3wK7UNokVxVk9kHnQH7bY9hbDyDhNXgjpRLqFlo4M2GgfyJHhjq8V4agfrgPQX3JtJQGbofmCHgA/nAKks+JAjFAAAAAElFTkSuQmCC")
    no-repeat scroll right center transparent;
  padding-right: 13px;
}

.quickReply,
.quickReply.expand {
  border-top: 1px solid  #e6e5ea !important; /* -------------------------------------------------------- */
}


.quickReply .popoutTxt {
  color: graytext;
  padding: 0 4px 0 15px;
  background: rgb(245,245,245) !important;
}

.quickReply .popout {
  height: 16px;
  display: block;
  position: absolute;
  bottom: 10px;
  left: 5px;
  border-width: 1px 1px 0 0;
  border-color: threedshadow;
border-color: #e6e5ea !important; /* -------------------------------------------------------- */
  border-style: solid;
  background-color: -moz-default-background-color;
  color: -moz-default-color;
  cursor: pointer;
  background-image: linear-gradient(rgba(160, 160, 160, 0.1), rgba(160, 160, 160, 0.1));
  z-index: 101;

  border-radius: 0 2px 0 0;
  background: rgb(245,245,245) !important;
}

.quickReply .popout::before {
  content: "";
  position: absolute;
  width: 16px;
  height: 16px;
  background-image: url('chrome://conversations/content/i/out.png');
  background-position: -3px 3px;
  background-repeat: no-repeat;
}

.quickReply .textarea {
  width: 100%;
  border: 1px solid #e6e5ea !important; /* -------------------------------------------------------- */
  min-height: 54px;
  height: 54px;
  padding: 0 0 0 100%; /* weird difference between iframe mozframetype="content" and iframe */
  resize: none;
  background-color: white !important;;
  display: block;
  position: relative;
  z-index: 100;
  outline: none;
  overflow: hidden;
  x-color: transparent;
  x-font-size: 10.5pt !important;
box-shadow: none !important;
}

HTML Body,
.moz-text-html,
.textarea.sans.selected {
  font-family: sans-serif;
  background-color: #fff !important;
}

iframe {
margin-top: 0px;
}

.moz-cite-prefix {
  font-size: 9pt !important;
color: #5b5b5b !important;
}

.quickReply ul.actions {
 border: none !important;
}

#conversationHeader > div button {
background: none !important;
border: none !important;
}

#conversationHeader > div button:hover,
#conversationHeader > div button:active,
#conversationHeader > div button:focus{
background: #dde9f7 !important;
border: 1px solid  #c6c6c6 !important; /* -------------------------------------------------------- */
}

/* Folder alert */

.alert {
  border-radius: 2px;
  box-shadow: none !important;
border: 1px solid  #c6c6c6 !important; /* -------------------------------------------------------- */
  font-size: medium;
  text-align: center;
  color: GrayText;

  background-color: rgba(125,125,105,.05);
  padding: 2.5em;
}


/* when already replied there are still options... */
.footerActions .edit-draft,
.footerActions .buttonForward,
.footerActions .buttonReplyAll,
.footerActions .buttonReplyList,
.footerActions .buttonReply {
   font-size: 10pt !important;
   color: graytext !important;
}

.footerActions .ext {
opacity: 0.6 !important;
}
