css:
@-moz-document regexp('http://www\\.espncricinfo\\.com.*/game/.*') {
.col-one {
    -webkit-filter: grayscale(100%);
    /* Safari 6.0 - 9.0 */
    
    filter: grayscale(100%);
}
.col-three {
    -webkit-filter: grayscale(100%);
    /* Safari 6.0 - 9.0 */
    
    filter: grayscale(100%);
}
.match-commentary.cricket .commentary-item.end-of-over h4 {
    background: #000000;
}
.over-circle.high-score {
    background: #3333DD;
}
.game-details.footer.pre .wrap .game-status,
.game-details.footer.live .wrap .game-status,
.game-details.footer.post .wrap .game-status {
    font-weight: bold;
    color: #FFF;
    background: #000;
}
#global-viewport #header-wrapper #global-header {
    filter: grayscale(100%);
}
.sub-module.current-inning table tr.results td .label {
    font-size: 12px;
}
.sub-module .carousel-item {
    display: none
}

[data-pagetype="gamepackage"] .game-strip{position:absolute;}

#global-viewport #header-wrapper{
	position:absolute;
}

.game-strip .team__banner{
	display:none;
}

.match-commentary.cricket .commentary-item{
	padding: 8px 0;
}

.sub-module>header, .sub-module>footer{
	display:none;
}

@media screen and (min-width: 1260px){

.layout-full, .layout-abc, .layout-bc, .layout-bc-large, .layout-cb, 	.layout-dbc, .layout-cbc{
	width:1440px;
}
    
.layout-cbc .col-one, .layout-cbc .col-three{
	width:250px;
}
    
.layout-cbc .col-two{
	width:800px;
}
    
}


}
