css:
@-moz-document regexp('http://www\\.espncricinfo\\.com.*/series/.*') {

.col-one {
    -webkit-filter: grayscale(100%);
    /* Safari 6.0 - 9.0 */
    
    filter: grayscale(100%);
}
.col-three {
    -webkit-filter: grayscale(100%);
    /* Safari 6.0 - 9.0 */
    
    filter: grayscale(100%);
}
.match-commentary.cricket .commentary-item.end-of-over h4 {
    background: #000;
}
.over-circle.high-score {
    background: #3333DD;
}
.game-details.footer.pre .wrap .game-status,
.game-details.footer.live .wrap .game-status,
.game-details.footer.post .wrap .game-status {
    font-weight: bold;
    color: #FFF;
    background: #000;
}
.espncricinfo #global-header .container,
.espncricinfo #global-header #global-nav {
    background: #444;
}
.sub-module .carousel-item {
    display: none;
}
#global-viewport #pane-main.details-header,
#global-viewport #pane-main.details-footer {
    background: #222222;
}
.main-container {
    filter: invert(100%);
}
.game-strip .score {
    color: #fff
}
[data-pagetype="gamepackage"] .game-strip {
    background: #222222;
    position: absolute;
}
.game-details.header {
    color: #FFF;
}
#global-nav-tertiary {
    filter: invert(100%);
}
.game-details.footer.pre .wrap .game-status,
.game-details.footer.live .wrap .game-status,
.game-details.footer.post .wrap .game-status {
    font-weight: bold;
    color: #000;
    background: #fff;
}
.over-circle.low-score {
    background: #444;
}
.sub-module {
    filter: invert(100%);
}
.headshot-md.athlete:not(.soccer) img {
    filter: invert(100%);
}
.col-three {
    background: #000;
}
#global-scoreboard,
.scoreCenter {
    background: #000;
}
#global-scoreboard .wrap .scoreboard-content .scores-carousel,
.scoreCenter .wrap .scoreboard-content .scores-carousel {
    background: #000;
}
#global-scoreboard .wrap .scoreboard-content .scores-carousel #leagues>.league>.scoreboard>div,
.scoreCenter .wrap .scoreboard-content .scores-carousel #leagues>.league>.scoreboard>div {
    background: #000;
}
.cscore_truncate {
    color: #FFF;
}
.cscore_score {
    color: #FFF;
}
#global-viewport #pane-main #main-container {
    background: #222;
}
.slick-initialized .slick-slide {
    filter: invert(0%);
}
.match-slick-arrows {
    filter: invert(100%);
}
#global-viewport #header-wrapper {
    position: absolute;
}
.sub-module-mobile-combine-split.combined {
    filter: invert(100%);
}
.game-strip .long-name,
.game-strip .short-name,
.game-strip .abbrev {
    color: #FFF;
}
.game-strip .team__banner {
    display: none;
}
.sub-module-news .feed-item-figure .img-wrap img {
    filter: invert(100%);
}
.match-commentary.cricket .commentary-item {
    padding: 8px 0;
}
.sub-module>header,
.sub-module>footer {
    display: none;
}
.match-detail--item span img {
    filter: invert(100%);
}
#article-feed {
    filter: invert(100%);
}
#article-feed .article .article-figure.video,
#article-feed .article .article-figure.iframe-video {
    filter: invert(0%);
}
#article-feed .article img {
    filter: invert(100%);
}
.feed-section:not(img) {
    filter: invert(100%);
}
.feed-section:first-child {
    padding-top: 0px;
}
.feed-section .comp.section-header {
    margin: 0 0;
}
.game-package-video .article-figure.video {
    filter: invert(100%);
}
#article-feed .article .article-social {
    filter: invert(100%);
}
.feed-item-figure .img-wrap img{
	filter:invert(100%);
}
@media screen and (min-width: 1260px) {
    .layout-full, .layout-abc, .layout-bc, .layout-bc-large, .layout-cb, .layout-dbc, .layout-cbc {
        width: 1440px;
    }
    .layout-cbc .col-one,
    .layout-cbc .col-three {
        width: 250px;
    }
    .layout-cbc .col-two{
        width: 800px;
    }
    #article-feed .article{
    	width:900px;                                   	
    }
    #article-feed .article .article-header, #article-feed .article .article-body{
    	width:800px;       
        max-width:900px;
    }
    
    
}
  
}
