css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("panoptikos.com") {
  
  html {
    background: #111;
  }
  
  .board-item {
    background: #222;
  }
  
  .board-item-title-anchor,
  .board-item-info-cell,
  .nav-link,
  .nav-link:visited {
    color: #999
  }
  
  a:hover {
    color: #e83a41;
  }
  
  button {
    background:linear-gradient(to bottom,#333333 5%,#272727 100%);
    color: #888;
    text-shadow: 1px 1px 0 rgba(55,55,55,0.5);
    border: 1px solid #333;
  }
}
