css:
@-moz-document domain("discordapp.com") {
/* messages */
.message-group { padding: 10px 0; margin-left: 10px; }
.message-group .avatar-large { margin-right: 10px; }

/* text input */
.channel-text-area-default { margin: 10px 0 20px; }
.typing { bottom: -6px; line-height: 20px; }

/* top bar */
.chat>.title-wrap { min-height: 40px; }

/* member bar */
.channel-members { max-width: 200px; }
.channel-members-wrap { min-width: 200px; }
.channel-members .member { padding: 5px 16px 5px 16px; }
.channel-members h2 { padding: 0 16px; margin-top: 18px; }
.old-h2 { margin: 5px 0; }

/* channels bar */
.channels-wrap { width: 210px; }
.header-1tSljs { height: 40px; }
.scroller-NXV0-d { margin-top: -10px; }

/* guilds */
.guilds-wrapper { width: 45px; }
.guilds-wrapper .scroller-wrap { width: 64px; }
.guilds-wrapper .guilds { padding: 8px 2px; }
.guilds-wrapper .guilds { padding: 8px 0 8px 4px; }
.guilds-wrapper .guilds .guild { width: 30px; height: 30px; margin-left: 4px; }
.guilds-wrapper .guilds .guild .guild-inner { line-height: 30px; }
.guilds-wrapper .guilds .guild .guild-inner a { width: 30px; height: 30px; background-size: 30px; }
.guilds-wrapper .guilds .guild+.guild { margin-top: 4px; }
.guilds-wrapper .guilds .guild:before { margin-left: 1px; }
.guilds-wrapper .guilds .friends-icon { width: 30px; height: 30px; background-size: 28px 24px; }
.guilds-wrapper .guilds .friends-online { margin: 3px 0; width: 36px; white-space: inherit; }
.guilds-wrapper .guild-separator { width: 36px; }
.guilds-wrapper .guilds-add { font-size: 25px; }

/* download app button */
.guilds-wrapper .btn-download-apps { display: none; }
}
