css:
/* -=<   about:newtab - LavaFox & BlackFox by Zigboom Designs   >=- */

@-moz-document url("about:newtab") {

/* SCROLLBOX */
body.activity-stream {
background: #000000 url("chrome://browser/skin/Extra/LavaOrb.png") !important;
background-position:center center !important;
background-attachment:fixed !important;
background-repeat:no-repeat !important;
background-size: auto !important;
  color: #ccc !important;
}
  
  a, .topic .topic-link {
  color: #ccc !important;
  }

  a:hover, .topic .topic-link:hover {
  color: #eee !important;
  }

  .topic .topic-read-more-logo {
    filter: grayscale(1);
  }  
  
/* Search */

 .activity-stream .search-wrapper input {
  background-color: hsla(0,0%,100%,.12);
  color: #dcdcdc !important;
  box-shadow: 0 0 0px rgba(255,255,255,.4);
  transition: all 1s !important;
   border: 1px solid hsla(0,0%,100%,0.1);
}

 .activity-stream .search-wrapper input:focus {
  border: 1px solid hsla(0,0%,100%,.2);
  box-shadow: 0 0 10px rgba(255,255,255,.4);
  transition: all 1s !important;
}

   .activity-stream .search-wrapper input:focus + .search-button {
    background:  url("chrome://browser/skin/search-arrow-go.svg#search-arrow-go") center center no-repeat, linear-gradient(#555555, #181818) padding-box no-repeat;
  box-shadow: 0 0 10px rgba(255,255,255,.4);
  transition: all 1s !important;
      background-color: #0996F8;
      color: #FFF; 
}

 .activity-stream .search-wrapper .search-button {
  transition: box-shadow 1s !important;
  color: #999 !important; 
    box-shadow: 0 1px 0 0 rgba(0, 0, 0, 0.1);
    background:  url("chrome://browser/skin/search-arrow-go.svg#search-arrow-go") center center no-repeat, linear-gradient(#555555, #181818) padding-box no-repeat;

}

   .activity-stream .search-wrapper .search-button:hover, .activity-stream .search-wrapper input:focus + .search-button:hover {
      z-index: 1;
      box-shadow: 0 0 10px rgba(255,255,255,.4);
    background:  url("chrome://browser/skin/search-arrow-go.svg#search-arrow-go") center center no-repeat, linear-gradient(#555555, #181818) padding-box no-repeat;
      color: #FFF;
      cursor: pointer; 
}


.activity-stream #searchSuggestionTable {
  background-color: rgba(40,40,40,.95) !important;
  border: 1px solid #444 !important;
  border-spacing: 0;
  box-shadow: 0 4px 5px rgba(0,0,0,.7) !important;
  border-radius: 3px !important;
  border-top-left-radius: 0 !important;
  border-top-right-radius: 0 !important;
  margin-top: -1px !important;
}

.activity-stream .contentSearchHeader  {
  background-color: rgba(40,40,40,.95) !important; 
  border-color: #444 !important;
}

.activity-stream .contentSearchSuggestionsList {
  border-bottom: 1px solid #444 !important;
}


.activity-stream .contentSearchSuggestionEntry:hover, .activity-stream .contentSearchOneOffItem:hover, .activity-stream .contentSearchOneOffItem.selected, .activity-stream .contentSearchSuggestionEntry.selected, .activity-stream .contentSearchSuggestionRow.selected {
  background-color: rgba(68,68,68,.95) !important;
}

.activity-stream .contentSearchOneOffItem {
  border-right: 1px solid #444 !important;
  background-image: none !important;
}

.activity-stream .contentSearchOneOffItem.end-of-row {
  border-right: 1px solid transparent !important;
    background-image: none !important;
}

.activity-stream .prefs-pane-button button {
  filter: invert(0.6);
}


.activity-stream .prefs-pane-button button:hover, .icon.icon-spacer {
  filter: invert(1);
}

.activity-stream .prefs-pane .sidebar {
  background-color: rgba(40,40,40,.95) !important;
  border: 1px solid #444 !important;
}

.activity-stream .actions button.done {
  background: linear-gradient(#555555, #181818);
    color: #ccc !important;
  box-shadow: 0 0 0px rgba(255,255,255,.2);
  transition: all 1s !important;
   border: 1px solid #444;
}

.activity-stream .actions button.done:hover {
  background: linear-gradient(#555555, #181818);
    color: #fff !important;
  box-shadow: 0 0 10px rgba(255,255,255,.2);
  transition: all 1s !important;
   border: 1px solid #555;
}

.activity-stream .context-menu {
  background-color: rgba(40,40,40,.95) !important;
  border: 1px solid #444 !important;
  border-spacing: 0;
  box-shadow: 0 4px 5px rgba(0,0,0,.7) !important;
  border-radius: 3px !important;
  }  
  
.activity-stream .section-title {
  display: none;
}

 .activity-stream .context-menu > ul > li > a:hover, .activity-stream .context-menu > ul > li > a:focus {
      background-color: rgba(68,68,68,.95) !important;
}

 .activity-stream .prefs-pane [type="checkbox"]:not(:checked) + label::before {
    background: transparent;
    border: 1px solid #ccc;
    border-radius: 10px;
}
  
 .activity-stream .prefs-pane [type="checkbox"]:checked + label::before {
    background: radial-gradient(#fff,transparent 50%);
    border: 1px solid #ccc;
}
  
  
.card-outer {
    background: rgba(24, 24, 24, 0.9) !important;
}
  
main section {
    margin-bottom: 0px !important;
}  
  
.outer-wrapper {
    padding: 22px 32px 32px !important;

}  

  
  .top-sites-list .top-site-outer .tile {
  background-color: hsla(0,0%,100%,.05);
  box-shadow: 0 0 0px rgba(255,255,255,0);
  transition: all 0.6s !important;
   border: 1px solid hsla(0,0%,100%,0.05);  
  }  

  .top-sites-list .top-site-outer .tile:hover {
    border: 1px solid hsla(0,0%,100%,.1);
  box-shadow: 0 0 5px rgba(255,255,255,.25);
  transition: all 0.6s !important;
} 

  
  
  
  
  
  
/* The */
}
/* End */
