css:
@-moz-document domain("realkana.com") {
body, body#h.b-b.kana, body#hd.b-b.kana, body#k.b-b.kana, body#s.b-b, body#i.b-b, body#o.b-b, body#p.b-b, body#a.b-b, body#kd.b-b.kana, body#ke.b-b.kana, #s input.blur, #s input {
    background-color: #202024 !important;
}
a.disabled, #o #top div label, #i p>a.span, #ad-links a {
    color: #202024;
}
#s input.blur, #s input, #app span {
    border-color: #202024;
}
#tabs li {
    border: 3px solid #303036;
    border-right-width: 0;
    border-top-width: 0;
}
#app, #app div:nth-child(3) {
    background-color: #303036;
    border-color: #202024;
}
#app span:last-child {
    background-image: url(https://i.imgur.com/aWjfWZR.png)
}
#tabs li a, #all, #app, #s input.blur {
    box-shadow: none;
}
div#tab, ul#tabs, #tabs li {
    background-color: #404048;
}
div#kana, div#kana table, div#preview, #kana td, div#t, div#backgrounds, div#backgrounds span, table#words, table#words td:last-child, table#words th, table#words td, table#words th:last-child, table#words tr:last-child td, table#challenges, #challenges tr:last-child td, #challenges tr.break td, #challenges th, #challenges td, #challenges tr:nth-child(9) td:nth-child(2), #challenges tr:nth-child(9) td:last-child, #challenges td:last-child, #challenges th:last-child {
    border-color: transparent;
}
#bottom.fade::before, #backgrounds, #bottom, #intro, #kana, #preview, #t, #tabs #front a, #top, div.top, div.bottom, #intro::after, #top.fade::after, div.top.fade::after, div.bottom.fade::before {
    background-image: none !important;
}
#preview span>span, div#kana span {
    background-image: url(http://i.imgur.com/v01w7gj.png);
}
#question span, #t span {
    background-image: url(https://i.imgur.com/LQubHJQ.png)
}
span, input, div#answer, body, a, a:visited, span.link, h1, div#site, h1+h2, div.pane h2, div.site, p {
    color: #FFFFFF;
}
div#ad-right, div#ad-left, div#circle, #iffy-left, #iffy-right, #bottom > ul > li:nth-child(1) > a {
    display: none !important;
}
#bottom > ul > li:nth-child(2) {
    border-left: 1px solid #404048;
    margin-left: 0px;
    padding-left: 0px;
}
}
